L["eft_ar_pgrip_diecsfde.compactname"] = "ECS FDE"
L["eft_ar_pgrip_diecsfde.description"] = "El pistolete ECS de polímero puede ser instalado en cualquier arma que sea compatible con los pistoletes de AR-15. Versión de color Flat Dark Earth."

L["eft_ar_pgrip_dlg123.printname"] = "Pistolete DLG Tactical DLG-123 para AR-15"
L["eft_ar_pgrip_dlg123.compactname"] = "DLG123"
L["eft_ar_pgrip_dlg123.description"] = "El pistolete DLG-123 puede ser instalado en cualquier arma que sea compatible con los pistoletes de AR-15. Fabricado por DLG Tactical."

L["eft_ar_pgrip_f1s1.printname"] = "Pistolete F1 Firearms Skeletonized Style 1 para AR-15"
L["eft_ar_pgrip_f1s1.compactname"] = "F1 St1"
L["eft_ar_pgrip_f1s1.description"] = "Un pistolete ligero y ergonómico para sistemas de armamento AR-15, fabricado por F1 Firearms."

