L["eft_muzzle_12g_threadapadter.printname"] = "Adaptador de rosca SilencerCo Salvo 12 para escopetas de Cal. 12"
L["eft_muzzle_12g_threadapadter.compactname"] = "Rosc.Cal.12"
L["eft_muzzle_12g_threadapadter.description"] = "Un adaptador de rosca SilencerCo para el silenciador Salvo de Calibre 12."

L["eft_muzzle_12g_chokeapadter.printname"] = "Adaptador de obturador SilencerCo Salvo 12 para escopetas de Cal. 12"
L["eft_muzzle_12g_chokeapadter.compactname"] = "Adapt.Cal.12"
L["eft_muzzle_12g_chokeapadter.description"] = "Un adaptador de obturador SilencerCo para el silenciador Salvo de Calibre 12."

L["eft_muzzle_12g_mecyl.printname"] = "Adaptador de bocacha ME Cylinder para escopetas de Cal. 12"
L["eft_muzzle_12g_mecyl.compactname"] = "Cylinder Cal.12"
L["eft_muzzle_12g_mecyl.description"] = "Un adaptador de rosca ME Cylinder para escopetas de calibre 12. Diseñado para colocar diferentes bocachas en el extremo del cañón."

L["eft_muzzle_12g_rtc.printname"] = "Obturador Remington Tactical Choke de Cal. 12"
L["eft_muzzle_12g_rtc.compactname"] = "RTC Cal.12"
L["eft_muzzle_12g_rtc.description"] = "El obturador Remington Tactical Choke de Calibre 12 fue diseñado específicamente para escopetas tácticas y de defensa del hogar. Este combina las propiedades de una ánima cilíndrica y las de un reductor de fogonazo."

L["eft_muzzle_12g_claw.printname"] = "Freno de boca Tromix Monster Claw de Cal. 12"
L["eft_muzzle_12g_claw.compactname"] = "MClaw"
L["eft_muzzle_12g_claw.description"] = "Un freno de boca Monster Claw de Calibre 12 producido por Tromix, reduce notablemente el retroceso y puede usarse para romper cristal templado."

L["eft_muzzle_12g_gk02.printname"] = "Freno de boca GK-02 de Calibre 12"
L["eft_muzzle_12g_gk02.compactname"] = "GK-02"
L["eft_muzzle_12g_gk02.description"] = "El freno de boca Ilyin GK-02 es una versión modernizada y mejorada del freno de boca Vsevolod de Ilyin, con mejores capacidades de reducir el retroceso y el empuje vertical del arma al disparar."

L["eft_muzzle_12g_hexasup.printname"] = "Silenciador Hexagon 12K de Calibre 12"
L["eft_muzzle_12g_hexasup.compactname"] = "Hexagon 12K"
L["eft_muzzle_12g_hexasup.description"] = "Un modulador de sonido para escopetas y carabinas de Calibre 12 (Saiga, Vepr y otras), producido por Hexagon."

L["eft_muzzle_12g_salvo.printname"] = "Silenciador SilencerCo Salvo 12 de Calibre 12"
L["eft_muzzle_12g_salvo.compactname"] = "Salvo 12"
L["eft_muzzle_12g_salvo.description"] = "Un modulador de sonido fabricado por SilencerCo para las carabinas y escopetas de Calibre 12 (Saiga, Vepr y otras). Es algo pesado, pero a final de cuentas es muy efectivo. Requiere un adaptador para poder montarlo."

-- [[ muzzle_ar10 ]]
L["eft_muzzle_ar10_cmmgsv.printname"] = "Freno de boca CMMG SV Brake de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_cmmgsv.compactname"] = "SV Brake"
L["eft_muzzle_ar10_cmmgsv.description"] = "El CMMG SV Brake es un freno de boca y compensador muy eficaz de una sola abertura, diseñado específicamente para sistemas de armas AR-10 de calibre .308 (7,62x51 mm OTAN). La gran abertura presenta una amplia superficie en la cual los gases impactan, lo que mantiene el cañón hacia abajo y la mira en el objetivo."

L["eft_muzzle_ar10_vette.printname"] = "Compensador Nordic Components Corvette de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_vette.compactname"] = "Vette 762"
L["eft_muzzle_ar10_vette.description"] = "El freno de boca y compensador Corvette de calibre 7,62x51 mm OTAN, producido por Nordic Components, reduce el retroceso y ascenso del cañón al direccionar los gases hacia arriba y los costados."

L["eft_muzzle_ar10_wave.printname"] = "Freno de boca Daniel Defense WAVE de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_wave.compactname"] = "WAVE 762"
L["eft_muzzle_ar10_wave.description"] = "El freno de boca Daniel Defense WAVE está diseñado para instalar el silenciador Daniel Defense WAVE al arma, pero también funciona de forma muy efectiva por sí solo. Está fabricado con acero aeroespacial inoxidable 17-4 PH y tiene un acabado en sal de nitrato para minimizar la corrosión."

L["eft_muzzle_ar10_warden.printname"] = "Bocacha reguladora de estallido SureFire Warden de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_warden.compactname"] = "War 762"
L["eft_muzzle_ar10_warden.description"] = "La bocacha reguladora de estallidos SureFire Warden cuenta con un cuerpo de acero inoxidable que mejora la apariencia general mientras protege las partes internas de daños. Este regulador de estallidos evita que la suciedad, el polvo, los escombros, etc., causen daños internos."

L["eft_muzzle_ar10_zk38.printname"] = "Freno de boca TAA ZK-38 de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_zk38.compactname"] = "ZK-38"
L["eft_muzzle_ar10_zk38.description"] = "El ZK es un freno de boca muy eficaz de una sola recámara con perfil inclinado, producido por Tactical Advantage Armory. La parte frontal es muy puntiaguda y también funciona como un dispositivo disuasorio durante enfrentamientos en espacios cerrados y ante multitudes. No se recomienda su uso en situaciones donde una alta conmoción es un factor determinante de riesgo."

L["eft_muzzle_ar10_30cb.printname"] = "Freno de boca TAA ZK-38 de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_30cb.compactname"] = "ZK-38"
L["eft_muzzle_ar10_30cb.description"] = "El ZK es un freno de boca muy eficaz de una sola recámara con perfil inclinado, producido por Tactical Advantage Armory. La parte frontal es muy puntiaguda y también funciona como un dispositivo disuasorio durante enfrentamientos en espacios cerrados y ante multitudes. No se recomienda su uso en situaciones donde una alta conmoción es un factor determinante de riesgo."

L["eft_muzzle_ar10_aac51t.printname"] = "Reductor de fogonazo AAC Blackout 51T de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_aac51t.compactname"] = "51T 762"
L["eft_muzzle_ar10_aac51t.description"] = "El Advanced Armament Corporation (AAC) Blackout 51T es un reductor que destaca por su efectividad, que también sirve como plataforma para montar el silenciador AAC 762-SDN-6. Puede ser instalado en fusiles basados en AR-10."

L["eft_muzzle_ar10_lantac.printname"] = "Freno de boca y compensador Lantac Dragon de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_lantac.compactname"] = "DGN762B"
L["eft_muzzle_ar10_lantac.description"] = "Un freno de boca Dragon producido por Lantac, diseñado para ser instalado en armamento de calibre 7,62x51 mm OTAN. Reduce el retroceso y contrarresta el ascenso del cañón al disparar."

L["eft_muzzle_ar10_qdc_brake.printname"] = "Freno de boca KAC QDC Muzzle Brake Kit de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_qdc_brake.compactname"] = "QDC 762"
L["eft_muzzle_ar10_qdc_brake.description"] = "El Knight's Armament Company Muzzle Brake Kit es un efectivo freno de boca que también sirve como plataforma de anclaje del silenciador PRS QDC. Permite su instalación en sistemas basados en AR-10."

L["eft_muzzle_ar10_m11.printname"] = "Freno de boca Precision Armanent M11 Severe-Duty de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_m11.compactname"] = "M11"
L["eft_muzzle_ar10_m11.description"] = "Un freno de boca M11 diseñado para ser instalado en sistemas tipo AR-10 y compatibles. Producido por Precision Armament."

L["eft_muzzle_ar10_x3.printname"] = "Compensador 2A Armanent X3 de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_x3.compactname"] = "X3 762"
L["eft_muzzle_ar10_x3.description"] = "Un compensador X3 producido por 2A Armaments para sistemas tipo AR-10 y compatibles de calibre 7,62x51 mm OTAN."

L["eft_muzzle_ar10_mdr.printname"] = "Reductor de fogonazo Desert Tech de 7,62x51 mm"
L["eft_muzzle_ar10_mdr.compactname"] = "MDR 762"
L["eft_muzzle_ar10_mdr.description"] = "Un reductor de fogonazo desarrollado por Desert Tech específicamente para los fusiles de asalto MDR de calibre 7,62x51 mm OTAN (.308)."

L["eft_muzzle_ar10_procomp.printname"] = "Freno de boca SureFire ProComp de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_procomp.compactname"] = "ProComp 762"
L["eft_muzzle_ar10_procomp.description"] = "Un freno de boca ProComp diseñado para ser instalado en sistemas tipo AR-10 y compatibles. Este freno de boca reduce en gran medida tanto el retroceso como el ascenso del cañón al disparar de forma controlada, permitiendo al usuario una rápida adquisición de objetivos. Producido por SureFire."

L["eft_muzzle_ar10_red.printname"] = "Freno de boca Fortis RED Brake de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_red.compactname"] = "RED 762"
L["eft_muzzle_ar10_red.description"] = "Un freno de boca RED Brake, diseñado para instalarse en sistemas tipo AR-10 y compatibles. Producido por Fortis."

L["eft_muzzle_ar10_atlas.printname"] = "Freno de boca Odin Works ATLAS-7 de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_atlas.compactname"] = "ATLAS-7"
L["eft_muzzle_ar10_atlas.description"] = "Un freno de boca ATLAS-7, diseñado para ser instalado en sistemas tipo AR-10 y compatibles. Producido por Odin Works."

L["eft_muzzle_ar10_scarsd.printname"] = "Reductor de fogonazo AAC SCAR-SD 51T de 7,62x51 mm"
L["eft_muzzle_ar10_scarsd.compactname"] = "SCAR-SD 762"
L["eft_muzzle_ar10_scarsd.description"] = "El Advanced Armament Corporation (AAC) SCAR-SD 51T es un reductor que destaca por su efectividad, que también sirve como plataforma para montar el silenciador AAC 762-SDN-6. Puede ser instalado en fusiles basados en AR-10."

L["eft_muzzle_ar10_shrewed.printname"] = "Freno de boca Keeno Arms SHREWD de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_shrewed.compactname"] = "SHREWD 762"
L["eft_muzzle_ar10_shrewed.description"] = "Un freno de boca SHREWD diseñado para ser instalado en sistemas tipo AR-10 y compatibles. Producido por Keeno Arms."

L["eft_silencer_ar10_prsqdc.printname"] = "Silenciador KAC PRS/QDC de 7,62x51 mm"
L["eft_silencer_ar10_prsqdc.compactname"] = "PRS QDC"
L["eft_silencer_ar10_prsqdc.description"] = "Un silenciador Knight's Armament Company PRS QDC, que puede instalarse en las bocachas KAC QDC Flash Supressor Kit y KAC QDC Muzzle Brake Kit."

L["eft_muzzle_ar10_bmda3.printname"] = "Adaptador Lantac Direct Thread A3 para la bocacha BMD Blast Mitigation Device"
L["eft_muzzle_ar10_bmda3.compactname"] = "BMD A3"
L["eft_muzzle_ar10_bmda3.description"] = "Un adaptador Direct Thread A3 producido por Lantac, diseñado para instalar la bocacha BMD Blast Mitigation Device en el cañón."

L["eft_muzzle_bmd_bmd762.printname"] = "Bocacha Lantac BMD Blast Mitigation Device de 7,62x51 mm"
L["eft_muzzle_bmd_bmd762.compactname"] = "BMD 762"
L["eft_muzzle_bmd_bmd762.description"] = "La bocacha BMD Blast Mitigation Device, producida por Lantac, es un protector de estallidos único que permite la instalación de un freno de boca en su interior y funcionar con normalidad para mitigar el ascenso del cañón al disparar. Puede instalarse en armamento de calibre 7,62x51 mm OTAN."

L["eft_muzzle_deltatek_dtk_ar10.printname"] = "Freno de boca Delta-Tek DTK-AR10 de 7,62x51 mm para AR-10"
L["eft_muzzle_deltatek_dtk_ar10.compactname"] = "DTK-AR10"
L["eft_muzzle_deltatek_dtk_ar10.description"] = "El freno de boca DTK-AR10 está diseñado para AR-10 y sistemas compatibles. Este freno de boca reduce en gran medida tanto el retroceso como el ascenso del cañón al disparar gracias a una recámara de compensación especial. Producido por Delta-Tek."

-- [[ muzzle_shared ]]
L["eft_muzzle_psr_protector.printname"] = "Bocacha protectora de rosca para el freno de boca AWC PSR"
L["eft_muzzle_psr_protector.compactname"] = "B.P. PSR"
L["eft_muzzle_psr_protector.description"] = "Una bocacha protectora de rosca universal para los frenos de boca producidos por AWC Silencers."

L["eft_muzzle_ar15_onemount.printname"] = "Adaptador Direct Thread Mount para Gemtech ONE"
L["eft_muzzle_ar15_onemount.compactname"] = "Adapt.ONE"
L["eft_muzzle_ar15_onemount.description"] = "Un adaptador de rosca Direct Thread Mount que permite instalar los silenciadores multicalibre Gemtech ONE directamente en la rosca del cañón."

L["eft_silencer_gemtechone.printname"] = "Silenciador multicalibre Gemtech ONE"
L["eft_silencer_gemtechone.compactname"] = "ONE"
L["eft_silencer_gemtechone.description"] = "El silenciador Gemtech ONE está diseñado para usarse con munición de calibre 7,62x51 mm OTAN, aunque también es compatible con otros calibres. Requiere del adaptador Direct Thread Mount para ser instalado."

L["eft_silencer_waveqd.printname"] = "Silenciador Daniel Defense WAVE QD"
L["eft_silencer_waveqd.compactname"] = "WAVE QD"
L["eft_silencer_waveqd.description"] = "Un silenciador Daniel Defense WAVE QD (Separación Rápida) el cual puede ser instalado en los frenos de boca WAVE compatibles de calibre 5,56x45 mm OTAN y 7,62x51 mm OTAN."

L["eft_silencer_ultra5.printname"] = "Silenciador Thunder Beast Arms Ultra 5"
L["eft_silencer_ultra5.compactname"] = "Ultra 5"
L["eft_silencer_ultra5.description"] = "El silenciador Ultra 5 está diseñado para utilizarse con munición de calibre 7,62x51 mm OTAN. Sin embargo, también funciona de maravilla como silenciador multicalibre para diversas plataformas, proporcionando un rendimiento excelente en los calibres 7,62x51 mm OTAN, .300, 6,8 mm SPC, 6,5 mm y 5,56x45 mm OTAN. Solo se puede instalar en frenos de boca fabricados por Thunder Beast Arms."

L["eft_silencer_sdn6.printname"] = "Silenciador multicalibre AAC 762-SDN-6"
L["eft_silencer_sdn6.compactname"] = "SDN-6"
L["eft_silencer_sdn6.description"] = "El silenciador AAC 762 SDN-6 está diseñado para utilizarse con munición de calibre 7,62x51 mm OTAN. Sin embargo, también funciona de maravilla como silenciador multicalibre para diversas plataformas, proporcionando un rendimiento excelente en los calibres 7,62x51 mm OTAN, .300, 6,8 mm SPC, 6,5 mm y 5,56x45 mm OTAN. Solo se puede instalar en bocachas \"51T\" compatibles."

L["eft_muzzle_ar10_keymount.printname"] = "Freno de boca Dead Air Keymount de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_keymount.compactname"] = "Keymount"
L["eft_muzzle_ar10_keymount.description"] = "Un freno de boca producido por Dead Air Silencers, diseñado para ser instalado en sistemas tipo AR-10 y compatibles. Este freno de boca reduce en gran medida tanto el retroceso como el ascenso del cañón al disparar de forma controlada, permitiendo al usuario una rápida adquisición de objetivos."

L["eft_muzzle_dthybrid.printname"] = "Adaptador de rosca Direct Thread Mount para SilencerCo Hybrid 46"
L["eft_muzzle_dthybrid.compactname"] = "DT Hybrid"
L["eft_muzzle_dthybrid.description"] = "Un adaptador de rosca Direct Thread Mount que permite instalar el silenciador SilencerCo Hybrid 46 directamente en la rosca del cañón de diferentes armas."

L["eft_muzzle_pws_cqb.printname"] = "Freno de boca PWS CQB de 5,56x45 mm para AR-15"
L["eft_muzzle_pws_cqb.compactname"] = "PWS CQB"
L["eft_muzzle_pws_cqb.description"] = "El freno de boca PWS CQB de calibre 5,56x45 mm OTAN está diseñado específicamente para armas compactas utilizadas en espacios confinados."

L["eft_silencer_dthybrid.printname"] = "Silenciador multicalibre SilencerCo Hybrid 46"
L["eft_silencer_dthybrid.compactname"] = "Hybrid 46"
L["eft_silencer_dthybrid.description"] = "El silenciador multicalibre SilencerCo Hybrid 46 está diseñado para poder usarse tanto con pistolas de calibre 9 mm hasta .45 ACP y para fusiles de 5,56 mm hasta .45. Sin embargo, se necesita el respectivo adaptador para su correcta instalación en el arma."

L["eft_silencer_r43_556.printname"] = "Silenciador Rotor 43 de 5,56x45 mm"
L["eft_silencer_r43_556.compactname"] = "R43 5,56x45 mm"
L["eft_silencer_r43_556.description"] = "Un freno de boca Rotor 43 que está diseñado para ser instalado en fusiles basados en AR o AK de calibre 5,56x45 mm OTAN. Pese a que legalmente se trata de un freno de boca, también funciona como silenciador."

L["eft_muzzle_ar10_sig_taperlok.printname"] = "Adaptador de bocacha SIG Sauer Taper-LOK de 7,62x51 mm/.300 BLK"
L["eft_muzzle_ar10_sig_taperlok.compactname"] = "A. T-LOK"
L["eft_muzzle_ar10_sig_taperlok.description"] = "El adaptador patentado de monturas \"Taper-LOK\" permite la instalación de varias bocachas en cañones de calibre 7,62x51 mm OTAN y .300 Blackout. Producido por SIG Sauer."

L["eft_muzzle_tlok_3prong.printname"] = "Reductor de fogonazo SIG Sauer 3-Prong de 7,62x51 mm"
L["eft_muzzle_tlok_3prong.compactname"] = "3-Pr. 762"
L["eft_muzzle_tlok_3prong.description"] = "El reductor de fogonazo SIG Sauer de tres puntas puede ser instalado en el adaptador de bocacha patentado Taper-LOK."

L["eft_muzzle_tlok_mirco.printname"] = "Freno de boca SIG Sauer Micro Brake de 7,62x51 mm"
L["eft_muzzle_tlok_mirco.compactname"] = "Micro 762"
L["eft_muzzle_tlok_mirco.description"] = "El freno de boca SIG Sauer Micro Brake puede ser instalado en el adaptador de bocacha patentado Taper-LOK."

L["eft_muzzle_tlok_tpb.printname"] = "Freno de boca SIG Sauer Two Port Brake de 7,62x51 mm"
L["eft_muzzle_tlok_tpb.compactname"] = "TPB 762"
L["eft_muzzle_tlok_tpb.description"] = "El freno de boca SIG Sauer Two Port Brake puede ser instalado en el adaptador de bocacha patentado Taper-LOK."

L["eft_silencer_ar10_tlok_srd.printname"] = "Silenciador SIG Sauer SRD762-QD de 7,62x51 mm"
L["eft_silencer_ar10_tlok_srd.compactname"] = "SRD762-QD"
L["eft_silencer_ar10_tlok_srd.description"] = "El SRD762-QD es un silenciador de titanio grado 5, requiere de adaptadores de bocacha tipo \"Quick-Release\" (Taper-LOK) para montar el silenciador al arma. Producido por SIG Sauer."

L["eft_muzzle_ar10_qdc_sup.printname"] = "Reductor de fogonazo KAC QDC Flash Suppressor Kit de 7,62x51 mm para AR-10"
L["eft_muzzle_ar10_qdc_sup.compactname"] = "KAC QDC"
L["eft_muzzle_ar10_qdc_sup.description"] = "El Knight's Armament Company Flash Suppressor Kit es muy efectivo para suprimir los fogonazos y que también sirve como plataforma de anclaje del silenciador PRS QDC. Permite su instalación en sistemas basados en AR-10."

L["eft_silencer_ar10_srdti.printname"] = "Silenciador SIG Sauer SRD762Ti de 7,62x51 mm"
L["eft_silencer_ar10_srdti.compactname"] = "SRD762"
L["eft_silencer_ar10_srdti.description"] = "El SRD762Ti es un silenciador de titanio grado 5, diseñado para su uso en cañones de calibres .308, 7,62 mm y .300 BLK, producido por SIG Sauer."

L["eft_muzzle_deltatek_dtk_ar15.printname"] = "Freno de boca Delta-Tek DTK-M16 de 5,56x45 mm para AR-15"
L["eft_muzzle_deltatek_dtk_ar15.compactname"] = "DTK-M16"
L["eft_muzzle_deltatek_dtk_ar15.description"] = "El freno de boca DTK-M16 está diseñado para AR-15 y sistemas compatibles. Este freno de boca reduce en gran medida tanto el retroceso como el ascenso del cañón al disparar gracias a una recámara de compensación especial. Producido por Delta-Tek."

L["eft_muzzle_carlson_ar15.printname"] = "Compensador DoubleStar Carlson Tac Comp de 5,56x45 mm para AR-15"
L["eft_muzzle_carlson_ar15.compactname"] = "CC450 556"
L["eft_muzzle_carlson_ar15.description"] = "El compensador Carlson Tac Comp está diseñado para AR-15 y sistemas compatibles. Reduce el retroceso como el ascenso del cañón al disparar. Ideal para armas de cañón corto. Producido por DoubleStar."

-- [[ optic ]]
L["eft_optic_1p78_eyecap.printname"] = "Ocular para la óptica NPZ 1P78"
L["eft_optic_1p78_eyecap.compactname"] = "O. 1P78"
L["eft_optic_1p78_eyecap.description"] = "Un ocular de goma para la óptica NPZ 1P78."

L["eft_optic_553.printname"] = "Mira holográfica EOTech 553"
L["eft_optic_553.compactname"] = "553"
L["eft_optic_553.description"] = "Una mira holográfica EOTech 553. Gracias a sus características de diseño, puede utilizarse con una amplia gama de armas, incluso con alzas no plegables. También es compatible con dispositivos de visión nocturna mediante el modo nocturno de la retícula. Es ampliamente utilizado por el SOCOM de los EEUU como mira principal para operaciones en espacios reducidos."

L["eft_optic_acrop1.printname"] = "Mira réflex Aimpoint ACRO P-1"
L["eft_optic_acrop1.compactname"] = "ACRO P-1"
L["eft_optic_acrop1.description"] = "Una mira réflex compacta ACRO P-1, producida por Aimpoint, diseñada para instalarse en monturas especiales. Es ligera, compacta y resistente."

L["eft_optic_compm4.printname"] = "Mira réflex Aimpoint CompM4"
L["eft_optic_compm4.compactname"] = "CompM4"
L["eft_optic_compm4.description"] = "Las miras réflex de la serie Aimpoint CompM4 son las más resistentes y duraderas que Aimpoint ha diseñado jamás. Se encuentran en servicio en muchas unidades militares de los países que integran la OTAN. Estas miras son extremadamente robustas y también son capaces de funcionar de forma continuada por más de 8 años, con una simple pila AA. La mira Aimpoint CompM4 es la versión más moderna adoptada por el U.S. Army como M68CCO (Close-Combat Optic), continuando el legado que Aimpoint ha mantenido desde 1997."

L["eft_optic_deltapoint.printname"] = "Mira réflex Leupold DeltaPoint"
L["eft_optic_deltapoint.compactname"] = "DP"
L["eft_optic_deltapoint.description"] = "La mira réflex compacta DeltaPoint de Leupold no conoce límites de aplicación. Diseñada originalmente para usarse en escopetas de corredera para competiciones y prácticas de tiro, ha sido reconocida por su buen rendimiento en todo tipo de armas. Tanto para escopetas y pistolas como para fusiles de asalto. Puede utilizarse como mira principal o auxiliar, siempre da buenos resultados."

L["eft_optic_exps3_tan.printname"] = "Mira holográfica EOTech EXPS3 (Tan)"
L["eft_optic_exps3_tan.compactname"] = "EXPS3"
L["eft_optic_exps3_tan.description"] = "Una mira holográfica EOTech EXPS3. Gracias a su diseño, puede utilizarse en una gran variedad de armas, incluso usarla con puntos de mira y alzas no abatibles. También es compatible con dispositivos de visión nocturna gracias a la retícula con modo para visión nocturna. El US SOCOM la utiliza como mira para operaciones en espacios cerrados."

L["eft_optic_ff3.printname"] = "Mira réflex Burris FastFire 3"
L["eft_optic_ff3.compactname"] = "FF3"
L["eft_optic_ff3.description"] = "Ligera y resistente, la serie de miras de colimador FastFire permite poner sobre tu objetivo un punto rojo brillante e inconfundible, con rapidez y puntería superior. Se puede utilizar como mira primaria, o como secundaria montada sobre otra mira u óptica."

L["eft_optic_h2.printname"] = "Mira réflex Aimpoint Micro H-2"
L["eft_optic_h2.compactname"] = "H-2"
L["eft_optic_h2.description"] = "La mira réflex compacta Micro H-2 de Aimpoint, fue diseñada para usarse con cualquier tipo de arma de fuego... e incluso con arcos. Ligera, compacta y resistente."

L["eft_optic_hhs1.printname"] = "Mira holográfica híbrida EOTech HHS-1"
L["eft_optic_hhs1.compactname"] = "HHS-1"
L["eft_optic_hhs1.description"] = "La mira holográfica híbrida HHS-1 (Holographic Hybrid Sight 1) se caracteriza por incorporar una mira holográfica EXPS3-4 junto con una óptica abatible G33.STS de x4 aumentos, todo sobre la misma montura. Producido por EOTech."

L["eft_optic_hhs1_tan.printname"] = "Mira holográfica híbrida EOTech HHS-1 (Tan)"
L["eft_optic_hhs1_tan.compactname"] = "HHS-1 Tan"
L["eft_optic_hhs1_tan.description"] = "La mira holográfica híbrida HHS-1 (Holographic Hybrid Sight 1) se caracteriza por incorporar una mira holográfica EXPS3-4 junto con una óptica abatible G33.STS de x4 aumentos, todo sobre la misma montura. Producido por EOTech. Versión de color Tan."

L["eft_optic_hs401g5.printname"] = "Mira réflex Holosun HS401G5"
L["eft_optic_hs401g5.compactname"] = "HS401G5"
L["eft_optic_hs401g5.description"] = "Una mira réflex HS401G5 con un designador láser integral, producida por Holosun."

L["eft_optic_kmz_eyecap.printname"] = "Ocular para la mira KMZ 1P59"
L["eft_optic_kmz_eyecap.compactname"] = "O. 1P59"
L["eft_optic_kmz_eyecap.description"] = "Un ocular de goma para la óptica KMZ 1P59."

L["eft_optic_kobra.printname"] = "Mira réflex Axion Kobra EKP-8-18"
L["eft_optic_kobra.compactname"] = "EKP-8-18"
L["eft_optic_kobra.description"] = "La mira réflex Kobra es muy popular entre las agencias de seguridad y tiradores civiles. Fue diseñada por las Fuerzas Armadas de la Federación Rusa, pero finalmente no fueron adoptadas formalmente para el servicio. Producida por Axion."

L["eft_optic_krechet.printname"] = "Mira réflex Valday Krechet"
L["eft_optic_krechet.compactname"] = "Krechet"
L["eft_optic_krechet.description"] = "Una mira réflex Krechet, diseñada para disparos de precisión durante el día y durante la noche al usarse en conjunto con dispositivos de visión nocturna."

L["eft_optic_mrs.printname"] = "Mira réflex Walther MRS"
L["eft_optic_mrs.compactname"] = "MRS"
L["eft_optic_mrs.description"] = "La mira réflex Walther MRS (Multi-Reticle Sight) permite seleccionar entre dos tipos de puntos y dos retículas, con niveles de brillo ajustable. Es barata, precisa (aunque se debe ajustar el cero al cambiar el tipo de retícula), compacta, duradera y trae una batería con mucha capacidad, aunque no muy resistente y fiable."

L["eft_optic_nspu_eyecap.printname"] = "Ocular para óptica NSPU-M"
L["eft_optic_nspu_eyecap.compactname"] = "O. NSPU-M"
L["eft_optic_nspu_eyecap.description"] = "Un ocular de goma para la óptica de visión nocturna NSPU-M."

L["eft_optic_okp7.printname"] = "Mira réflex OKP-7"
L["eft_optic_okp7.compactname"] = "OKP-7"
L["eft_optic_okp7.description"] = "El diseño original de la mira OKP permite disparar desde posiciones inestables en condiciones de movilidad variable, reduce la fatiga del operador, y si fuese necesario, no obstaculiza el uso de las miras estándar del arma. La característica clave de esta mira es su cristal reflector sin montura que permite la visión de todo el campo de tiro sin distracciones. En su lugar, el reflector está protegido por un visor fino y duradero que es casi inapreciable durante los disparos."

L["eft_optic_p1x42.printname"] = "Mira réflex VOMZ Pilad P1x42 Weaver"
L["eft_optic_p1x42.compactname"] = "P1x42"
L["eft_optic_p1x42.description"] = "Una mira réflex de tipo abierto. Diseñada para que las armas deportivas y de caza puedan apuntar con precisión a diferentes tipos de objetivos, incluidos los que se mueven rápidamente. Producida por VOMZ Pilad."

L["eft_optic_pk06.printname"] = "Mira réflex BelOMO PK-06"
L["eft_optic_pk06.compactname"] = "PK-06"
L["eft_optic_pk06.description"] = "Una mira réflex abierta moderna con ajuste de brillo automático en la retícula y que permite la selección de tres tipos de retículas diferentes. Producida por Zenit-BelOMO."

L["eft_optic_pk120.printname"] = "Mira holográfica Valday PK-120 (1P87)"
L["eft_optic_pk120.compactname"] = "PK-120"
L["eft_optic_pk120.description"] = "La PK-120 (Índice GRAU - 1P87) es una mira holográfica producida por Valday. Es parte del complejo DPSK (Complejo de Mira y Observación Diurna y Nocturna) del diseño experimental \"Ratnik\"."

L["eft_optic_pro.printname"] = "Mira réflex Aimpoint PRO"
L["eft_optic_pro.compactname"] = "PRO"
L["eft_optic_pro.description"] = "La mira réflex Aimpoint PRO (Patrol Rifle Optic) se instala en las monturas QRP2 o LRP, las cuales se acoplan a los raíles Picatinny del arma sin necesidad de herramientas adicionales. La mira tiene dos tapas ópticas plegables - una sólida al frente y una transparente en la parte de atrás - permitiéndole al usuario el poder disparar en situaciones de emergencia con las tapas colocadas y con ambos ojos abiertos. La mira PRO es completamente compatible con todas las generaciones de visión nocturna."

L["eft_optic_pso_eyecap.printname"] = "Ocular para óptica PSO"
L["eft_optic_pso_eyecap.compactname"] = "O. PSO"
L["eft_optic_pso_eyecap.description"] = "Un ocular de goma para la familia de ópticas PSO."

L["eft_optic_reapir_eyecup.printname"] = "Ocular para óptica Trijicon REAP-IR"
L["eft_optic_reapir_eyecup.compactname"] = "O. REAP-IR"
L["eft_optic_reapir_eyecup.description"] = "Un ocular de goma para la óptica térmica Trijicon REAP-IR."

L["eft_optic_rmr.printname"] = "Mira réflex Trijicon RMR"
L["eft_optic_rmr.compactname"] = "RMR"
L["eft_optic_rmr.description"] = "La RMR (Ruggedized Miniature Reflex) es una mira réflex duradera, ligera y compacta. Puede ser instalada como mira principal o de respaldo. Requiere de una montura especial para su instalación en raíles Weaver. Producida por Trijicon."

L["eft_optic_romeo4.printname"] = "Mira réflex SIG Sauer ROMEO4"
L["eft_optic_romeo4.compactname"] = "ROMEO4"
L["eft_optic_romeo4.description"] = "La SIG Sauer ROMEO4 es una mira réflex compacta que otorga una adquisición de objetivos rápida y precisa."

L["eft_optic_romeo8t.printname"] = "Mira réflex SIG Sauer ROMEO8T"
L["eft_optic_romeo8t.compactname"] = "ROMEO8T"
L["eft_optic_romeo8t.description"] = "Una mira réflex ROMEO8T, diseñada para disparos de precisión con fusiles deportivos modernos y escopetas. Producida por SIG Sauer."

L["eft_optic_srs02.printname"] = "Mira réflex Trijicon SRS-02"
L["eft_optic_srs02.compactname"] = "SRS-02"
L["eft_optic_srs02.description"] = "La mira réflex Trijicon SRS Sealed Reflex Sight es compacta y ocupa menos espacio en el raíl, manteniendo la durabilidad y el rendimiento. Su gran apertura de 38 mm permite un amplio campo de visión y una rápida adquisición de objetivos. La Trijicon SRS Sealed Reflex Sight cuenta con un punto de 1,75 MOA para mayor precisión. La SRS se alimenta de una fotocélula asistida por una pila de tipo AA."

L["eft_optic_t1.printname"] = "Mira réflex Aimpoint Micro T-1"
L["eft_optic_t1.compactname"] = "T-1"
L["eft_optic_t1.description"] = "La mira réflex compacta Micro T-1 de Aimpoint, fue diseñada para usarse con cualquier tipo de arma de fuego... e incluso con arcos. Ligera, compacta y resistente."

L["eft_optic_t12w.printname"] = "Mira réflex térmica Torrey Pines Logic T12W 30 Hz"
L["eft_optic_t12w.compactname"] = "T12W"
L["eft_optic_t12w.description"] = "Una mira \"réflex\" térmica compacta de baja tasa de refrescamiento. Producida por Torrey Pines Logic."

L["eft_optic_tulpan_eyecap.printname"] = "Ocular para la óptica NPZ USP-1"
L["eft_optic_tulpan_eyecap.compactname"] = "O. USP-1"
L["eft_optic_tulpan_eyecap.description"] = "Un protector ocular de goma para la óptica NPZ USP-1."

L["eft_optic_uh1.printname"] = "Mira holográfica Vortex Razor AMG UH-1"
L["eft_optic_uh1.compactname"] = "UH-1"
L["eft_optic_uh1.description"] = "La mira holográfica Vortex Razor AMG UH-1 no es tan solo un diseño futurista. De acuerdo a sus creadores, representa una auténtica revolución en el campo de las miras réflex y holográficas."

L["eft_optic_utg.printname"] = "Mira réflex Leapers UTG"
L["eft_optic_utg.compactname"] = "UTG"
L["eft_optic_utg.description"] = "Una mira réflex UTG de punto Rojo/Verde, producida por Leapers Inc. Este modelo es compacto y de tamaño reducido, el cual puede ser acoplado en raíles Weaver/Picatinny."

L["eft_optic_vulcan_eyecup.printname"] = "Ocular para óptica Armasight Vulcan MG"
L["eft_optic_vulcan_eyecup.compactname"] = "O. Vulcan MG"
L["eft_optic_vulcan_eyecup.description"] = "Un ocular de goma para la óptica de visión nocturna Vulcan MG, producido por Armasight."

L["eft_optic_xps3_0.printname"] = "Mira holográfica EOTech XPS3-0"
L["eft_optic_xps3_0.compactname"] = "XPS3-0"
L["eft_optic_xps3_0.description"] = "La mira holográfica XPS3 es más compacta que otras de su clase, pero igual de efectiva. Su tamaño reducido deja más espacio de montaje para equipo adicional. Tanto los cazadores como los operativos de las fuerzas armadas lo valoran por su ligereza y tamaño reducido, características muy importantes en misiones de larga duración. El modelo 3-0 presenta una retícula de un punto de 1 MOA."

L["eft_optic_xps3_2.printname"] = "Mira holográfica EOTech XPS3-2"
L["eft_optic_xps3_2.compactname"] = "XPS3-2"
L["eft_optic_xps3_2.description"] = "La mira holográfica XPS3 es más compacta que otras de su clase, pero igual de efectiva. Su tamaño reducido deja más espacio de montaje para equipo adicional. Tanto los cazadores como los operativos de las fuerzas armadas lo valoran por su ligereza y tamaño reducido, características muy importantes en misiones de larga duración. El modelo 3-2 presenta una retícula de dos puntos de 1 MOA."

L["eft_aux_ekp_shade.printname"] = "Antirreflejos para la mira Axion Kobra"
L["eft_aux_ekp_shade.compactname"] = "Ant. Kobra"
L["eft_aux_ekp_shade.description"] = "Un antirreflejos para las miras de la familia Kobra. Protege el lente contra golpes mecánicos y reduce los reflejos. Producido por Axion."

L["eft_optic_targetring.printname"] = "Mira réflex VOMZ Pilad TargetRing"
L["eft_optic_targetring.compactname"] = "TargetRing"
L["eft_optic_targetring.description"] = "Diseñada para cazar aves o presas pequeñas, así como el disparo deportivo a objetivos estacionarios o en movimiento rápido. Es ideal para estos propósitos, ya que crea un círculo contrastado sobre el objetivo, proporcionando al tirador la oportunidad de adelantarse a la dirección de movimiento de este sin perder velocidad. Solo se puede montar en armas de ánima lisa (escopetas)."

L["eft_optic_elcan_hco.printname"] = "Mira holográfica ELCAN Specter HCO"
L["eft_optic_elcan_hco.compactname"] = "HCO"
L["eft_optic_elcan_hco.description"] = "Una mira Specter Holographic Combat Optic. Producida por ELCAN."

L["eft_optic_sig_romeo7.printname"] = "Mira réflex SIG Sauer ROMEO7 1x30 (Prototipo)"
L["eft_optic_sig_romeo7.compactname"] = "ROMEO7"
L["eft_optic_sig_romeo7.description"] = "Uno de los primeros prototipos de la mira réflex ROMEO7 1x30. Producida por SIG Sauer."

L["eft_optic_zeus_eyecup.printname"] = "Ocular para óptica Armasight Zeus-Pro"
L["eft_optic_zeus_eyecup.compactname"] = "O. ZeusPro"
L["eft_optic_zeus_eyecup.description"] = "Un ocular de goma para la óptica térmica Zeus-Pro 640. Producido por Armasight."

L["eft_optic_boss.printname"] = "Mira réflex Wilcox BOSS Xe"
L["eft_optic_boss.compactname"] = "BOSS Xe"
L["eft_optic_boss.description"] = "Una mira réflex BOSS (Ballistically Optimized Sighting System; Sistema de Mira Balísticamente Optimizado) que combina las funciones de un punto rojo, un bloque táctico y miras metálicas. Producida por Wilcox."

L["eft_optic_shakhin_eyecup.printname"] = "Cyclone Shakhin scope eyecup"
L["eft_optic_shakhin_eyecup.compactname"] = "Shakhin"
L["eft_optic_shakhin_eyecup.description"] = "A rubber eyecup for the Shakhin thermal scope. Manufactured by Central Research Institute Cyclone."

-- [[ rearsight ]]
L["eft_rearsight_a2.printname"] = "Alza Colt A2 para AR-15"
L["eft_rearsight_a2.compactname"] = "A. A2"
L["eft_rearsight_a2.description"] = "Un alza desmontable Colt A2. Estándar para los fusiles de asalto M4A1."

L["eft_rearsight_kac.printname"] = "Alza abatible KAC"
L["eft_rearsight_kac.compactname"] = "A. KAC"
L["eft_rearsight_kac.description"] = "Un alza abatible y desmontable para ser instalada en diversas armas, producida por Knight's Armament Company."

L["eft_rearsight_kacmicro.printname"] = "Alza abatible KAC Micro"
L["eft_rearsight_kacmicro.compactname"] = "A. KAC M"
L["eft_rearsight_kacmicro.description"] = "Un alza abatible compacta y desmontable, producida por Knight's Armament Company."

L["eft_rearsight_kriss_defiance.printname"] = "Alza abatible KRISS Defiance de perfil bajo"
L["eft_rearsight_kriss_defiance.compactname"] = "A. Defiance"
L["eft_rearsight_kriss_defiance.description"] = "Un alza Defiance abatible y desmontable de perfil bajo, para ser instalada sobre la montura del arma. Producida por KRISS."

L["eft_rearsight_m4carry.printname"] = "Mira trasera/Asa de transporte para AR-15"
L["eft_rearsight_m4carry.compactname"] = "M.T. Asa"
L["eft_rearsight_m4carry.description"] = "Un conjunto desmontable de asa de transporte con alza (Mira trasera) para los fusiles AR-15. Viene por defecto en los fusiles de asalto M4A1."

L["eft_rearsight_mbus.printname"] = "Alza abatible Magpul MBUS Gen2"
L["eft_rearsight_mbus.compactname"] = "A. MBUS"
L["eft_rearsight_mbus.description"] = "Un alza MBUS Gen2 abatible y desmontable, para ser instalada sobre la montura. Producida por Magpul."

L["eft_rearsight_mbus_fde.printname"] = "Alza abatible Magpul MBUS Gen2 (FDE)"
L["eft_rearsight_mbus_fde.compactname"] = "A. MBUS"
L["eft_rearsight_mbus_fde.description"] = "Un alza MBUS Gen2 abatible y desmontable, para ser instalada sobre la montura. Producida por Magpul. Versión de color Flat Dark Earth."

L["eft_rearsight_mcx.printname"] = "Alza abatible para MCX"
L["eft_rearsight_mcx.compactname"] = "A. MCX"
L["eft_rearsight_mcx.description"] = "Un alza abatible y desmontable para los fusiles de asalto MCX, diseñada originalmente para la plataforma AR. Producida por SIG Sauer."

L["eft_rearsight_mp7.printname"] = "Alza abatible para HK MP7"
L["eft_rearsight_mp7.compactname"] = "A. MP7"
L["eft_rearsight_mp7.description"] = "Un alza abatible y desmontable para los subfusiles MP7. Producida por Heckler & Koch."

L["eft_rearsight_mpx.printname"] = "Alza abatible para MPX"
L["eft_rearsight_mpx.compactname"] = "A. MPX"
L["eft_rearsight_mpx.description"] = "Un alza abatible y desmontable para los subfusiles MPX, se instala sobre la montura del cuerpo superior. Producida por SIG Sauer."

-- [[ scope ]]
L["eft_scope_25mm_pilad.printname"] = "Óptica VOMZ Pilad 4x32 de 25,4 mm"
L["eft_scope_25mm_pilad.compactname"] = "Pilad 4x32"
L["eft_scope_25mm_pilad.description"] = "Diseñada para disparar en corta y media distancia. Es un tipo de óptica muy conveniente para disparar a objetivos en movimiento a distancias de hasta 150 metros."

L["eft_scope_30mm_march.printname"] = "Óptica March Tactical 3-24x42 FFP de 30 mm"
L["eft_scope_30mm_march.compactname"] = "3-24x42 FFP"
L["eft_scope_30mm_march.description"] = "La óptica March Tactical 3-24x42 FFP es fiable y precisa con cualquier munición de cualquier calibre."

L["eft_scope_30mm_mark4.printname"] = "Óptica Leupold Mark 4 LR 6.5-20x50 de 30 mm"
L["eft_scope_30mm_mark4.compactname"] = "6.5-20x50"
L["eft_scope_30mm_mark4.description"] = "Una óptica Leupold Mark 4 6.5-20x50 - precisa, certera, resistente y fiable. Este modelo proporciona una magnificación de 6,5x a 20x, para que te hagas a una idea del tamaño real del objetivo al que apuntas."

L["eft_scope_30mm_razor.printname"] = "Óptica Vortex Razor HD Gen.2 1-6x24 de 30 mm"
L["eft_scope_30mm_razor.compactname"] = "Razor HD Gen.2"
L["eft_scope_30mm_razor.description"] = "Una óptica táctica diseñada para proporcionar una rápida adquisición de objetivos y la máxima precisión. Producida por Vortex Optics."

L["eft_scope_30mm_sb_pmii1.printname"] = "Óptica Schmidt & Bender PM II 1-8x24 de 30 mm"
L["eft_scope_30mm_sb_pmii1.compactname"] = "PM II 1-8x24"
L["eft_scope_30mm_sb_pmii1.description"] = "La óptica Schmidt & Bender PM II 1-8x24 fue creada originalmente para fuerzas militares de élite para una adquisición de objetivos con alta calidad y precisión."

L["eft_scope_30mm_tac30.printname"] = "Óptica Burris FullField TAC30 1-4x24 de 30 mm"
L["eft_scope_30mm_tac30.compactname"] = "TAC30"
L["eft_scope_30mm_tac30.description"] = "La ótica TAC30 ofrece un amplio campo de visión y su magnificación más baja es de 1x, de esta forma, la adquisición de objetivos se vuelve más fácil y rápida. Su magnificación máxima de 4x, junto con su retícula Ballistic CQ hacen muy sencillo el poder impactar a objetivos a 300 yardas. El brillante círculo central permite entablar combate de forma inmediata ante objetivos a cortas distancias."

L["eft_scope_30mm_vudu.printname"] = "Óptica EOTech Vudu 1-6x24 de 30 mm"
L["eft_scope_30mm_vudu.compactname"] = "Vudu 1-6x24"
L["eft_scope_30mm_vudu.description"] = "La óptica EOTech Vudu 1-6x24 es efectiva tanto en plataformas AR-15 como en fusiles de cerrojo. Su retícula circular estilo EOTech está diseñada para una fácil adquisición de objetivos al usar una baja magnificación y al mismo tiempo, proporciona la resolución y precisión necesarias para disparos a larga distancia."

L["eft_scope_34mm_atacr.printname"] = "Óptica Nightforce ATACR 7-35x56 de 34 mm"
L["eft_scope_34mm_atacr.compactname"] = "ATACR 7-35x56"
L["eft_scope_34mm_atacr.description"] = "La NightForce ATACR 7-35x56 de trata de una óptica versátil con un extenso grado de magnificación y un amplio campo de visión, haciendo fácil el adquirir y enfrentar a los objetivos."

L["eft_scope_34mm_hsff.printname"] = "Óptica Hensoldt FF 4-16x56 de 34 mm"
L["eft_scope_34mm_hsff.compactname"] = "FF 4-16"
L["eft_scope_34mm_hsff.description"] = "La óptica Hensoldt FF 4-16x56 ha sido desarrollada para ofrecer el máximo rendimiento con alta capacidad de ajuste y manteniendo un sistema óptico corto. La versión FF de la óptica tiene la retícula fijada en el primer plano de la imagen, de manera que el patrón de la retícula también aumenta cuando se cambia el aumento. Esto hace posible determinar la distancia al objetivo utilizando el MIL-DOT, sin necesidad de hacer ningún cálculo."

L["eft_scope_34mm_sb_pmii3.printname"] = "Óptica Schmidt & Bender PM II 3-20x50 de 34 mm"
L["eft_scope_34mm_sb_pmii3.compactname"] = "PM II 3-20x50"
L["eft_scope_34mm_sb_pmii3.description"] = "La óptica Schmidt & Bender PM II 3-20x50 fue creada originalmente para fuerzas militares de élite para una adquisición de objetivos con alta calidad y precisión."

L["eft_scope_34mm_sb_pmii5.printname"] = "Óptica Schmidt & Bender PM II 5-25x56 de 34 mm"
L["eft_scope_34mm_sb_pmii5.compactname"] = "PM II 5-25x56"
L["eft_scope_34mm_sb_pmii5.description"] = "La óptica Schmidt & Bender PM II 5-25x56 fue creada originalmente para fuerzas militares de élite para una adquisición de objetivos con alta calidad y precisión."

L["eft_scope_acog_ta01.printname"] = "Óptica de asalto Trijicon ACOG TA01NSN 4x32 (Negra)"
L["eft_scope_acog_ta01.compactname"] = "TA01NSN"
L["eft_scope_acog_ta01.description"] = "La óptica Trijicon ACOG TA01NSN es el modelo ACOG más popular de Trijicon, diseñada según las especificaciones exactas del US Special Operations Command para ser usada por fuerzas estadounidenses de élite. El rango de la retícula permite una compensación de caída de bala de hasta 600 metros sin tener que realizar ajustes manuales. También es posible instalar una mira de respaldo. No requiere baterías. Versión de color Negro."

L["eft_scope_acog_ta01_tan.printname"] = "Óptica de asalto Trijicon ACOG TA01NSN 4x32 (Tan)"
L["eft_scope_acog_ta01_tan.compactname"] = "TA01NSN"
L["eft_scope_acog_ta01_tan.description"] = "La óptica Trijicon ACOG TA01NSN es el modelo ACOG más popular de Trijicon, diseñada según las especificaciones exactas del US Special Operations Command para ser usada por fuerzas estadounidenses de élite. El rango de la retícula permite una compensación de caída de bala de hasta 600 metros sin tener que realizar ajustes manuales. También es posible instalar una mira de respaldo. No requiere baterías. Versión de color Tan."

L["eft_scope_acog_ta11d.printname"] = "Óptica de asalto Trijicon ACOG TA11D 3,5x35"
L["eft_scope_acog_ta11d.compactname"] = "TA11D"
L["eft_scope_acog_ta11d.description"] = "Una óptica de asalto ACOG 3,5x35 para fusil, producida por Trijicon. Sigue siendo la favorita entre tiradores de competición, agencias policiales y civiles amantes de la libertad alrededor del mundo entero."

L["eft_scope_adop4.printname"] = "Óptica NcSTAR ADO P4 Sniper 3-9x42"
L["eft_scope_adop4.compactname"] = "ADO P4"
L["eft_scope_adop4.description"] = "Una óptica Advance Dual Optic (ADO) de magnificación variable de 3x-9x y cuenta con unas lentes de 42 mm. La óptica ADO cuenta con una mira réflex de punto rojo integrada sobre el cuerpo principal. Producida por NcSTAR."

L["eft_scope_bravo4.printname"] = "Óptica de asalto SIG Sauer BRAVO4 4x30"
L["eft_scope_bravo4.compactname"] = "BRAVO4"
L["eft_scope_bravo4.description"] = "Diseñada por SIG Sauer, la óptica BRAVO 4X30 es única por su gran campo de visión, un 43% más amplio que el de sus competidores más cercanos. También posee un raíl extra en la parte superior que permite la instalación de una mira compacta de respaldo."

L["eft_scope_compactprism.printname"] = "Óptica Monstrum Tactical Compact Prism Scope 2x32"
L["eft_scope_compactprism.compactname"] = "Compact 2x32"
L["eft_scope_compactprism.description"] = "Una óptica refractiva compacta con magnificación de 2x, producida por Monstrum Tactical."

L["eft_scope_dovetail_1p78.printname"] = "Óptica NPZ 1P78-1 2,8x"
L["eft_scope_dovetail_1p78.compactname"] = "1P78-1"
L["eft_scope_dovetail_1p78.description"] = "Una óptica diurna con magnificación de 2,8x, se instala en una montura Dovetail especial. Producida por NPZ."

L["eft_scope_dovetail_ekp802.printname"] = "Mira réflex Axion Kobra EKP-8-02 (Dovetail)"
L["eft_scope_dovetail_ekp802.compactname"] = "EKP-8-02 DT"
L["eft_scope_dovetail_ekp802.description"] = "La mira réflex Cobra es muy popular entre las agencias de seguridad y los tiradores civiles. Fue diseñada por las Fuerzas Armadas de la Federación Rusa, pero nunca fue adoptada oficialmente. Esta versión está diseñada para ser instalada en raíles tipo Dovetail. Producida por Axion."

L["eft_scope_dovetail_nspum.printname"] = "Óptica de visión nocturna NSPU-M 3.5x Dovetail"
L["eft_scope_dovetail_nspum.compactname"] = "NSPU-M"
L["eft_scope_dovetail_nspum.description"] = "NSPU-M (Nochnóy Strelkóvyy Pritsél Unifitsírovannyy Modernizírovannyy - \"Óptica Nocturna Unificada para Fusil, Modernizada\") Índice GRAU - 1PN58. Una antigua óptica soviética con visión nocturna para la serie de fusiles AK, diseñada para ser instalada en los raíles Dovetail."

L["eft_scope_dovetail_okp7.printname"] = "Mira réflex OKP-7 (Dovetail)"
L["eft_scope_dovetail_okp7.compactname"] = "OKP-7 DT"
L["eft_scope_dovetail_okp7.description"] = "El diseño original de la mira OKP permite disparar desde posiciones inestables en condiciones de movilidad variable, reduce la fatiga del operador, y si fuese necesario, no obstaculiza el uso de las miras estándar del arma. La característica clave de esta mira es su cristal reflector sin montura que permite la visión de todo el campo de tiro sin distracciones. En su lugar, el reflector está protegido por un visor fino y duradero que es casi inapreciable durante los disparos. Esta variante se instala en una montura de tipo Dovetail."

L["eft_scope_dovetail_pk1.printname"] = "Mira réflex NPZ PK1 Obzor Dovetail"
L["eft_scope_dovetail_pk1.compactname"] = "Obzor"
L["eft_scope_dovetail_pk1.description"] = "Una mira réflex diseñada para proporcionar una rápida adquisición de objetivos a cazadores que operan en temperaturas extremadamente frías, se instala en raíles Dovetail. Producida por NPZ."

L["eft_scope_dovetail_pkaa.printname"] = "Mira réflex BelOMO PK-AA Dovetail"
L["eft_scope_dovetail_pkaa.compactname"] = "PK-AA"
L["eft_scope_dovetail_pkaa.description"] = "Una mira réflex PK-AA, diseñada para ser instalada en fusiles estilo AK de cualquier modelo con montura de raíl Dovetail. Producida por BelOMO."

L["eft_scope_dovetail_pso1.printname"] = "Óptica BelOMO PSO-1 4x24"
L["eft_scope_dovetail_pso1.compactname"] = "PSO-1"
L["eft_scope_dovetail_pso1.description"] = "Una óptica PSO-1 fabricada por BelOMO con estándar de calidad para fusiles de francotirador militares. Esta óptica está diseñada para ser utilizada con el Fusil de Francotirador Dragunov (SVD) y consta de un aumento x4 y 6 grados de campo de visión."

L["eft_scope_dovetail_pso1m2.printname"] = "Óptica BelOMO PSO-1M2-1 4x24"
L["eft_scope_dovetail_pso1m2.compactname"] = "PSO-1M2-1"
L["eft_scope_dovetail_pso1m2.description"] = "Una óptica PSO-1M2-1 fabricada por BelOMO con estándar de calidad para fusiles de francotirador militares. Esta óptica está diseñada para disparos de precisión con el Fusil de Francotirador Especial (VSS) y el Fusil de Francotirador Dragunov (SVD) y consta de un aumento x4 y 6 grados de campo de visión."

L["eft_scope_dovetail_pso1m2non1.printname"] = "Óptica BelOMO PSO-1M2 4x24"
L["eft_scope_dovetail_pso1m2non1.compactname"] = "PSO-1M2"
L["eft_scope_dovetail_pso1m2non1.description"] = "Una óptica PSO-1M2 fabricada por BelOMO con estándar de calidad para fusiles de francotirador militares. Esta óptica está diseñada para disparos de precisión con el Fusil de Francotirador Dragunov (SVD) en una gran variedad de objetivos, cuenta con una magnificación de 4x y 6 grados de campo visual."

L["eft_scope_dovetail_tulpan.printname"] = "Óptica NPZ USP-1 Tyulpan 4x"
L["eft_scope_dovetail_tulpan.compactname"] = "USP-1"
L["eft_scope_dovetail_tulpan.description"] = "La mira de fusil unificada USP-1 \"Tyulpan\" está diseñada para ser capaz de producir fuego preciso con los fusiles de asalto AK-74N, AK-74M, AN-94 y las ametralladoras ligeras RPK-74N y PKMN tanto en operaciones diurnas como nocturnas. Se instala en la montura Dovetail."

L["eft_scope_elcan.printname"] = "Óptica ELCAN SpecterDR 1x/4x"
L["eft_scope_elcan.compactname"] = "SpecterDR"
L["eft_scope_elcan.description"] = "La óptica SpecterDR (Dual Role) 1x/4x, de la serie de ópticas Specter diseñadas por ELCAN, ha marcado un antes y un después en el desarrollo de las ópticas. Se la considera pionera, por ser la primera óptica de aumento variable que realmente posee dos modos de magnificación de 1x y 4x aumentos con un simple toque. También cuenta con una mira metálica de respaldo."

L["eft_scope_elcan_fde.printname"] = "Óptica ELCAN SpecterDR 1x/4x (FDE)"
L["eft_scope_elcan_fde.compactname"] = "SpecterDR"
L["eft_scope_elcan_fde.description"] = "La óptica SpecterDR (Dual Role) 1x/4x, de la serie de ópticas Specter diseñadas por ELCAN, ha marcado un antes y un después en el desarrollo de las ópticas. Se la considera pionera, por ser la primera óptica de aumento variable que realmente posee dos modos de magnificación de 1x y 4x aumentos con un simple toque. También cuenta con una mira metálica de respaldo. Versión de color Flat Dark Earth."

L["eft_scope_flir.printname"] = "Óptica térmica FLIR RS-32 2,25-9x 35 mm 60 Hz"
L["eft_scope_flir.compactname"] = "FLIR RS-32"
L["eft_scope_flir.description"] = "Una óptica de imagen térmica con varios usos y ventajas. Puede instalarse en armas para usarse como mira, en un casco (usando un adaptador) como monocular de visión térmica y como un dispositivo de observación por separado."

L["eft_scope_hamr.printname"] = "Óptica de asalto híbrida Leupold Mark 4 HAMR 4x24 DeltaPoint"
L["eft_scope_hamr.compactname"] = "HAMR"
L["eft_scope_hamr.description"] = "Esta óptica híbrida producida por Leupold se compone de una mira Mark 4 HAMR de 4x24 mm con una mira réflex DeltaPoint instalada encima. Fue desarrollada para realizar fuego de precisión a media distancia con carabinas al usar la magnificación de 4x, y ser efectiva en combate cercano, gracias a su mira réflex compacta."

L["eft_scope_kmz_1p59.printname"] = "Óptica KMZ 1P59 3-10x"
L["eft_scope_kmz_1p59.compactname"] = "1P59"
L["eft_scope_kmz_1p59.description"] = "La poderosa óptica 1P59 \"Giperon\" está diseñada para incrementar la precisión al disparar con fusiles SVD, este incremento en su efectividad puede variar entre 1,3 a 2 veces de mejoría en comparación a la óptica PSO-1 estándar (dependiendo de la distancia y el tamaño del objetivo) gracias a su mayor magnificación, la incorporación de un telémetro más preciso, combinando las operaciones para medir la distancia del objetivo e ingresar la elevación del apuntado, así como designar la escala de los ángulos del apuntado incorporados en el campo de visión de la óptica."

L["eft_scope_kmz_1p69.printname"] = "Óptica KMZ 1P69 3-10x"
L["eft_scope_kmz_1p69.compactname"] = "1P69"
L["eft_scope_kmz_1p69.description"] = "La poderosa óptica 1P59 \"Giperon\" es una versión avanzada de las ópticas \"Giperon\" y está diseñada para instalarse en fusiles de francotirador SV-98 con el fin de incrementar su eficiencia en el campo de batalla. La óptica tiene un ajuste de magnificación flexible, así como un telémetro bastante preciso, facilitando las operaciones para medir la distancia del objetivo e ingresar la elevación del apuntado."

L["eft_scope_pa_prism.printname"] = "Óptica Kiba Arms Short Prism 2,5x"
L["eft_scope_pa_prism.compactname"] = "Prism 2,5x"
L["eft_scope_pa_prism.description"] = "Una óptica Short Prism con magnificación de 2,5x fabricada por Kiba Arms. La baja magnificación de la óptica y la brillante iluminación de su retícula, permiten realizar disparos rápidos a cortas distancias con ambos ojos abiertos."

L["eft_scope_reapir.printname"] = "Óptica térmica Trijicon REAP-IR"
L["eft_scope_reapir.compactname"] = "REAP-IR"
L["eft_scope_reapir.description"] = "Una óptica de imagen térmica con varios usos y ventajas. Puede instalarse en armas para usarse como mira, en un casco (usando un adaptador) como monocular de visión térmica y como un dispositivo de observación por separado."

L["eft_scope_swampfox.printname"] = "Óptica SwampFox Trihawk Prism Scope 3x30"
L["eft_scope_swampfox.compactname"] = "Trihawk 3x30"
L["eft_scope_swampfox.description"] = "Una óptica refractiva con magnificación de 3x, producida por SwampFox."

L["eft_scope_valday_ps320.printname"] = "Óptica Valday PS-320 1/6x"
L["eft_scope_valday_ps320.compactname"] = "PS-320"
L["eft_scope_valday_ps320.description"] = "La PS-320 1x/6x es una óptica prototipo de aumento variable, diseñada por Valday."

L["eft_scope_vulcan.printname"] = "Óptica de visión nocturna Armasight Vulcan MG 3.5x Bravo"
L["eft_scope_vulcan.compactname"] = "Vulcan MG 3.5x"
L["eft_scope_vulcan.description"] = "La Vulcan MG es una óptica de visión nocturna de tercera generación con un aumento de 3,5x, producida por Armasight."

L["eft_scope_echo1.printname"] = "Mira réflex térmica SIG Sauer ECHO1 1-2x30 mm 30 Hz"
L["eft_scope_echo1.compactname"] = "ECHO1"
L["eft_scope_echo1.description"] = "La mira réflex ECHO1 puede emplearse de diversas maneras y proporciona muchas ventajas. Producida por SIG Sauer."

L["eft_scope_zeuspro.printname"] = "Óptica térmica Armasight Zeus-Pro 640 2-8x50 30 Hz"
L["eft_scope_zeuspro.compactname"] = "Zeus-Pro 640"
L["eft_scope_zeuspro.description"] = "Una óptica térmica Zeus-Pro 640 con magnificación de 2x y 8x. Producida por Armasight."

L["eft_scope_30mm_tango6t.printname"] = "Óptica SIG TANGO6T 1-6x24 de 30 mm"
L["eft_scope_30mm_tango6t.compactname"] = "TANGO6T"
L["eft_scope_30mm_tango6t.description"] = "La TANGO6T es una óptica variable de baja potencia, robusta y bien construida. Este modelo cuenta con una magnificación de 1x a 6x y una retícula de primer plano focal. Esta óptica es ideal para distancias cortas y medias, así como para la cacería en zonas rurales. Producida por SIG Sauer."

L["eft_scope_dovetail_ekp1s03.printname"] = "Mira réflex Axion Kobra EKP-1S-03 (Dovetail)"
L["eft_scope_dovetail_ekp1s03.compactname"] = "EKP-1S-03"
L["eft_scope_dovetail_ekp1s03.description"] = "La Kobra se trata de una mira réflex de punto rojo muy popular entre las agencias policiales y tiradores civiles. Fue desarrollada para las Fuerzas Armadas de la Federación Rusa. La EKP-1S-03 se ha ganado la reputación de ser fiable y a prueba de fallas en las condiciones de campo más duras entre cazadores, tiradores deportivos, soldados y coleccionistas. Esta mira está diseñada para ser instalada en las monturas laterales tipo Dovetail. Producida por Axion."

L["eft_scope_35mm_5hd.printname"] = "Óptica Nightforce ATACR 7-35x56 de 34 mm"
L["eft_scope_35mm_5hd.compactname"] = "ATACR 7-35x56"
L["eft_scope_35mm_5hd.description"] = "La NightForce ATACR 7-35x56 de trata de una óptica versátil con un extenso grado de magnificación y un amplio campo de visión, haciendo fácil el adquirir y enfrentar a los objetivos."

L["eft_scope_shakhin.printname"] = "Cyclone Shakhin 3.7x thermal scope"
L["eft_scope_shakhin.compactname"] = "Shakhin"
L["eft_scope_shakhin.description"] = "A 3.7x thermal scope manufactured by Central Research Institute Cyclone."

-- [[ silencer_9x19 ]]
L["eft_silencer_alpha9.printname"] = "Silenciador Alpha Dog Alpha 9 de 9x19 mm"
L["eft_silencer_alpha9.compactname"] = "Alpha 9"
L["eft_silencer_alpha9.description"] = "Un silenciador para pistola con un raíl Weaver que permite la instalación de miras. Producido por Alpha Dog."

L["eft_silencer_illusion.printname"] = "Silenciador AAC Illusion 9 de 9x19 mm"
L["eft_silencer_illusion.compactname"] = "Illusion 9"
L["eft_silencer_illusion.description"] = "El Illusion 9 es un silenciador compacto de alto rendimiento diseñado específicamente para las pistolas semiautomáticas modernas de calibre 9 mm. Fabricado por Advanced Armament Corporation."

L["eft_silencer_osprey9.printname"] = "Silenciador SilencerCo Osprey 9 de 9x19 mm"
L["eft_silencer_osprey9.compactname"] = "Osprey 9"
L["eft_silencer_osprey9.description"] = "Un silenciador ligero para pistolas y subfusiles de calibre 9x19 mm, fabricado por SilencerCo."

L["eft_silencer_r43_9x19.printname"] = "Silenciador Rotor 43 de 9x19 mm"
L["eft_silencer_r43_9x19.compactname"] = "R43 9x19"
L["eft_silencer_r43_9x19.description"] = "Un freno de boca Rotor 43 que está diseñado para ser instalado en los subfusiles PP-19-01 Vityaz de calibre 9x19 mm. Pese a que legalmente se trata de un freno de boca, también funciona como silenciador."

L["eft_silencer_srd9.printname"] = "Silenciador SIG Sauer SRD9 de 9x19 mm"
L["eft_silencer_srd9.compactname"] = "SRD9"
L["eft_silencer_srd9.description"] = "El SRD9 es un silenciador para el calibre 9x19 mm, fabricado por SIG Sauer."

