L["eft_mag_ak_55_762_30.printname"] = "Cargador de 30 cartuchos 7,62x39 mm para AK (Fabricado en 1955 o posterior)"
L["eft_mag_ak_55_762_30.compactname"] = "AK55 30"
L["eft_mag_ak_55_762_30.description"] = "Un cargador estándar de metal con capacidad para 30 cartuchos de 7,62x39 mm, para AK y armamento compatible de 1955 en adelante. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_6l10_762_30.printname"] = "Cargador de baquelita de 30 cartuchos 7,62x39 mm 6L10 para AK"
L["eft_mag_ak_6l10_762_30.compactname"] = "6L10 30"
L["eft_mag_ak_6l10_762_30.description"] = "Un cargador 6L10 fabricado con un compuesto de moldeo AG-4S, con capacidad para 30 cartuchos de 7,62x39 mm, para fusiles AK y sistemas compatibles. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_6l18_545_45.printname"] = "Cargador de 45 cartuchos 5,45x39 mm 6L18 para AK-74"
L["eft_mag_ak_6l18_545_45.compactname"] = "6L18 45"
L["eft_mag_ak_6l18_545_45.description"] = "Un cargador Izhmash 6L18 de baquelita con capacidad para 45 cartuchos de 5,45x39 mm, para el fusil AK-74 y sistemas compatibles. También es el cargador estándar para la ametralladora ligera RPK-74."

L["eft_mag_ak_6l20_545_30.printname"] = "Cargador de 30 cartuchos 5,45x39 mm 6L20 para AK-74"
L["eft_mag_ak_6l20_545_30.compactname"] = "6L20 30"
L["eft_mag_ak_6l20_545_30.description"] = "Un cargador Izhmash 6L20 de baquelita con capacidad para 30 cartuchos de 5,45x39 mm, para el fusil AK-74 y sistemas compatibles."

L["eft_mag_ak_6l23p_545_30.printname"] = "Cargador de 30 cartuchos 5,45x39 mm 6L23 para AK-74 (Ciruela)"
L["eft_mag_ak_6l23p_545_30.compactname"] = "6L23 30"
L["eft_mag_ak_6l23p_545_30.description"] = "Un cargador Izhmash 6L23 de polímero con capacidad para 30 cartuchos de 5,45x39 mm, para el fusil AK-74 y sistemas compatibles. Elaborado con polímero pigmentado de color ciruela, por lo cual, se ha ganado el apodo de \"Sliva\" (Ciruela)."

L["eft_mag_ak_6l23_545_30.printname"] = "Cargador de 30 cartuchos 5,45x39 mm 6L23 para AK-74"
L["eft_mag_ak_6l23_545_30.compactname"] = "6L23 30"
L["eft_mag_ak_6l23_545_30.description"] = "Un cargador Izhmash 6L23 de polímero con capacidad para 30 cartuchos de 5,45x39 mm, para el fusil AK-74 y sistemas compatibles."

L["eft_mag_ak_6l26_545_45.printname"] = "Cargador de 45 cartuchos 5,45x39 mm 6L26 para AK-74"
L["eft_mag_ak_6l26_545_45.compactname"] = "6L26 45"
L["eft_mag_ak_6l26_545_45.description"] = "Un cargador Izhmash 6L26 de polímero con capacidad para 45 cartuchos de 5,45x39 mm, para el fusil AK-74 y sistemas compatibles."

L["eft_mag_ak_6l29_556_30.printname"] = "Cargador de 30 cartuchos 5,56x45 mm 6L29 para AK-101"
L["eft_mag_ak_6l29_556_30.compactname"] = "6L29 30"
L["eft_mag_ak_6l29_556_30.description"] = "Un cargador Izhmash 6L29 de polímero con capacidad para 30 cartuchos de 5,56x45 mm OTAN, para el fusil AK-101 y sistemas compatibles."

L["eft_mag_ak_6l31_545_60.printname"] = "Cargador de 60 cartuchos 5,45x39 mm 6L31 para AK-74"
L["eft_mag_ak_6l31_545_60.compactname"] = "6L31 60"
L["eft_mag_ak_6l31_545_60.description"] = "Un cargador Izhmash 6L31 de polímero con cuatro hileras para 60 cartuchos de 5,45x39 mm, para el fusil AK-74 y sistemas compatibles. Producido en un pequeño lote, nunca fue fabricado en serie."

L["eft_mag_ak_6p2bak_762_40.printname"] = "Cargador de baquelita de 40 cartuchos 7,62x39 mm 6P2 para AK"
L["eft_mag_ak_6p2bak_762_40.compactname"] = "6P2 Baq. 40"
L["eft_mag_ak_6p2bak_762_40.description"] = "Un cargador estándar de baquelita con capacidad para 40 cartuchos de 7,62x39 mm, para la ametralladora ligera RPK y puede utilizarse en armamento AK compatible. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_6p2sb11_762_40.printname"] = "Cargador de 40 cartuchos 7,62x39 mm Molot Arms 6P2.Sb-11 para AK"
L["eft_mag_ak_6p2sb11_762_40.compactname"] = "6P2.Sb-11 40"
L["eft_mag_ak_6p2sb11_762_40.description"] = "Un cargador estándar 6P2 Sb-11 con capacidad para 40 cartuchos de 7,62x39 mm, para las ametralladoras ligeras RPK de 1961 en adelante y puede utilizarse en armamento AK compatible. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_ak103_762_30.printname"] = "Cargador de 30 cartuchos 7,62x39 mm para AK-103"
L["eft_mag_ak_ak103_762_30.compactname"] = "AK-103 30"
L["eft_mag_ak_ak103_762_30.description"] = "Un cargador Izhmash de baquelita con capacidad para 30 cartuchos de 7,62x39 mm, para el fusil AK-103 y sistemas compatibles. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_ak12_545_30_old.printname"] = "Cargador de 30 cartuchos 5,45x39 mm para AK-12 (Primer modelo)"
L["eft_mag_ak_ak12_545_30_old.compactname"] = "AK-12 Prim. 30"
L["eft_mag_ak_ak12_545_30_old.description"] = "Uno de los primeros modelos de cargador para AK-12, hecho de polímero y con ventanillas para mejor conteo de municiones, con capacidad para 30 cartuchos de 5,45x39 mm, para AK-12 y fusiles compatibles. Producido por Izhmash."

L["eft_mag_ak_ak30fde_762_30.printname"] = "Cargador de 30 cartuchos 7,62x39 mm US Palm \"AK30\" para AK (FDE)"
L["eft_mag_ak_ak30fde_762_30.compactname"] = "AK30 FDE 30"
L["eft_mag_ak_ak30fde_762_30.description"] = "Un cargador US Palm AK30 de polímero con capacidad para 30 cartuchos de 7,62x39 mm, para AK y armamento compatible. También puede ser abastecido con munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente. Versión de color Flat Dark Earth."

L["eft_mag_ak_ak30_762_30.printname"] = "Cargador de 30 cartuchos 7,62x39 mm US Palm \"AK30\" para AK (Negro)"
L["eft_mag_ak_ak30_762_30.compactname"] = "AK30 30"
L["eft_mag_ak_ak30_762_30.description"] = "Un cargador US Palm AK30 de polímero con capacidad para 30 cartuchos de 7,62x39 mm, para AK y armas compatibles. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente. Versión de color Negro."

L["eft_mag_ak_akms_762_30.printname"] = "Cargador de aluminio de 30 cartuchos 7,62x39 mm para AKMS"
L["eft_mag_ak_akms_762_30.compactname"] = "AKMS Al. 30"
L["eft_mag_ak_akms_762_30.description"] = "Un cargador de aluminio con capacidad para 30 cartuchos de 7,62x39 mm, para AKMS y armamento compatible, apodado \"aerotransportado\". Fabricado con una aleación ligera de aluminio y reforzado con canaletas adicionales. Es poco común, ya que nunca fue producido en masa. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_alum_762_10.printname"] = "Cargador de aluminio de 10 cartuchos 7,62x39 mm para AK"
L["eft_mag_ak_alum_762_10.compactname"] = "AK Al. 10"
L["eft_mag_ak_alum_762_10.description"] = "Un cargador de aluminio con capacidad para 10 cartuchos de 7,62x39 mm, para AK y armamento compatible. Hecho con una aleación de aluminio ligero y reforzado con canaletas adicionales. Se ha instalado un limitador de capacidad para 10 cartuchos con la finalidad de ser aceptado en el mercado civil. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_molot_762_75.printname"] = "Cargador de tambor de 75 cartuchos 7,62x39 mm Molot Arms para AK"
L["eft_mag_ak_molot_762_75.compactname"] = "Molot 75"
L["eft_mag_ak_molot_762_75.description"] = "Un cargador metálico de tambor con capacidad para 75 cartuchos de 7,62x39 mm, para la ametralladora ligera RPK y sistemas compatibles AK. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_pmag_545_30.printname"] = "Cargador de 30 cartuchos 5,45x39 mm Magpul PMAG 30 GEN M3 para AK-74"
L["eft_mag_ak_pmag_545_30.compactname"] = "GEN M3 30"
L["eft_mag_ak_pmag_545_30.description"] = "Un cargador Magpul PMAG 30 AK74 GEN M3 de polímero con capacidad para 30 cartuchos de 5,45x39 mm para AK y sistemas compatibles."

L["eft_mag_ak_pmag_762_30.printname"] = "Cargador de 30 cartuchos 7,62x39 mm Magpul PMAG 30 GEN M3 para AK"
L["eft_mag_ak_pmag_762_30.compactname"] = "GEN M3 30"
L["eft_mag_ak_pmag_762_30.description"] = "Un cargador Magpul PMAG 30 AK/AKM GEN M3 de polímero con capacidad para 30 cartuchos de 7,62x39 mm, para AK y armamento compatible. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_pmag_banana_762_30.printname"] = "Cargador de 30 cartuchos 7,62x39 mm Magpul PMAG 30 GEN M3 para AK (Banana)"
L["eft_mag_ak_pmag_banana_762_30.compactname"] = "GEN M3 30"
L["eft_mag_ak_pmag_banana_762_30.description"] = "Un cargador Magpul PMAG 30 AK/AKM GEN M3 de polímero con capacidad para 30 cartuchos de 7,62x39 mm, para AK y armamento compatible. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente. Con camuflaje táctico amarillo banana."

L["eft_mag_ak_promag_762_73.printname"] = "Cargador de tambor de 73 cartuchos 7,62x39 mm ProMag AK-A-16 para AK"
L["eft_mag_ak_promag_762_73.compactname"] = "AK-A-16 73"
L["eft_mag_ak_promag_762_73.description"] = "Un cargador ProMag de polímero con capacidad para 73 cartuchos de 7,62x39 mm, para el fusil AKM y sistemas compatibles."

L["eft_mag_ak_ribbed_762_10.printname"] = "Cargador de metal acanalado de 10 cartuchos 7,62x39 mm para AK"
L["eft_mag_ak_ribbed_762_10.compactname"] = "AK Acan. 10"
L["eft_mag_ak_ribbed_762_10.description"] = "Un cargador metálico acanalado que ha sido reducido, con capacidad para 10 cartuchos de 7,62x39 mm para sistemas compatibles con AK. Hecho a partir de un cargador militar estándar que ha sido recortado para cumplir con las leyes de armas de fuego civiles de la Federación Rusa. También acepta munición de calibre .366 TKM para utilizarse en armamento compatible tipo AK del calibre correspondiente."

L["eft_mag_ak_rpk16_545_95.printname"] = "Cargador de tambor de 95 cartuchos 5,45x39 mm para RPK-16"
