L["eft_rec_ar15_m4a1.printname"] = "Cuerpo superior para M4A1 de 5,56x45 mm"
L["eft_rec_ar15_m4a1.compactname"] = "C.S. M4A1"
L["eft_rec_ar15_m4a1.description"] = "Un cuerpo superior para el fusil de asalto M4A1, fabricado por Colt. Cuenta con una montura que permite colocar dispositivos adicionales."

L["eft_rec_ar15_adar.printname"] = "Cuerpo superior ADAR 2-15 para AR-15 de 5,56x45 mm"
L["eft_rec_ar15_adar.compactname"] = "C.S. ADAR"
L["eft_rec_ar15_adar.description"] = "Un cuerpo superior para la carabina ADAR 2-15, producido por ADAR. Cuenta con una montura que permite colocar dispositivos adicionales."

