-- [[ muzzle_ar10 ]]
L["eft_muzzle_ar10_cmmgsv.printname"] = "Frein de bouche CMMG SV Brake 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_cmmgsv.compactname"] = "SV Brake"
L["eft_muzzle_ar10_cmmgsv.description"] = "Le frein de bouche et compensateur CMMG SV Brake est un frein de bouche efficace à simple évent conçu spécifiquement pour le calibre .308 des plateformes AR-10. Le large évent présente une vaste surface sur laquelle les gaz viennent frapper, ce qui garde la bouche du canon et la visée sur la cible."

L["eft_muzzle_ar10_vette.printname"] = "Frein de bouche Nordic Components Corvette 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_vette.compactname"] = "Vette 762"
L["eft_muzzle_ar10_vette.description"] = "The Corvette 7.62x51 Compensator Muzzle Brake from Nordic Components reduces recoil and muzzle flip by directing gases upward and to the side."

L["eft_muzzle_ar10_wave.printname"] = "Frein de bouche Daniel Defense WAVE 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_wave.compactname"] = "WAVE 762"
L["eft_muzzle_ar10_wave.description"] = "Le frein de bouche Daniel Defense Wave est conçu pour monter efficacement le réducteur de son Daniel Defense WAVE sur l'arme hôte, mais fonctionne aussi efficacement sans celui-ci. Il est fabriqué en acier inoxydable aéronautique17-4 PH et a une finition en bain de sels de nitrite pour une corrosion minime."

L["eft_muzzle_ar10_warden.printname"] = "Atténuateur de blast SureFire Warden 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_warden.compactname"] = "Warden 762"
L["eft_muzzle_ar10_warden.description"] = "The SureFire Warden blast regulator features a stainless steel body that enhances the overall appearance while protecting the inner parts from harm. This blast regulator prevents dirt, dust, debris, etc., from causing internal damage. "

L["eft_muzzle_ar10_zk38.printname"] = "Frein de bouche TAA ZK-38 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_zk38.compactname"] = "ZK-38"
L["eft_muzzle_ar10_zk38.description"] = "Le ZK est un frein de bouche oblique à simple chambre très efficace produit par Tactical Advantage Armory. L'avant très pointu est dissuasif en combat rapproché et peut être utilisé sur des cibles souples. Non recommandé pour les situations dans lesquelles un gros choc est nécessaire."

L["eft_muzzle_ar10_30cb.printname"] = "Frein de bouche TAA ZK-38 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_30cb.compactname"] = "ZK-38"
L["eft_muzzle_ar10_30cb.description"] = "Le ZK est un frein de bouche oblique à simple chambre très efficace produit par Tactical Advantage Armory. L'avant très pointu est dissuasif en combat rapproché et peut être utilisé sur des cibles souples. Non recommandé pour les situations dans lesquelles un gros choc est nécessaire."

L["eft_muzzle_ar10_aac51t.printname"] = "Cache-flamme AAC Blackout 51T 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_aac51t.compactname"] = "51T 762"
L["eft_muzzle_ar10_aac51t.description"] = "Le cache-flamme Advanced Armament Corporation (AAC) Blackout 51T 7,62 x 51 mm est un cache-flamme efficace qui offre une plateforme de montage pour le réducteur de son AAC 762 SDN-6. Peut être installé sur les plateformes AR-10."

L["eft_muzzle_ar10_lantac.printname"] = "Frein de bouche/compensateur Lantac Dragon 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_lantac.compactname"] = "DGN762B"
L["eft_muzzle_ar10_lantac.description"] = "Le frein de bouche Dragon par Lantac est conçu pour être installé sur les armes chambrées en 7,62 x 51 mm. Il réduit le recul ainsi que le relèvement du canon."

L["eft_muzzle_ar10_qdc_brake.printname"] = "Frein de bouche KAC QDC 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_qdc_brake.compactname"] = "QDC 762"
L["eft_muzzle_ar10_qdc_brake.description"] = "Le kit de frein de bouche Knight's Armament Company est un cache-flamme efficace offrant aussi un support de montage pour le réducteur de son PRS QDC. Il peut être installé sur les armes de la plateforme AR-10."

L["eft_muzzle_ar10_m11.printname"] = "Frein de bouche Precision Armanent M11 Severe-Duty 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_m11.compactname"] = "M11"
L["eft_muzzle_ar10_m11.description"] = "The M11 muzzle brake designed for installation on AR-10-type systems and compatibles. Manufactured by Precision Armament."

L["eft_muzzle_ar10_x3.printname"] = "Compensateur 2A Armanent X3 7,62 x 51 pour AR-10"
L["eft_muzzle_ar10_x3.compactname"] = "X3 762"
L["eft_muzzle_ar10_x3.description"] = "The X3 compensator designed for installation on AR-10-type systems and compatibles. Manufactured by 2A Armament."

L["eft_muzzle_ar10_mdr.printname"] = "Cache-flamme Desert Tech 7,62 x 51 mm"
L["eft_muzzle_ar10_mdr.compactname"] = "MDR 762"
L["eft_muzzle_ar10_mdr.description"] = "A flash hider developed by Desert Tech specifically for the MDR 7.62x51 (.308) assault rifles."

L["eft_muzzle_ar10_procomp.printname"] = "Frein de bouche SureFire ProComp 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_procomp.compactname"] = "ProComp 762"
L["eft_muzzle_ar10_procomp.description"] = "The ProComp muzzle brake designed for installation on AR-10-type systems and compatibles. The muzzle brake greatly reduces both recoil impulse and muzzle rise so that the user's weapon tracks straight back to keep them on target for faster shot-to-shot recovery. Manufactured by SureFire."

L["eft_muzzle_ar10_red.printname"] = "Frein de bouche Fortis RED Brake 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_red.compactname"] = "RED 762"
L["eft_muzzle_ar10_red.description"] = "Le frein de bouche RED Brake est un frein de bouche pour les armes basées sur le système AR-10. Produit par Fortis."

L["eft_muzzle_ar10_atlas.printname"] = "Frein de bouche Odin Works ATLAS-7 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_atlas.compactname"] = "ATLAS 762"
L["eft_muzzle_ar10_atlas.description"] = "The ATLAS-7 muzzle brake designed for installation on AR-10-type systems and compatibles. Manufactured by Odin Works."

L["eft_muzzle_ar10_scarsd.printname"] = "Cache-flamme AAC SCAR-SD 51T 7,62 x 51 mm"
L["eft_muzzle_ar10_scarsd.compactname"] = "SCAR-SD 762"
L["eft_muzzle_ar10_scarsd.description"] = "The Advanced Armament Corporation (AAC) SCAR-SD 51T flash hider is an effective flash suppressor that also serves as an attachment platform for the AAC 762-SDN-6 sound suppressor. Can be installed on AR-10-based rifles."

L["eft_muzzle_ar10_shrewed.printname"] = "Frein de bouche Keeno Arms SHREWD 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_shrewed.compactname"] = "SHREWD 762"
L["eft_muzzle_ar10_shrewed.description"] = "Le Frein de bouche SHREWD conçu pour une installation sur les plateformes AR-10 et systèmes compatibles. Produit par Keeno Arms."

L["eft_silencer_ar10_prsqdc.printname"] = "Réducteur de son KAC PRS/QDC 7,62 x 51 mm"
L["eft_silencer_ar10_prsqdc.compactname"] = "PRS QDC"
L["eft_silencer_ar10_prsqdc.description"] = "Knight's Armament Company PRS QDC sound suppressor, which can be installed on compatible QDC Flash Suppressor Kit and QDC Muzzle Brake Kit muzzle devices."

L["eft_muzzle_ar10_bmda3.printname"] = "Adaptateur de filetage pour Lantac Blast Mitigation Device A3"
L["eft_muzzle_ar10_bmda3.compactname"] = "BMD A3"
L["eft_muzzle_ar10_bmda3.description"] = "The A3 adapter by Lantac designed for installation of the BMD Blast Mitigation Device."

L["eft_muzzle_bmd_bmd762.printname"] = "Atténuateur de blast Lantac BMD 7,62 x 51 mm"
L["eft_muzzle_bmd_bmd762.compactname"] = "BMD 762"
L["eft_muzzle_bmd_bmd762.description"] = "The BMD Blast Mitigation Device by Lantac is a unique blast shield that allows the muzzle brake contained inside it to continue to function as normal with regard to muzzle climb mitigation. Installed on 7.62x51 caliber weapons."

L["eft_muzzle_deltatek_dtk_ar10.printname"] = "Frein de bouche Delta-Tek DTK-AR10 7,62 x 51 mm pour AR-10"
L["eft_muzzle_deltatek_dtk_ar10.compactname"] = "DTK-AR10"
L["eft_muzzle_deltatek_dtk_ar10.description"] = "Le frein de bouche DTK-AR10, conçu pour la plateforme AR-10 et autres systèmes compatibles, réduit fortement le recul et le relèvement du canon grâce à ses chambres de compensation spécifiques. Produit par Delta-Tek."

-- [[ muzzle_shared ]]
L["eft_muzzle_psr_protector.printname"] = "Bague de protection pour frein de bouche AWC PSR"
L["eft_muzzle_psr_protector.compactname"] = "PSR prot."
L["eft_muzzle_psr_protector.description"] = "Une bague de protection du filetage pour les freins de bouches PSR. Produite par AWC Silencers."

L["eft_muzzle_ar15_onemount.printname"] = "Adaptateur de filetage pour Gemtech ONE"
L["eft_muzzle_ar15_onemount.compactname"] = "Adapt. ONE"
L["eft_muzzle_ar15_onemount.description"] = "The Direct Thread Mount adapter for installation of the Gemtech ONE multi-caliber sound suppressors directly onto the barrel threading."

L["eft_silencer_gemtechone.printname"] = "Réducteur de son Gemtech ONE multi-calibre"
L["eft_silencer_gemtechone.compactname"] = "ONE"
L["eft_silencer_gemtechone.description"] = "Le réducteur de son Gemtech ONE est conçu pour être utilisé avec le calibre 7,62 x 51 mm OTAN, mais il est également compatible avec plusieurs autres calibres. Il requiert un adaptateur de filetage pour être monté."

L["eft_silencer_waveqd.printname"] = "Réducteur de son Daniel Defense Wave QD"
L["eft_silencer_waveqd.compactname"] = "WAVE QD"
L["eft_silencer_waveqd.description"] = "The Daniel Defense WAVE QD (Quick Detach) sound suppressor which can be installed on the compatible WAVE muzzle brakes."

L["eft_silencer_ultra5.printname"] = "Réducteur de son Thunder Beast Ultra 5"
L["eft_silencer_ultra5.compactname"] = "Ultra 5"
L["eft_silencer_ultra5.description"] = "Le réducteur de son Ultra 5 est conçu pour être utilisé avec le calibre 7,62 x 51 mm OTAN, mais il est également un superbe réducteur de son multi-calibres offrant d'excellentes performances pour les calibres .300 AAC, 6,8 SPC, 6,5 mm et 5,56 mm. Il peut être uniquement monté sur les freins de bouche Thunder Beast compatibles."

L["eft_silencer_sdn6.printname"] = "Réducteur de son AAC 762-SDN-6 multi-calibres"
L["eft_silencer_sdn6.compactname"] = "SDN-6"
L["eft_silencer_sdn6.description"] = "Le réducteur de son AAC 762 SDN-6 est conçu pour être utilisé avec le calibre 7,62 x 51 mm OTAN, mais il est également un superbe réducteur de son multi-calibres offrant d'excellentes performances pour les calibres .300 AAC, 6,8 SPC, 6,5 mm et 5,56 mm. Il peut être uniquement monté sur les cache-flammes \"51T\" compatibles."

L["eft_muzzle_ar10_keymount.printname"] = "Frein de bouche Dead Air Keymount 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_keymount.compactname"] = "Keymount"
L["eft_muzzle_ar10_keymount.description"] = "Le frein de bouche par Dead Air Silencers conçu pour être installé sur les plateformes AR-10 et armes compatibles. Il réduit grandement aussi bien le recul que le relèvement de l'arme afin qu'elle reste en cible et permet à l'utilisateur d'enchaîner plus rapidement les tirs."

L["eft_muzzle_dthybrid.printname"] = "Adaptateur de filetage pour SilencerCo Hybrid 46"
L["eft_muzzle_dthybrid.compactname"] = "Adapt. DT"
L["eft_muzzle_dthybrid.description"] = "Adaptateur de filetage pour l'installation du réducteur de son SilencerCo Hybrid 46 directement sur le filetage du canon."

L["eft_muzzle_pws_cqb.printname"] = "Frein de bouche PWS CQB 5,56 x 45 mm pour AR-15"
L["eft_muzzle_pws_cqb.compactname"] = "PWS CQB"
L["eft_muzzle_pws_cqb.description"] = "Le frein de bouche PWS CQB 5,56 x 45 mm est spécifiquement conçu pour les armes compactes et une utilisation en espace confiné."

L["eft_silencer_dthybrid.printname"] = "Réducteur de son SilencerCo Hybrid 46 multi-calibre"
L["eft_silencer_dthybrid.compactname"] = "Hybrid 46"
L["eft_silencer_dthybrid.description"] = "Le réducteur de son SilencerCo Hybrid 46 multi-calibre est conçu pour être utilisé avec des pistolets allant du 9 mm au .45 ACP et des fusils allant du 5.56 mm au .45. Adaptateur dédié requis pour l'installation sur l'arme."

L["eft_silencer_r43_556.printname"] = "Réducteur de son Rotor 43 5,56 x 45 mm"
L["eft_silencer_r43_556.compactname"] = "R43 556"
L["eft_silencer_r43_556.description"] = "Le réducteur de son Rotor 43 s'installe sur les systèmes AR ou AK en 5,56x45. Bien qu'ayant le rôle d'un frein de bouche, il fonctionne aussi comme un réducteur de son."

L["eft_muzzle_ar10_sig_taperlok.printname"] = "Adaptateur de filetage SIG Sauer Taper-LOK 7,62 x 51 mm/.300 AAC BLK"
L["eft_muzzle_ar10_sig_taperlok.compactname"] = "T-LOK"
L["eft_muzzle_ar10_sig_taperlok.description"] = "Le système de montage breveté \"Taper-LOK\" permet l'installation d'une large gammes de freins de bouche sur les armes chambrées en 7,62 x 51 mm et .300 Blackout. Produit par SIG Sauer."

L["eft_muzzle_tlok_3prong.printname"] = "Cache-flamme SIG Sauer 3-prong 7,62 x 51 mm"
L["eft_muzzle_tlok_3prong.compactname"] = "3-pr 762"
L["eft_muzzle_tlok_3prong.description"] = "Le cache-flamme SIG Sauer 3-prong s'installe sur le système de montage breveté Taper-LOK."

L["eft_muzzle_tlok_mirco.printname"] = "Frein de bouche SIG Sauer Micro Brake 7,62 x 51 mm"
L["eft_muzzle_tlok_mirco.compactname"] = "Micro 762"
L["eft_muzzle_tlok_mirco.description"] = "The SIG Sauer Micro Brake muzzle brake that can be installed on the patented Taper-LOK mounting system."

L["eft_muzzle_tlok_tpb.printname"] = "Frein de bouche SIG Sauer Two Port Brake 7,62 x 51 mm"
L["eft_muzzle_tlok_tpb.compactname"] = "TPB 762"
L["eft_muzzle_tlok_tpb.description"] = "Le frein de bouche SIG Sauer Brake Two Port peut être installé sur le système de montage breveté Taper-LOK."

L["eft_silencer_ar10_tlok_srd.printname"] = "Réducteur de son SIG Sauer SRD762-QD 7,62 x 51 mm"
L["eft_silencer_ar10_tlok_srd.compactname"] = "SRD762-QD"
L["eft_silencer_ar10_tlok_srd.description"] = "SRD762-QD is a Grade 5 titanium suppressor that uses quick-release muzzle devices to attach the suppressor to the gun. Manufactured by SIG Sauer."

L["eft_muzzle_ar10_qdc_sup.printname"] = "Cache-flamme KAC QDC 7,62 x 51 mm pour AR-10"
L["eft_muzzle_ar10_qdc_sup.compactname"] = "KAC QDC"
L["eft_muzzle_ar10_qdc_sup.description"] = "Le cache-flamme Knight's Armament Company est un cache-flamme efficace servant également de plateforme pour le montage du réducteur de son PRS QDC. Il peut être installé sur les armes de la plateforme AR-10."

L["eft_silencer_ar10_srdti.printname"] = "Réducteur de son SIG Sauer SRD762Ti 7,62 x 51 mm"
L["eft_silencer_ar10_srdti.compactname"] = "SRD762"
L["eft_silencer_ar10_srdti.description"] = "Le SRD762Ti est un réducteur de son en titane de grade 5 conçu pour les calibres .308/7,62/.300 AAC BLK. Produit par SIG Sauer."

L["eft_muzzle_deltatek_dtk_ar15.printname"] = "Frein de bouche Delta-Tek DTK-M16 5,56 x 45 mm pour AR-15"
L["eft_muzzle_deltatek_dtk_ar15.compactname"] = "DTK-M16"
L["eft_muzzle_deltatek_dtk_ar15.description"] = "Le frein de bouche DTK-M16, conçu pour la plateforme AR-15 et autres systèmes compatibles, réduit fortement le recul et le relèvement du canon grâce à ses chambres de compensation spécifiques. Produit par Delta-Tek."

L["eft_muzzle_carlson_ar15.printname"] = "Compensateur DoubleStar Carlson Tac Comp 5,56 x 45 mm pour AR-15"
L["eft_muzzle_carlson_ar15.compactname"] = "CC450 556"
L["eft_muzzle_carlson_ar15.description"] = "The Carlson Tac Comp compensator designed for AR-15 and compatible systems. Reduces recoil impulse and muzzle rise. Ideal for short barrel weapons. Manufactured by DoubleStar."

-- [[ optic ]]
L["eft_optic_1p78_eyecap.printname"] = "Bonnette pour lunette NPZ 1P78"
L["eft_optic_1p78_eyecap.compactname"] = "Bonn. 1P78"
L["eft_optic_1p78_eyecap.description"] = "Une bonnette en caoutchouc pour la lunette de visée NPZ 1P78."

L["eft_optic_553.printname"] = "Viseur holographique EOTech  553"
L["eft_optic_553.compactname"] = "EOT 553"
L["eft_optic_553.description"] = "Le viseur holographique EOTech 553. Grâce à son gabarit, il peut être utilisé sur un large éventail d’armes même avec une visée mécanique non-escamotable. Compatible avec les lunettes de vision nocturne par le biais du mode réticule VN. Viseur de prédilection de l'US SOCOM pour les opérations en milieu confiné."

L["eft_optic_acrop1.printname"] = "Viseur reflex Aimpoint ACRO P-1"
L["eft_optic_acrop1.compactname"] = "ACRO P-1"
L["eft_optic_acrop1.description"] = "Le viseur reflex compact ACRO P-1 par Aimpoint a été conçu pour l'utilisation avec une embase dédiée. Léger, compact et fiable."

L["eft_optic_compm4.printname"] = "Viseur reflex Aimpoint COMP M4"
L["eft_optic_compm4.compactname"] = "COMP M4"
L["eft_optic_compm4.description"] = "La série de viseurs Aimpoint COMP M4 sont les viseurs les plus robustes qu'Aimpoint n'est jamais conçu et sont mêmes les viseurs de dotation réglementaire des soldats d'infanterie de beaucoup de pays de l'OTAN. Ces optiques sont très solides et peuvent fonctionner continuellement jusqu'à 8 ans avec une simple pile AA. L'Aimpoint CompM4 est la toute dernière version du M68CCO (optique de combat rapproché) de l'armée U.S, transmettant l'héritage qu'Aimpoint a acquis depuis 1997."

L["eft_optic_deltapoint.printname"] = "Viseur reflex Leupold DeltaPoint"
L["eft_optic_deltapoint.compactname"] = "DP"
L["eft_optic_deltapoint.description"] = "Le viseur reflex compact DeltaPoint par Leupold ne connait pas de limite dans ses possibilités d'application. Conçu à l'origine pour être utilisé sur des fusils à pompes en compétition, il fût reconnu pour ses bonnes performances sur un large éventail d'armes. Des fusils aux pistolets en passant par les fusils d'assaut, comme viseur principal ou secondaire, il montrera d'excellents résultats."

L["eft_optic_exps3_tan.printname"] = "Viseur holographique EOTech EXPS3"
L["eft_optic_exps3_tan.compactname"] = "EXPS3"
L["eft_optic_exps3_tan.description"] = "Viseur holographique EOTech EXPS3. Grâce à son gabarit, il peut être utilisé avec un large éventail d’armes même avec une visée mécanique non-rabattable. Compatible avec les lunettes de vision nocturne par le biais du mode réticule NV. Viseur de prédilection du SOCOM pour les opérations en milieu confiné."

L["eft_optic_ff3.printname"] = "Viseur reflex Burris FastFire 3"
L["eft_optic_ff3.compactname"] = "FF3"
L["eft_optic_ff3.description"] = "Légère et robuste, la série FastFire porte un point rouge lumineux sur votre cible pour vous donner un avantage immédiat de rapidité et de précision. Peut être utilisée comme viseur principal ou comme optique secondaire au sommet d'un viseur déjà en place."

L["eft_optic_h2.printname"] = "Viseur reflex Aimpoint Micro H-2"
L["eft_optic_h2.compactname"] = "H-2"
L["eft_optic_h2.description"] = "Le viseur point rouge compact Micro H-2 par Aimpoint a été conçu pour être utilisé avec n'importe quelle arme à feu, et même avec les arcs. Léger, compact et fiable."

L["eft_optic_hhs1.printname"] = "Viseur holographique hybride EOTech HHS-1"
L["eft_optic_hhs1.compactname"] = "HHS-1"
L["eft_optic_hhs1.description"] = "Le viseur holographique hybride HHS-1 (Holographic Hybrid Sight 1) comprend un viseur holographique EXPS3-4 et un grossisseur G33.STS. Produit par EOTech."

L["eft_optic_hhs1_tan.printname"] = "Viseur holographique hybride EOTech HHS-1 (Tan)"
L["eft_optic_hhs1_tan.compactname"] = "HHS-1 Tan"
L["eft_optic_hhs1_tan.description"] = "Le viseur holographique hybride HHS-1 (Holographic Hybrid Sight 1) comprend un viseur holographique EXPS3-4 et un grossisseur G33.STS. Produit par EOTech. Modèle couleur Tan."

L["eft_optic_hs401g5.printname"] = "Viseur reflex Holosun HS401G5"
L["eft_optic_hs401g5.compactname"] = "HS401G5"
L["eft_optic_hs401g5.description"] = "The HS401G5 reflex sight whith a built-in laser designator, manufactured by Holosun."

L["eft_optic_kmz_eyecap.printname"] = "Bonnette pour lunette KMZ 1P59"
L["eft_optic_kmz_eyecap.compactname"] = "Bon. 1P59"
L["eft_optic_kmz_eyecap.description"] = "Une bonnette en caoutchouc pour la lunette de visée KMZ 1P59."

L["eft_optic_kobra.printname"] = "Viseur reflex Axion Kobra EKP-8-18"
L["eft_optic_kobra.compactname"] = "EKP-8-18"
L["eft_optic_kobra.description"] = "Le viseur reflex Kobra est très populaire parmi les agences de sécurité et les tireurs civils. Il a été conçu pour les forces armées de la Fédération Russe, mais n'a jamais été officiellement adopté. Conçu par Axion."

L["eft_optic_krechet.printname"] = "Viseur reflex Valday Krechet"
L["eft_optic_krechet.compactname"] = "Krechet"
L["eft_optic_krechet.description"] = "The Krechet reflex sight, designed for precision shooting at day and at night when used in combination with a night vision device."

L["eft_optic_mrs.printname"] = "Viseur reflex Walther MRS"
L["eft_optic_mrs.compactname"] = "MRS"
L["eft_optic_mrs.description"] = "Le viseur reflex Walther multi-réticule (MRS) dispose de 2 points et 2 réticules avec des niveaux de luminosité réglables. Bon marché, précis (mais doit être réglé chaque fois que le type de réticule est modifié), compact et offrant une bonne autonomie, mais peu solide et fiable."

L["eft_optic_nspu_eyecap.printname"] = "Bonnette pour lunette NSPU-M"
L["eft_optic_nspu_eyecap.compactname"] = "Bon. NSPU-M"
L["eft_optic_nspu_eyecap.description"] = "Une bonnette en caoutchouc pour la lunette de vision nocturne NSPU-M."

L["eft_optic_okp7.printname"] = "Viseur reflex OKP-7 (Picatinny)"
L["eft_optic_okp7.compactname"] = "OKP-7"
L["eft_optic_okp7.description"] = "Le design unique des viseurs OKP permet de tirer depuis des positions instables, dans des conditions changeant rapidement, en réduisant la fatigue de l'opérateur et, si nécessaire, permet une utilisation sans entrave des mires métalliques de base. La caractéristique clé de ce viseur est un réflecteur sans bordures qui ne bloque pas le champ de vision du tireur. Au lieu de cela, le réflecteur est protégé par un écran mince et résistant quasiment imperceptible en tirant. Cette variante est installée sur un rail Picatinny ou Weaver."

L["eft_optic_p1x42.printname"] = "Viseur reflex VOMZ Pilad P1x42 Weaver"
L["eft_optic_p1x42.compactname"] = "P1x42"
L["eft_optic_p1x42.description"] = "Un viseur reflex ouvert, conçu pour la visée de précision des armes de sport et de chasse sur différents types de cibles, y compris des cibles mouvantes rapides. Produit par VOMZ."

L["eft_optic_pk06.printname"] = "Viseur reflex BelOMO PK-06"
L["eft_optic_pk06.compactname"] = "PK-06"
L["eft_optic_pk06.description"] = "Un viseur reflex ouvert moderne équipé d'un ajustement automatique de la luminosité du réticule et de 3 types de réticule. Produit par Zenit-BelOMO."

L["eft_optic_pk120.printname"] = "Viseur holographique Valday PK-120 (1P87)"
L["eft_optic_pk120.compactname"] = "PK-120"
L["eft_optic_pk120.description"] = "PK-120 (GRAU index - 1P87) is a holographic sight produced by Valday. It is part of the DPSK complex (day-night sight-observation complex) of the experimental design work \"Ratnik\"."

L["eft_optic_pro.printname"] = "Viseur reflex Aimpoint PRO"
L["eft_optic_pro.compactname"] = "PRO"
L["eft_optic_pro.description"] = "Le viseur reflex Aimpoint PRO (Patrol Rifle Optic) est installé sur les embases QRP2 ou LRP qui se fixent sur les rails Picatinny sans outils supplémentaires. Le viseur possède un couvre oculaire et objectif résistants et transparent permettant à l'utilisateur de tirer sans les ouvrir et avec les deux yeux ouverts en situation d'urgence. Le viseur PRO est totalement compatible avec toutes les générations de module de vision nocturnes."

L["eft_optic_pso_eyecap.printname"] = "Bonnette pour lunette PSO"
L["eft_optic_pso_eyecap.compactname"] = "Bonn. PSO"
L["eft_optic_pso_eyecap.description"] = "Une bonnette en caoutchouc pour la famille des lunettes de visée PSO."

L["eft_optic_reapir_eyecup.printname"] = "Bonnette pour lunette Trijicon REAP-IR"
L["eft_optic_reapir_eyecup.compactname"] = "REAP-IR"
L["eft_optic_reapir_eyecup.description"] = "Une bonnette en caoutchouc pour la lunette de visée thermique Trijicon REAP-IR."

L["eft_optic_rmr.printname"] = "Viseur reflex Trijicon RMR"
L["eft_optic_rmr.compactname"] = "RMR"
L["eft_optic_rmr.description"] = "RMR (Ruggedized Miniature Reflex) is a durable and lightweight compact reflex sight. Can be used as a main or backup sight. Requires a specific mount for installation on Weaver rails. Manufactured by Trijicon."

L["eft_optic_romeo4.printname"] = "Viseur reflex SIG Sauer ROMEO4"
L["eft_optic_romeo4.compactname"] = "ROMEO4"
L["eft_optic_romeo4.description"] = "SIG Sauer ROMEO4 is a reflex sight for fast and accurate target acquisition."

L["eft_optic_romeo8t.printname"] = "Viseur reflex SIG Sauer ROMEO8T"
L["eft_optic_romeo8t.compactname"] = "ROMEO8T"
L["eft_optic_romeo8t.description"] = "The ROMEO8T reflex sight, designed for precision shooting for modern sporting rifles and shotguns. Manufactured by SIG Sauer."

L["eft_optic_srs02.printname"] = "Viseur reflex Trijicon SRS-02"
L["eft_optic_srs02.compactname"] = "SRS-02"
L["eft_optic_srs02.description"] = "Le viseur reflex Trijicon SRS (Sealed Reflex Sight) est compact et prend peu d'espace sur les rails tandis qu'il conserve toutes ses performances et sa durabilité. Sa large ouverture de 38 mm autorise un large champ de vision et une acquisition rapide de la cible. Le viseur reflex Trijicon SRS possède un réticule de 1,75 minute d'angle. Il est alimenté par une cellule photovoltaïque et une simple pile AA."

L["eft_optic_t1.printname"] = "Viseur reflex Aimpoint Micro T-1"
L["eft_optic_t1.compactname"] = "T-1"
L["eft_optic_t1.description"] = "Le viseur reflex compact Micro T-1 par Aimpoint a été conçu pour être utilisé avec n'importe quelle type d'arme à feu et même avec les arcs. Léger, compact et robuste."

L["eft_optic_t12w.printname"] = "Viseur reflex thermique Torrey Pines Logic T12W 30 Hz"
L["eft_optic_t12w.compactname"] = "T12W"
L["eft_optic_t12w.description"] = "Un viseur reflex thermique compact à faible fréquence. Produit par Torrey Pines Logic."

L["eft_optic_tulpan_eyecap.printname"] = "Bonnette pour lunette NPZ USP-1"
L["eft_optic_tulpan_eyecap.compactname"] = "Bon. USP-1"
L["eft_optic_tulpan_eyecap.description"] = "Une bonnette en caoutchouc pour la lunette de visée NPZ USP-1."

L["eft_optic_uh1.printname"] = "Viseur holographique Vortex Razor AMG UH-1"
L["eft_optic_uh1.compactname"] = "UH-1"
L["eft_optic_uh1.description"] = "Le viseur holographique Vortex Razor AMG UH-1 ne se repose pas que sur son design futuriste. Selon ses créateurs, c'est aussi une révolution parmi les viseurs holographiques et reflex."

L["eft_optic_utg.printname"] = "Viseurs reflex Leapers UTG"
L["eft_optic_utg.compactname"] = "UTG"
L["eft_optic_utg.description"] = "Le viseur reflex UTG avec un point circulaire rouge/vert, produit par Leapers Inc. Ce modèle est compact et se monte sur les rails Weaver/Picatinny."

L["eft_optic_vulcan_eyecup.printname"] = "Bonnette pour lunette Armasight Vulcan MG"
L["eft_optic_vulcan_eyecup.compactname"] = "Vulcan MG"
L["eft_optic_vulcan_eyecup.description"] = "Une bonnette en caoutchouc pour la lunette de visée Vulcan MG, roduite par Armasight."

L["eft_optic_xps3_0.printname"] = "Viseur holographique EOTech XPS3-0"
L["eft_optic_xps3_0.compactname"] = "XPS3-0"
L["eft_optic_xps3_0.description"] = "Le XPS3 est plus compact que les autres viseurs holographiques, mais tout aussi efficace. De plus, une telle approche laisse plus d'espace pour des équipements supplémentaires. Les chasseurs et les forces armées l'apprécient pour sa petite taille et son poids, qui sont particulièrement remarqués au cours des longues missions. Le modèle 3-0 dispose d'un réticule à 1 point de 1 MOA."

L["eft_optic_xps3_2.printname"] = "Viseur holographique EOTech XPS3-2"
L["eft_optic_xps3_2.compactname"] = "XPS3-2"
L["eft_optic_xps3_2.description"] = "Le XPS3 est plus compact que les autres viseurs holographiques, mais tout aussi efficace, de plus, une telle approche laisse plus d'espace pour des équipements supplémentaires. Les chasseurs et les forces armées l'apprécient pour sa petite taille et son poids, qui sont particulièrement remarqués au cours des longues missions. Le modèle 3-2 dispose d'un réticule à 2 points de 1 MOA."

L["eft_aux_ekp_shade.printname"] = "Pare-soleil pour Axion Kobra"
L["eft_aux_ekp_shade.compactname"] = "Kobra"
L["eft_aux_ekp_shade.description"] = "Un pare-soleil pour la famille des viseurs reflex Kobra. Protège la lentille contre les impacts mécaniques et supprime les reflets. Produit par Axion."

L["eft_optic_targetring.printname"] = "Viseur reflex VOMZ Pilad TargetRing"
L["eft_optic_targetring.compactname"] = "TargetRing"
L["eft_optic_targetring.description"] = "Designed for hunting birds or small game, for sport shooting at stationary or fast-moving targets. Ideal for this purpose, it creates a contrasting circle on the target, giving the shooter an opportunity to make the pre-direction and not to lose speed.\nIt is mounted only on smoothbore (shotgun) weapons."

L["eft_optic_elcan_hco.printname"] = "Viseur holographique ELCAN Specter HCO"
L["eft_optic_elcan_hco.compactname"] = "HCO"
L["eft_optic_elcan_hco.description"] = "Le viseur Specter Holographic Combat Optic (HCO). Produit par ELCAN."

L["eft_optic_sig_romeo7.printname"] = "Viseur reflex SIG Sauer ROMEO7 1 x 30 mm (prototype)"
L["eft_optic_sig_romeo7.compactname"] = "ROMEO7"
L["eft_optic_sig_romeo7.description"] = "Un prototype initial du viseur reflex ROMEO7 1 x 30. Produit par SIG Sauer."

L["eft_optic_zeus_eyecup.printname"] = "Bonnette pour lunette Armasight Zeus-Pro"
L["eft_optic_zeus_eyecup.compactname"] = "ZeusPro"
L["eft_optic_zeus_eyecup.description"] = "Une bonnette en caoutchouc pour la lunette de visée Zeus Pro, produite par Armasight."

L["eft_optic_boss.printname"] = "Viseur reflex Wilcox BOSS Xe"
L["eft_optic_boss.compactname"] = "BOSS Xe"
L["eft_optic_boss.description"] = "Le viseur reflex BOSS (Ballistically Optimized Sighting System) combine les fonctions d'un point rouge, d'un module de visée laser et d'une mire. Produit par Wilcox."

L["eft_optic_shakhin_eyecup.printname"] = "Cyclone Shakhin scope eyecup"
L["eft_optic_shakhin_eyecup.compactname"] = "Shakhin"
L["eft_optic_shakhin_eyecup.description"] = "A rubber eyecup for the Shakhin thermal scope. Manufactured by Central Research Institute Cyclone."

