L["eft_grip_ak_pp19.compactname"] = "PP-19-01"
L["eft_grip_ak_pp19.description"] = "Une poignée pistolet produite par Izhmash pour les pistolets-mitrailleurs PP-19-01 Vityaz et Saiga-9."

