L["eft_mag_ak_6l31_545_60.printname"] = "Chargeur 6L31 5,45 x 39 mm 60 coups pour AK-74"
L["eft_mag_ak_6l31_545_60.compactname"] = "6L31 60"
L["eft_mag_ak_6l31_545_60.description"] = "Un chargeur polymère Izhmash 6L31 5,45 x 39 mm 60 coups à quatre piles imbriquées pour AK-74 et les systèmes compatibles. Produit en petit lot, jamais numéroté."

L["eft_mag_ak_6p2bak_762_40.printname"] = "Chargeur bakélite 7,62 x 39 mm 40 coups pour AK"
L["eft_mag_ak_6p2bak_762_40.compactname"] = "6P2 bak. 40"
L["eft_mag_ak_6p2bak_762_40.description"] = "Un chargeur en bakélite standard de 40 coups pour RPK. Il est aussi compatible avec les armes AK en calibre 7,62 x 39 mm. Peut aussi être chargé avec des cartouches de .366 TKM pour les armes du même calibres."

L["eft_mag_ak_6p2sb11_762_40.printname"] = "Chargeur Molot Arms 7,62 x 39 mm 40 coups pour AK (6P2.Sb-11)"
L["eft_mag_ak_6p2sb11_762_40.compactname"] = "6P2 Sb-11 40"
L["eft_mag_ak_6p2sb11_762_40.description"] = "Un chargeur métallique 6P2 Sb-11 7,62 x 39 mm d'une capacité de 40 coups d'origine pour le fusil-mitrailleur RPK mis en service à partir de 1961 jusqu'à aujourd'hui. Il est également compatible avec toutes les plateformes AK du même calibre. Peut aussi être chargé avec des cartouches de .366 TKM pour les armes du même calibre."

L["eft_mag_ak_ak103_762_30.printname"] = "Chargeur 7,62 x 39 mm 30 coups pour AK-103"
L["eft_mag_ak_ak103_762_30.compactname"] = "AK-103 30"
L["eft_mag_ak_ak103_762_30.description"] = "A 30-round bakelite Izhmash magazine for 7.62x39 ammo, for AK-103 and compatible systems. Can also be supplied with .366 TKM ammo for use with the corresponding caliber AK-compatible weapons."

L["eft_mag_ak_ak12_545_30_old.printname"] = "Chargeur Gen1 5,45 x 39 mm 30 coups pour AK-12"
L["eft_mag_ak_ak12_545_30_old.compactname"] = "AK-12 Gen1 30"
L["eft_mag_ak_ak12_545_30_old.description"] = "Un chargeur polymère 30 coups de première génération doté d'une fenêtre pour le contrôle des munitions, pour cartouches de 5,45 x 39 mm et armes compatibles AK-12. Produit par Izhmash."

L["eft_mag_ak_ak30fde_762_30.printname"] = "Chargeur US Palm \"AK30\" 7,62 x 39 mm 30 coups pour AK (FDE)"
L["eft_mag_ak_ak30fde_762_30.compactname"] = "AK30 FDE 30"
L["eft_mag_ak_ak30fde_762_30.description"] = "Un chargeur polymère US Palm AK30 7,62 x 39 mm d'une capacité de 30 coups pour AK et armes compatibles. Peut aussi être chargé avec des cartouches de .366 TKM pour les armes du même calibre. Modèle Flat Dark Earth."

L["eft_mag_ak_ak30_762_30.printname"] = "Chargeur US Palm \"AK30\" 7,62 x 39 mm 30 coups pour AK (noir)"
L["eft_mag_ak_ak30_762_30.compactname"] = "AK30 30"
L["eft_mag_ak_ak30_762_30.description"] = "Un chargeur polymère US Palm AK30 7,62 x 39 mm d'une capacité de 30 coups pour AK et armes compatibles. Peut aussi être chargé avec des cartouches de .366 TKM pour les armes du même calibre. Modèle noir."

L["eft_mag_ak_akms_762_30.printname"] = "Chargeur aluminium 7,62 x 39 mm 30 coups pour AKMS"
L["eft_mag_ak_akms_762_30.compactname"] = "AKMS al. 30"
L["eft_mag_ak_akms_762_30.description"] = "A 30-round aluminum magazine for 7.62x39 AKMS and compatibles, nicknamed “Airborne”. Made of light aluminum alloy and reinforced by additional ribs. It's quite a rarity, as it was never in mass production. Can also be supplied with .366 TKM ammo for use with the corresponding caliber AK-compatible weapons."

L["eft_mag_ak_alum_762_10.printname"] = "Chargeur 7,62 x 39 mm 10 coups pour AK"
L["eft_mag_ak_alum_762_10.compactname"] = "AK alu 10"
L["eft_mag_ak_alum_762_10.description"] = "Un chargeur aluminium de 10 coups pour les AK en 7,62 x 39 mm et armes compatibles, fait d'un alliage d'aluminium léger et renforcé par des nervures. Ce chargeur est modifié en limitant sa capacité à 10 cartouches pour le marché civil. Peut aussi être chargé avec des cartouches de .366 TKM pour les armes du même calibre."

L["eft_mag_ak_molot_762_75.printname"] = "Chargeur tambour Molot Arms 7,62 x 39 mm 75 coups pour AK"
L["eft_mag_ak_molot_762_75.compactname"] = "Molot 75"
L["eft_mag_ak_molot_762_75.description"] = "Un chargeur tambour en métal de 75 coups pour le fusil-mitrailleur RPK. Il est aussi compatible avec certains systèmes AK de calibre 7,62 x 39 mm. Peut aussi être chargé avec des cartouches de .366 TKM pour les armes du même calibres."

L["eft_mag_ak_pmag_545_30.printname"] = "Chargeur Pmag 30 AK74 GEN M3 5,45 x 39 mm 30 coups pour AK"
L["eft_mag_ak_pmag_545_30.compactname"] = "GEN M3 30"
L["eft_mag_ak_pmag_545_30.description"] = "A 30-round polymer Magpul Pmag 30 AK74 GEN M3 magazine for 5.45x39 AK and compatible weapons."

