L["eft_muzzle_ak_antidote.printname"] = "Frein de bouche/compensateur Venom Tactical Antidote 7,62 x 39 mm pour AK"
L["eft_muzzle_ak_antidote.compactname"] = "Antidote"
L["eft_muzzle_ak_antidote.description"] = "Le frein de bouche et compensateur Antidote par Venom Tactical et Rifle Dynamics est conçu pour être installé sur les armes classiques types AK. Il réduit le recul ainsi que le relèvement du canon."

L["eft_muzzle_ak_cncwar.printname"] = "Adaptateur de filetage CNC Warrior 5,56 x 45 mm pour AK"
