L["eft_muzzle_ak_tt_ak.compactname"] = "TT AK"
L["eft_muzzle_ak_tt_ak.description"] = "L'adaptateur de filetage Taktika Tula. Permet d'installer des accessoires modernes sur la bouche du canon des AK et AKM."

