L["eft_silencer_ar15_qdc556.compactname"] = "QDC 556"
L["eft_silencer_ar15_qdc556.description"] = "Réducteur de sons Knight's Armament Company QDC 556, conçu pour une utilisation avec une arme en 5,56 x 45 mm. Peut uniquement être installé sur le cache-flamme KAC QDC à 3 dents."

L["eft_silencer_ar15_m4sdk.printname"] = "Réducteur de son Griffin Armament M4SD-K 5,56 x 45 mm pour AR-15"
L["eft_silencer_ar15_m4sdk.compactname"] = "M4SD-K"
L["eft_silencer_ar15_m4sdk.description"] = "Le réducteur de son M4SD-K est conçu pour être utilisé avec les systèmes d'arme de calibre 5,56 x 45 mm. Peut uniquement être monté sur les cache-flammes possédant une interface SDQD. Produit par Griffin Armament."

L["eft_silencer_ar15_socommonster.printname"] = "Réducteur de son SureFire SOCOM556 MONSTER 5,56 x 45 mm"
L["eft_silencer_ar15_socommonster.compactname"] = "556-MONSTER"
L["eft_silencer_ar15_socommonster.description"] = "Le réducteur de son SureFire SOCOM556-RC2 pour calibres 5,56 x 45 mm et .223, ne peut être installé que sur embouts de canons SureFire compatibles."

L["eft_silencer_ar15_socomrc2.printname"] = "Réducteur de son Surefire SOCOM556-RC2 5,56 x 45 mm"
L["eft_silencer_ar15_socomrc2.compactname"] = "RC2"
L["eft_silencer_ar15_socomrc2.description"] = "The SureFire SOCOM556-RC2 5.56x45 and .223 sound suppressor, can only be installed on compatible SureFire muzzle devices."

L["eft_silencer_ar15_socommini.printname"] = "Réducteur de son SureFire SOCOM556-MINI MONSTER 5,56 x 45 mm"
L["eft_silencer_ar15_socommini.compactname"] = "556-MINI"
L["eft_silencer_ar15_socommini.description"] = "Le réducteur de son SureFire SOCOM556-MINI MONSTER pour calibres 5,56 x 45 mm et .223, est une version plus courte du SOCOM556-MONSTER. Ne peut être installé que sur embouts de canon Surefire compatibles."

L["eft_silencer_ar15_sakerasr.printname"] = "Réducteur de sons SilencerCo SAKER ASR 556 5,56 x 45 mm"
L["eft_silencer_ar15_sakerasr.compactname"] = "Saker ASR 556"
L["eft_silencer_ar15_sakerasr.description"] = "Le réducteur de son SilencerCo Saker ASR 556, conçu pour une utilisation avec une arme en 5,56 x 45 mm. Peut être installé sur les freins de bouche ASR compatibles."

L["eft_silencer_ar15_kacqdssnt4.printname"] = "Réducteur de son KAC QDSS NT-4 5,56 x 45 mm (noir)"
L["eft_silencer_ar15_kacqdssnt4.compactname"] = "NT-4 noir"
L["eft_silencer_ar15_kacqdssnt4.description"] = "Le réducteur de son Knight's Armament Company QDSS NT-4 pour 5,56 x 45 mm et .223 ne peut qu'être installé sur les cache-flammes KAC compatibles."

L["eft_silencer_ar15_kacqdssnt4_f.printname"] = "Réducteur de son KAC QDSS NT-4 5,56 x 45 mm (FDE)"
L["eft_silencer_ar15_kacqdssnt4_f.compactname"] = "NT-4 FDE"
L["eft_silencer_ar15_kacqdssnt4_f.description"] = "Le réducteur de son Knight's Armament Company QDSS NT-4 pour 5,56 x 45 mm et .223 ne peut qu'être installé sur les cache-flammes KAC compatibles."

