L["eft_aksu_rec_ak_b.printname"] = "Copertura antipolvere AKS-74UB"
L["eft_aksu_rec_ak_b.compactname"] = "AKS-74UB"
L["eft_aksu_rec_ak_b.description"] = "Copertura antipolvere standard per AKS-74UB, prodotta dalla Izhmash."

L["eft_aksu_rec_ak_std.printname"] = "Copertura antipolvere AKS-74U (6P26 Sb.7)"
L["eft_aksu_rec_ak_std.compactname"] = "6P26 Sb.7"
L["eft_aksu_rec_ak_std.description"] = "Copertura antipolvere standard per AKS-74U, AKS-74UN, prodotto dalla IzhMash."

L["eft_aksu_stock_std.printname"] = "Calcio a telaio di metallo AKS-74U (6P26 Sb.5)"
