L["eft_gas_ak_6p20.printname"] = "Tubo del gas AK-47 (6P20 Sb.1-2)"
L["eft_gas_ak_6p20.compactname"] = "6P20 Sb.1-2"
L["eft_gas_ak_6p20.description"] = "Un tubo del gas standard per fucili automatici AK-74. I tubi del gas incanalano la direzione di marcia del pistone a gas."

