L["eft_hg_ak_wasr.printname"] = "Impugnatura in legno WASR-10/63 CAF"
L["eft_hg_ak_wasr.compactname"] = "WASR"
L["eft_hg_ak_wasr.description"] = "Un paramano/impugnatura Rumena per fucili WASR-10/63, prodotto dalla Cugir Arms Factory. Compatibile con i sistemi d'arma della famiglia AK."

L["eft_hg_ak_x47.printname"] = "Sistema di slitte per paramano tattico AK TDI X47"
L["eft_hg_ak_x47.compactname"] = "X47"
L["eft_hg_ak_x47.description"] = "Un paramano tattico in alluminio per la famiglia dei fucili d'assalto AK, progettato e prodotto dalla TDI Arms. Dotato di molteplici supporti di slitte per l'installazione di una vasta gamma di equipaggiamento aggiuntivo."

