L["eft_mag_ak_6l31_545_60.printname"] = "Caricatore da 60 colpi AK-74 5.45x39 6L31"
L["eft_mag_ak_6l31_545_60.compactname"] = "6L31 60"
L["eft_mag_ak_6l31_545_60.description"] = "Caricatore 6L31 Izhmash in polimero da 60 colpi 5.45x39, per AK-74 e altri sistemi compatibili. Prodotto in piccola quantità e mai serializzato."

L["eft_mag_ak_6p2bak_762_40.printname"] = "Caricatore in bachelite da 40 colpi AK 7.62x39"
L["eft_mag_ak_6p2bak_762_40.compactname"] = "6P2 bak. 40"
L["eft_mag_ak_6p2bak_762_40.description"] = "Un caricatore in bachelite standard da 40 colpi per l'RPK. Si adatta a 7.62x39 AK e armi compatibili. Può anche essere fornito con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente."

L["eft_mag_ak_6p2sb11_762_40.printname"] = "Caricatore in alluminio da 40 colpi AK 7.62x39 Molot Arms (6P2.Sb-11)"
L["eft_mag_ak_6p2sb11_762_40.compactname"] = "6P2 Sb-11 40"
L["eft_mag_ak_6p2sb11_762_40.description"] = "Il caricatore standard 6P2 Sb-11 da 40 colpi 7.62x39 per l'RPK LMG dal 1961 in poi, si adatta a qualsiasi arma compatibile con AK. Può anche essere caricato con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente."

L["eft_mag_ak_ak103_762_30.printname"] = "Caricatore da 30 colpi AK-103 7.62x39"
L["eft_mag_ak_ak103_762_30.compactname"] = "АК-103 30"
L["eft_mag_ak_ak103_762_30.description"] = "Un caricatore Izhmash in bachelite da 30 colpi per munizioni 7.62x39, per AK-103 e sistemi compatibili. Può anche essere fornito con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente."

L["eft_mag_ak_ak12_545_30_old.printname"] = "Caricatore da 30 colpi primo modello AK-12 5.45x39"
L["eft_mag_ak_ak12_545_30_old.compactname"] = "AK-12 old 30"
L["eft_mag_ak_ak12_545_30_old.description"] = "Un primo modello di caricatore in polimero da 30 colpi con finestre per il controllo delle munizioni, per cartuccia 5.45x39 per AK-12 e fucili compatibili. Prodotto dalla Izhmash."

L["eft_mag_ak_ak30fde_762_30.printname"] = "Caricatore da 30 colpi AK 7.62x39 US Palm \"AK30\" (FDE)"
L["eft_mag_ak_ak30fde_762_30.compactname"] = "AK30 FDE 30"
L["eft_mag_ak_ak30fde_762_30.description"] = "Un caricatore US Palm AK30 in polimero da 30 colpi per 7.62x39 AK e armi compatibili. Può anche essere fornito con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente. Versione Flat Dark Earth."

L["eft_mag_ak_ak30_762_30.printname"] = "Caricatore da 30 colpi AK 7.62x39 US Palm \"AK30\" (Nero)"
L["eft_mag_ak_ak30_762_30.compactname"] = "AK30 30"
L["eft_mag_ak_ak30_762_30.description"] = "Un caricatore US Palm AK30 in polimero da 30 colpi per 7.62x39 AK e armi compatibili. Può anche essere fornito con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente. Versione nera."

L["eft_mag_ak_akms_762_30.printname"] = "Caricatore in alluminio da 30 colpi AKMS 7.62x39"
L["eft_mag_ak_akms_762_30.compactname"] = "AKMS al. 30"
L["eft_mag_ak_akms_762_30.description"] = "Un caricatore in alluminio da 30 colpi per 7.62x39 AKMS e compatibili, soprannominato \"Airborne\". Realizzato in lega leggera di alluminio e rinforzato da nervature aggiuntive. È piuttosto una rarità, poiché non è mai stato prodotto in serie. Può anche essere fornito con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente."

L["eft_mag_ak_alum_762_10.printname"] = "Caricatore in alluminio da 10 colpi AK 7.62x39"
L["eft_mag_ak_alum_762_10.compactname"] = "AK al. 10"
L["eft_mag_ak_alum_762_10.description"] = "Caricatore in alluminio da 10 colpi per armi 7.62x39 AK e compatibili, realizzato in lega leggera di alluminio e rinforzato da nervature aggiuntive. Questa modifica viene fornita con un limitatore di capacità di 10 colpi per il mercato civile. Può anche essere fornito con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente."

L["eft_mag_ak_molot_762_75.printname"] = "Caricatore a tamburo da 75 colpi AK 7.62x39 Molot Arms"
L["eft_mag_ak_molot_762_75.compactname"] = "Molot 75"
L["eft_mag_ak_molot_762_75.description"] = "Un caricatore a tamburo metallico da 75 colpi per la mitragliatrice leggera RPK. Si adatta a 7.62x39 AK e sistemi d'arma compatibili. Può anche essere fornito con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente."

L["eft_mag_ak_pmag_545_30.printname"] = "Caricatore da 30 colpi AK-74 5.45x39 Magpul PMAG 30 GEN M3"
L["eft_mag_ak_pmag_545_30.compactname"] = "GEN M3 30"
L["eft_mag_ak_pmag_545_30.description"] = "Un caricatore Magpul Pmag 30 AK74 GEN M3 in polimero da 30 colpi per armi 5.45x39 AK e compatibili."

L["eft_mag_ak_pmag_762_30.printname"] = "Caricatore da 30 colpi AK 7.62x39 Magpul PMAG 30 GEN M3"
L["eft_mag_ak_pmag_762_30.compactname"] = "GEN M3 30"
L["eft_mag_ak_pmag_762_30.description"] = "Un caricatore in polimeri 7.62x39 Magpul Pmag 30 AK/AKM GEN M3 per AK 7.62x39 ed altre armi compatibili. Può essere anche caricato con proiettili .366 TKM usandolo con calibri corrispondenti per armi AK compatibili."

L["eft_mag_ak_pmag_banana_762_30.printname"] = "Caricatore da 30 colpi AK 7.62x39 Magpul PMAG 30 GEN M3 (Banana)"
L["eft_mag_ak_pmag_banana_762_30.compactname"] = "GEN M3 30"
L["eft_mag_ak_pmag_banana_762_30.description"] = "Caricatore Magpul PMAG 30 AK/AKM GEN M3 in polimero da 30 colpi per armi 7.62x39 AK e compatibili. Può anche essere fornito con munizioni .366 TKM per l'uso con le armi compatibili con AK di calibro corrispondente. Il caricatore è dipinto in un colore giallo banana tattico."

L["eft_mag_ak_promag_762_73.printname"] = "Caricatore a tamburo da 73 colpi AK 7.62x39 ProMag AK-A-16"
