L["eft_ammo_12x70_hpcopper.description"] = "Munición de calibre 12/70 Copper Sabot Premier - Es un cartucho con una posta de punta hueca para escopetas de Calibre 12."

L["eft_ammo_12x70_magnum.printname"] = "Cartucho de 12/70 - Perdigones Magnum de 8,5 mm"
L["eft_ammo_12x70_magnum.compactname"] = "Magnum"
