L["eft_ar_buffertube_m7a1.compactname"] = "M7A1PDW"
L["eft_ar_buffertube_m7a1.description"] = "El Kit TROY M7A1 PDW Stock es una culata retráctil hecha para elevar las carabinas de calibre 5,56x45 mm OTAN, haciéndolas más compactas, cerca de 3 pulgadas más cortas respecto a las culatas tradicionales. Reduce de forma general el tamaño del arma y amortigua el retroceso. Versión de color Negro."

L["eft_ar_buffertube_m7a1f.printname"] = "Culata TROY M7A1 PDW para AR-15 (FDE)"
L["eft_ar_buffertube_m7a1f.compactname"] = "M7A1PDW"
L["eft_ar_buffertube_m7a1f.description"] = "El Kit TROY M7A1 PDW Stock es una culata retráctil hecha para elevar las carabinas de calibre 5,56x45 mm OTAN, haciéndolas más compactas, cerca de 3 pulgadas más cortas respecto a las culatas tradicionales. Reduce de forma general el tamaño del arma y amortigua el retroceso. Versión de color Flat Dark Earth."

L["eft_ar_buffertube_rtm.printname"] = "Tubo amortiguador RTM ATP para AR-15"
L["eft_ar_buffertube_rtm.compactname"] = "Tubo ATP"
L["eft_ar_buffertube_rtm.description"] = "Un tubo amortiguador ATP con 6 posiciones y diámetro Mil-Spec, compatible con cualquier carabina basada en AR-15. Producido por RTM."

L["eft_ar_buffertube_siadv.printname"] = "Tubo amortiguador Strike Industries Advanced Receiver Extension para AR-15"
L["eft_ar_buffertube_siadv.compactname"] = "Tubo ARE"
L["eft_ar_buffertube_siadv.description"] = "Un tubo amortiguador Advanced Receiver Extension con especificaciones Mil-Spec, compatible con cualquier carabina o fusil basado en AR-15. Este tubo tiene un sistema distintivo que reduce la fricción con la culata y reduce el peso al mismo tiempo que fortalece las áreas con mayor estrés. Producido por Strike Industries."

L["eft_ar_buffertube_siadvred.printname"] = "Tubo amortiguador Strike Industries Advanced Receiver Extension para AR-15 (Rojo Anodizado)"
L["eft_ar_buffertube_siadvred.compactname"] = "Tubo ARE"
L["eft_ar_buffertube_siadvred.description"] = "Un tubo amortiguador Advanced Receiver Extension con especificaciones Mil-Spec, compatible con cualquier carabina o fusil basado en AR-15. Este tubo tiene un sistema distintivo que reduce la fricción con la culata y reduce el peso al mismo tiempo que fortalece las áreas con mayor estrés. Producido por Strike Industries. Versión de color Rojo Anodizado."

L["eft_ar_buffertube_socom.printname"] = "Culata DoubleStar ACE SOCOM Gen.4 para AR-15"
L["eft_ar_buffertube_socom.compactname"] = "ACE SOCOM"
L["eft_ar_buffertube_socom.description"] = "La cuarta generación de las recientemente populares culatas SOCOM, producida por DoubleStar. La culata cuenta con un tubo amortiguador integrado resistente y una cubierta de espuma sobre el tubo, la cual proporciona un cómodo soporte de mejilla."

L["eft_ar_buffertube_std.printname"] = "Tubo amortiguador Colt Carbine para AR-15"
L["eft_ar_buffertube_std.compactname"] = "Tubo Carbine"
L["eft_ar_buffertube_std.description"] = "Un Tubo Amortiguador Colt y Extensión del Cajón de Mecanismos, de 4 posiciones y con diámetro Mil-Spec, para carabinas basadas en AR-15."

L["eft_ar_buffertube_ubrgen2.printname"] = "Culata Magpul UBR GEN2 para AR-15 (Negra)"
L["eft_ar_buffertube_ubrgen2.compactname"] = "UBR GEN2"
L["eft_ar_buffertube_ubrgen2.description"] = "Una culata telescópica UBR GEN2, producida por Magpul. Versión de color Negro."

L["eft_ar_buffertube_ubrgen2f.printname"] = "Culata Magpul UBR GEN2 para AR-15 (FDE)"
L["eft_ar_buffertube_ubrgen2f.compactname"] = "UBR GEN2"
L["eft_ar_buffertube_ubrgen2f.description"] = "Una culata telescópica UBR GEN2, producida por Magpul. Versión de color Flat Dark Earth."

L["eft_ar_buffertube_viperpdw.printname"] = "Culata Strike Industries Viper PDW para AR-15"
L["eft_ar_buffertube_viperpdw.compactname"] = "Cul. Viper PDW"
L["eft_ar_buffertube_viperpdw.description"] = "El kit Strike Industries Viper PDW es una culata retráctil para ser instalada en sistemas de armas basadas en AR-15. Reduce el tamaño del arma de forma general y ayuda a amortiguar el retroceso."

L["eft_ar_buffertube_arfx.printname"] = "AR-15 DoubleStar ACE ARFX Skeleton Stock"
L["eft_ar_buffertube_arfx.compactname"] = "ARFX"
L["eft_ar_buffertube_arfx.description"] = "The lightweight ACE ARFX Skeleton Stock provides comfort, balance and aesthetics. Fits both the Mil-Spec and civilian M/AR platform. Manufactured by DoubleStar."

L["eft_ar_buffertube_baskak.printname"] = "Culata Armacon Baskak"
L["eft_ar_buffertube_baskak.compactname"] = "Baskak"
L["eft_ar_buffertube_baskak.description"] = "Una culata rusa con un diseño de tubo ligero y sencillo, para su uso en sistemas AK. Puede instalarse tanto en adaptadores para culatas Mil-Spec como adaptadores Com-Spec M/AR. ¡No es compatible con sistemas AR!"

-- [[ ar_pgrip ]]
L["eft_ar_pgrip_colta2.printname"] = "Pistolete Colt A2 para AR-15"
L["eft_ar_pgrip_colta2.compactname"] = "A2"
L["eft_ar_pgrip_colta2.description"] = "El pistolete de polímero Colt A2 puede ser instalado en cualquier arma compatible con los pistoletes de AR-15. Es el pistolete estándar para todas las armas basadas en AR-15 del Ejército de los EEUU y el USMC."

L["eft_ar_pgrip_diecsfde.printname"] = "Pistolete Damage Industries ECS para AR-15 (FDE)"
L["eft_ar_pgrip_diecsfde.compactname"] = "ECS FDE"
L["eft_ar_pgrip_diecsfde.description"] = "El pistolete ECS de polímero puede ser instalado en cualquier arma que sea compatible con los pistoletes de AR-15. Versión de color Flat Dark Earth."

L["eft_ar_pgrip_dlg123.printname"] = "Pistolete DLG Tactical DLG-123 para AR-15"
