-- [[ scope ]]
L["eft_scope_25mm_pilad.printname"] = "Cannocchiale VOMZ Pilad 4x32 25.4mm"
L["eft_scope_25mm_pilad.compactname"] = "Pilad 4х32"
L["eft_scope_25mm_pilad.description"] = "Progettato per sparare a piccole e medie distanze. Il mirino sarà pratico per sparare a oggetti in movimento a una distanza massima di 150m."

L["eft_scope_30mm_march.printname"] = "Mirino ottico March Tactical 3-24x42 FFP"
L["eft_scope_30mm_march.compactname"] = "3-24x42 FFP"
L["eft_scope_30mm_march.description"] = "Il mirino ottico March Tactical 3-24x42 FFP è affidabile e preciso con munizioni di qualsiasi potenza."

L["eft_scope_30mm_mark4.printname"] = "Cannocchiale Leupold Mark 4 LR 6.5-20x50"
L["eft_scope_30mm_mark4.compactname"] = "6.5-20x50"
L["eft_scope_30mm_mark4.description"] = "Preciso, accurato, durevole e affidabile - l'ottica per fucili Leupold Mark 4 6,5-20x50. Il modello fornisce un ingrandimento da 6,5x ​​a 20x dandoti un'idea chiara delle dimensioni effettive dell'oggetto mirato."

L["eft_scope_30mm_razor.printname"] = "Cannocchiale Vortex Razor HD Gen.2 1-6x24"
L["eft_scope_30mm_razor.compactname"] = "Razor HD Gen.2"
L["eft_scope_30mm_razor.description"] = "Un cannocchiale tattico progettato per una rapida acquisizione del bersaglio e la massima precisione. Prodotto dalla Vortex Optics."

L["eft_scope_30mm_sb_pmii1.printname"] = "Ottica Schmidt & Bender PM II 1-8x24"
L["eft_scope_30mm_sb_pmii1.compactname"] = "PM II 1-8x24"
L["eft_scope_30mm_sb_pmii1.description"] = "Il cannocchiale da puntamento Schmidt & Bender PM II 1-8x24 è stato originariamente creato per le forze militari d'élite per l'acquisizione di bersagli di alta qualità e la massima precisione. "

L["eft_scope_30mm_tac30.printname"] = "Cannocchiale Burris FullField TAC30 1-4x24"
L["eft_scope_30mm_tac30.compactname"] = "TAC30"
L["eft_scope_30mm_tac30.description"] = "Il TAC30 offre un campo visivo molto ampio e un ingrandimento 1x all'estremità inferiore, per un'acquisizione del bersaglio facile e veloce. Lo zoom completo 4x e il reticolo Ballistic CQ rendono molto pratici i tiri a circa 300 iarde. Il punto centrale illuminato e l'ampio cerchio luminoso consentono l'aggancio istantaneo del bersaglio a distanza ravvicinata."

L["eft_scope_30mm_vudu.printname"] = "Cannocchiale EOTech Vudu 1-6x24"
L["eft_scope_30mm_vudu.compactname"] = "1-6x24"
L["eft_scope_30mm_vudu.description"] = "Il cannocchiale EOTech Vudu 1-6x24, ugualmente efficace su piattaforme AR e fucili a retrocarica. Il suo reticolo ad anello in stile EOTech è progettato per un rapido ingaggio del bersaglio a bassa potenza, offrendo al contempo la risoluzione e la precisione necessarie per i bersagli a lungo raggio."

L["eft_scope_34mm_atacr.printname"] = "Cannocchiale Nightforce ATACR 7-35x56"
L["eft_scope_34mm_atacr.compactname"] = "ATACR 7-35x56"
L["eft_scope_34mm_atacr.description"] = "L'ottica versatile NightForce ATACR 7-35x56 con un'ampia gamma di ingrandimenti e un ampio campo visivo, facilitando la visione e l'ingaggio dei bersagli."

L["eft_scope_34mm_hsff.printname"] = "Ottica Hensoldt FF 4-16x56"
L["eft_scope_34mm_hsff.compactname"] = "FF 4-16"
L["eft_scope_34mm_hsff.description"] = "Il mirino telescopico Hensoldt FF 4-16x56 è stato sviluppato per offrire le massime prestazioni ed elevate portate di regolazione pur mantenendo un sistema ottico ridotto. Grazie alla maggiore trasparenza nella gamma spettrale blu e alla luce regolabile del reticolo, è particolarmente comodo in condizioni di scarsa illuminazione. La versione FF dell'ottica ha il reticolo posizionato in primo piano, quindi, il motivo del reticolo viene ingrandito anche quando si cambia l'ingrandimento. In questo modo è possibile determinare la distanza con il MIL-DOT senza doverla calcolare."

L["eft_scope_34mm_sb_pmii3.printname"] = "Ottica Schmidt & Bender PM II 3-20x50 34mm"
L["eft_scope_34mm_sb_pmii3.compactname"] = "PM II 3-20x50"
L["eft_scope_34mm_sb_pmii3.description"] = "L'ottica Schmidt & Bender PM II 3-20x50 è stato originariamente creato per le forze militari d'élite per l'acquisizione di bersagli di alta qualità e la massima precisione."

L["eft_scope_34mm_sb_pmii5.printname"] = "Cannocchiale Schmidt & Bender PM II 5-25x56 34mm"
L["eft_scope_34mm_sb_pmii5.compactname"] = "PM II 5-25x56"
L["eft_scope_34mm_sb_pmii5.description"] = "Il cannocchiale da puntamento Schmidt & Bender PM II 5-25x56 è stato originariamente creato per le forze militari d'élite per l'acquisizione del bersaglio di alta qualità e la massima precisione."

L["eft_scope_acog_ta01.printname"] = "Ottica Trijicon ACOG TA01NSN 4x32 (Nera)"
L["eft_scope_acog_ta01.compactname"] = "TA01NSN"
L["eft_scope_acog_ta01.description"] = "Progettato secondo le esatte specifiche dell'US Special Operations Command per l'uso da parte delle forze d'elite americane, il Trijicon ACOG TA01NSN è il più popolare ACOG di Trijicon. Il reticolo di puntamento permette di compensare il moto del proiettile fino a 600 metri senza alcuna regolazione manuale. Si possono montare ulteriori mirini posteriori di riserva. Non è necessaria alcuna batteria. Versione nera."

L["eft_scope_acog_ta01_tan.printname"] = "Ottica Trijicon ACOG TA01NSN 4x32 (Marrone)"
L["eft_scope_acog_ta01_tan.compactname"] = "TA01NSN"
L["eft_scope_acog_ta01_tan.description"] = "Progettato secondo le esatte specifiche dell'US Special Operations Command per l'uso da parte delle forze d'elite americane, il Trijicon ACOG TA01NSN è il più popolare ACOG di Trijicon. Il reticolo di puntamento permette di compensare il moto del proiettile fino a 600 metri senza alcuna regolazione manuale. Si possono montare ulteriori mirini posteriori di riserva. Non è necessaria alcuna batteria. Versione marrone."

L["eft_scope_acog_ta11d.printname"] = "Ottica Trijicon ACOG 3.5x35"
L["eft_scope_acog_ta11d.compactname"] = "TA11D"
L["eft_scope_acog_ta11d.description"] = "L'ottica per fucili ACOG 3.5x35 prodotto dalla Trijicon. Rimane ancora uno dei preferiti tra i tiratori competitivi, le forze dell'ordine e i civili amanti della libertà da tutto il mondo."

L["eft_scope_adop4.printname"] = "Canocchiale NcSTAR ADO P4 Sniper 3-9x42"
L["eft_scope_adop4.compactname"] = "ADO P4"
L["eft_scope_adop4.description"] = "L'ottica ad ingrandimento variabile Advance Dual Optic (ADO) 3X-9X con obiettivo da 42mm. L'ottica ADO è dotata di un mirino a punto rosso integrato sulla parte superiore del corpo del cannocchiale. Prodotto dalla NcSTAR."

L["eft_scope_bravo4.printname"] = "Ottica SIG Sauer BRAVO4 4X30"
L["eft_scope_bravo4.compactname"] = "BRAVO4"
L["eft_scope_bravo4.description"] = "Progettato dalla SIG Sauer, il mirino ottico BRAVO4 4X30 presenta il FOV eccezionalmente ampio, il 43% più largo rispetto ai concorrenti più vicini. Ha anche un supporto per slitta aggiuntiva sulla parte superiore che consente l'installazione di un mirino compatto di backup."

L["eft_scope_compactprism.printname"] = "Ottica Monstrum Tactical Compact Prism 2x32"
L["eft_scope_compactprism.compactname"] = "Compact 2x32"
L["eft_scope_compactprism.description"] = "Ottica prismatica compatta con 2 livelli di ingrandimento, prodotto dalla Mostrum."

L["eft_scope_dovetail_1p78.printname"] = "Ottica NPZ 1P78-1 2.8x"
L["eft_scope_dovetail_1p78.compactname"] = "1P78-1"
L["eft_scope_dovetail_1p78.description"] = "Un'ottica diurna con ingrandimento 2.8x, installata su slitte a coda di rondine. Prodotto dalla NPZ."

L["eft_scope_dovetail_ekp802.printname"] = "Mirino reflex Axion Kobra EKP-8-02 (Coda di rondine)"
L["eft_scope_dovetail_ekp802.compactname"] = "EKP-8-02 DT"
L["eft_scope_dovetail_ekp802.description"] = "Kobra è un mirino reflex molto popolare tra le agenzie di sicurezza e i tiratori civili. È stato progettato per le forze armate della Federazione Russa, ma non è mai stato formalmente adottato. Questa versione è progettata per l'installazione su slitte a coda di rondine. Prodotto dalla Axion."

L["eft_scope_dovetail_nspum.printname"] = "Ottica a coda di rondine per la visione notturna NSPU-M"
L["eft_scope_dovetail_nspum.compactname"] = "NSPU-M"
L["eft_scope_dovetail_nspum.description"] = "NSPU-M (Nochnoy Strelkovyy Pritsel Unifitsirovannyy Modernizirovannyy - 'Night Rifle Scope Unified Modernized'), Indice GRAU - 1PN58. Una vecchia ottica sovietica per la visione notturna per fucili della serie AK, progettata per adattarsi alle slitte a coda di rondine."

L["eft_scope_dovetail_okp7.printname"] = "Mirino reflex OKP-7 (Coda di rondine)"
L["eft_scope_dovetail_okp7.compactname"] = "OKP-7 DT"
L["eft_scope_dovetail_okp7.description"] = "Il design originale del mirino OKP, permette di sparare da posizioni instabili in condizioni di rapido cambiamento, riduce l’affaticamento dell’operatore, e, se necessario, permette l’uso di mirini meccanici di base senza ostacoli.\nLa caratteristica fondamentale di questo mirino è che è un accessorio riflettore senza montatura che non ostruisce il campo di fuoco; in alternativa, il riflettore è protetto da una sottile e resistente visiera che è quasi impercettibile mentre si spara. Questa variante è installata nel attacco a coda di rondine."

L["eft_scope_dovetail_pk1.printname"] = "Mirino reflex a coda di rondine NPZ PK1 \"Obzor\""
L["eft_scope_dovetail_pk1.compactname"] = "\"Obzor\""
L["eft_scope_dovetail_pk1.description"] = "Un mirino reflex progettato per i cacciatori per l'acquisizione rapida del bersaglio durante il funzionamento a temperature molto rigide, installato su slitte a coda di rondine. Prodotto dalla NPZ."

L["eft_scope_dovetail_pkaa.printname"] = "Mirino reflex a coda di rondine BelOMO PK-AA"
L["eft_scope_dovetail_pkaa.compactname"] = "PK-AA"
