L["eft_weapon_akmsn"] = "AKMSN"
L["eft_weapon_akmsn_full"] = "Karabinek automatyczny Kałasznikowa AKMSN 7,62 x 39"
L["eft_weapon_akmsn_desc"] = "Karabinek automatyczny AKMSN (Awtomat Kałasznikowa Modiernizirowannyj Składnoj Nocznoj – „zmodernizowany karabinek automatyczny Kałasznikowa nocny ze składaną kolbą”) 7,62 × 39 mm to wariant AKMS z szyną montażową typu jaskółczy ogon do instalacji lunet noktowizyjnych rodziny NSP – NSP-2/3/3A, NSPU, NSPU-M."

L["eft_weapon_aks74"] = "AKS-74"
L["eft_weapon_aks74_full"] = "Karabinek automatyczny Kałasznikowa AKS-74 5,45 x 39"
L["eft_weapon_aks74_desc"] = "Karabinek automatyczny AKS-74 (Awtomat Kałasznikowa Składnoj 74 – „skrócony karabinek automatyczny Kałasznikowa 74”) kalibru 5,45 × 39 mm to wariant karabinku automatycznego AK-74 wyposażony w składaną na bok metalową kolbą zaprojektowany głównie do użytku przez piechotę szturmową i opracowany równolegle z podstawowym AK-74. Łoże AKS-74 jest wykonane z wytłoczonych z blachy rozpórek, sprasowanych maszynowo w kształt litery „U” i zmontowanych za pomocą wykrojnika i spawania."

L["eft_weapon_aks74n"] = "AKS-74N"
