L["eft_weapon_rop30"] = "Flara"
L["eft_weapon_rop30_full"] = "Reaktywna flara sygnałowa ROP-30 (biała)"
L["eft_weapon_rop30_desc"] = "ROP-30 to reaktywna flara sygnałowa przeznaczona do oświetlania miejsc słabo widocznych w porze nocnej."

L["eft_weapon_rsp30b"] = "Niebieska"
L["eft_weapon_rsp30b_full"] = "Reaktywna flara sygnałowa RSP-30 (niebieska)"
L["eft_weapon_rsp30b_desc"] = "Reaktywne naboje sygnałowe do dowodzenia i utrzymywania interakcji w drużynie. Niektóre Scavy używają tego koloru flary do wysyłania pewnych rodzajów sygnałów. Pytanie brzmi, co oznaczają te sygnały?"

L["eft_weapon_rsp30g"] = "Zielona"
L["eft_weapon_rsp30g_full"] = "Reaktywna flara sygnałowa RSP-30 (zielona)"
L["eft_weapon_rsp30g_desc"] = "RSP-30 to reaktywne naboje sygnałowe służące do dowodzenia i utrzymywania interakcji między oddziałami. Wielogwiazdkowe naboje rakietowe są wykorzystywane jako flary."

L["eft_weapon_rsp30r"] = "Czerwona"
L["eft_weapon_rsp30r_full"] = "Reaktywna flara sygnałowa RSP-30 (czerwona)"
L["eft_weapon_rsp30r_desc"] = "RSP-30 to reaktywne naboje sygnałowe służące do dowodzenia i utrzymywania interakcji między oddziałami. Wielogwiazdkowe naboje rakietowe są wykorzystywane jako flary."

L["eft_weapon_rsp30sy"] = "S-żółta"
L["eft_weapon_rsp30sy_full"] = "Reaktywna flara sygnałowa RSP-30 (specjalna żółta)"
L["eft_weapon_rsp30sy_desc"] = "Specjalny nabój używany do sygnalizowania odpowiednich osób i zbierania specjalnego ładunku dla Prapora."

L["eft_weapon_rsp30y"] = "Żółta"
L["eft_weapon_rsp30y_full"] = "Reaktywna flara sygnałowa RSP-30 (żółta)"
L["eft_weapon_rsp30y_desc"] = "RSP-30 to reaktywne naboje sygnałowe służące do dowodzenia i utrzymywania interakcji między oddziałami. Wielogwiazdkowe naboje rakietowe są wykorzystywane jako flary."

