L["eft_weapon_sp81"] = "SP-81"
L["eft_weapon_sp81_full"] = "Pistolet sygnałowy ZiD SP-81 26 x 75"
L["eft_weapon_sp81_desc"] = "Gładkolufowy pistolet zaprojektowany do wystrzeliwania nabojów z flarami sygnalizacyjnymi palącymi się na różne kolory. Wyprodukowany przez zakłady W.A. Diegtiariowa."

/////// Melee weapons
L["eft_weapon_6x5"] = "6H5"
L["eft_weapon_6x5_full"] = "Bagnet 6H5"
L["eft_weapon_6x5_desc"] = "6H5 to bagnet przyjęty w 1989 roku jako standardowe wyposażenie karabinka automatycznego AK-74."

L["eft_weapon_akula"] = "Akula"
L["eft_weapon_akula_full"] = "Akula push dagger"
L["eft_weapon_akula_desc"] = "As dangerous as the fish it's named after."

L["eft_weapon_wycc"] = "Siekiera"
L["eft_weapon_wycc_full"] = "Zabytkowa siekiera"
L["eft_weapon_wycc_desc"] = "Zabytkowa siekiera z uszkodzoną rączką, która należała do członka starożytnego klanu siekierkowców zwanych WYCC."

L["eft_weapon_gladius"] = "TWG"
L["eft_weapon_gladius_full"] = "APOK Tactical Wasteland Gladius"
L["eft_weapon_gladius_desc"] = "Miecz Tactical Wasteland Gladius, wyprodukowany przez firmę APOK. Podstawowa broń do walki wręcz mistrza Areny. Plotki głoszą, że jest to dokładnie to ostrze, którym walczył gladiator Woron. A przynajmniej tak wygląda."

L["eft_weapon_a2607"] = "A-2607"
L["eft_weapon_a2607_full"] = "Nóż Bars A-2607 ze stali 95x18"
L["eft_weapon_a2607_desc"] = "Nóż A-2607 marki Bars wykuty ze stali 95x18 z drewnianą rękojeścią i mosiężnym jelcem."

L["eft_weapon_a2607d"] = "A-2607"
L["eft_weapon_a2607d_full"] = "Nóż Bars A-2607 z damastu skuwanego"
L["eft_weapon_a2607d_desc"] = "Nóż A-2607 marki Bars wykuty z damastu skuwanego z rękojeścią wenge i mosiężnym jelcem."

L["eft_weapon_camper"] = "Toporek"
L["eft_weapon_camper_full"] = "Toporek obozowy"
L["eft_weapon_camper_desc"] = "Uniwersalny toporek podróżny do użytku w warunkach polowych lub w gospodarstwie domowym. Sprawdza się również jako broń do walki wręcz, jeśli sprawy przybiorą zbyt desperacki obrót."

L["eft_weapon_crash"] = "SCA"
L["eft_weapon_crash_full"] = "Toporek"
L["eft_weapon_crash_desc"] = "Zaprojektowana do pracy z twardymi tworzywami. Materiały i konstrukcja zapewniają optymalną siłę uderzenia i wytrzymałość. Główka siekiery została wykonana z tytanu 6AL4V o grubości 2,85 cala."

L["eft_weapon_cultist"] = "Nóż"
L["eft_weapon_cultist_full"] = "Nóż kultysty"
L["eft_weapon_cultist_desc"] = "Nóż o dziwnym kształcie i z dziwnymi znakami, odebrany kultystom. Wygląda na to, że jest używany jako nóż rytualny, ale najwyraźniej nie jest ograniczony do tego zastosowania. Ma technologiczne zmiany w konstrukcji przeznaczone do używania substancji toksycznych – lepiej nie dotykać ostrza."

L["eft_weapon_fulcrum"] = "Bagnet ER"
L["eft_weapon_fulcrum_full"] = "Bagnet ER FULCRUM"
L["eft_weapon_fulcrum_desc"] = "Extrema Ratio opracowało nóż FULCRUM na rozkaz Siedziby Włoskich Oddziałów Alpejskich, które wymagały uniwersalnego i wszechstronnego noża dla piechoty, mogącego z powodzeniem zastąpić nawet bardziej wyspecjalizowane wyposażenie. Skuteczność noża doprowadziła do opracowania bagnetu FULCRUM, który miał te same cechy, ale mógł zostać przymocowany do karabinu."

L["eft_weapon_freeman"] = "Łom"
L["eft_weapon_freeman_full"] = "Łom Freemana"
L["eft_weapon_freeman_desc"] = "Stalowy i niezawodny łom wyprodukowany przez firmę Freemana. Uwaga! Może prowadzić do kaskady rezonansowej."

L["eft_weapon_tomahawk"] = "TTKA"
L["eft_weapon_tomahawk_full"] = "Taktyczny Tomahawk Kiba Arms"
L["eft_weapon_tomahawk_desc"] = "Taktyczny tomahawk, narzędzie wielofunkcyjne i narzędzie do wyłamywania – wszystko w jednym."

L["eft_weapon_millerbros"] = "M-2"
L["eft_weapon_millerbros_full"] = "Miecz taktyczny Miller Bros. Blades M-2"
L["eft_weapon_millerbros_desc"] = "Krótki miecz taktyczny „M-2”. Wyprodukowany przez firmę Miller Bros. Blades."

L["eft_weapon_mpl50"] = "MPŁ-50"
L["eft_weapon_mpl50_full"] = "Saperka MPŁ-50"
L["eft_weapon_mpl50_desc"] = "MPŁ-50 (Małaja Piechotnaja Łopata 50 – „mała łopatka piechoty 50”) to niewielka łopata wynaleziona przez duńskiego oficera Madsa Johana Bucha Linnemanna w 1869 roku. Chociaż normalnie jest saperką, to MPŁ-50 w czasie konfliktu ma wiele zastosowań: od broni do walki wręcz po przyrząd do gotowania."

L["eft_weapon_taran"] = "PR-Taran"
L["eft_weapon_taran_full"] = "Pałka policyjna PR-Taran"
L["eft_weapon_taran_desc"] = "Pałka PR-Taran z bocznym chwytem jest używana przez organy ścigania do ochrony i kontroli tłumu."

L["eft_weapon_rebel"] = "RedRebel"
L["eft_weapon_rebel_full"] = "Czekan Red Rebel"
L["eft_weapon_rebel_desc"] = "Perfekcyjnie wyważony czekan; czub wykonany z jednego kawałka wzmocnionego metalu."

L["eft_weapon_voodoo"] = "Hawk"
L["eft_weapon_voodoo_full"] = "Taktyczny tomahawk SOG Voodoo Hawk"
L["eft_weapon_voodoo_desc"] = "SOG połączył swoje trzy najlepsze cechy: przedłużone ostrze tnące, kompaktową rękojeść z nylonu wzmacnianego włóknem szklanym oraz metalową nasadkę na końcu."

L["eft_weapon_sp8"] = "SP-8"
L["eft_weapon_sp8_full"] = "Maczeta SP-8"
L["eft_weapon_sp8_desc"] = "SP-8 to maczeta wykonana z jednolitego kawałka wysokowęglowej stali. Wyprodukowana w Ontario Knife Company."

L["eft_weapon_superfors"] = "Dead Blow"
L["eft_weapon_superfors_full"] = "Młot Superfors DB 2020 Dead Blow"
L["eft_weapon_superfors_desc"] = "Ten młot pokro uretanem, który daje „grzmot” przy użyciu stalowych kulek amortyzujących uderzenie i redukujących odrzut drgań na rękojeść."

L["eft_weapon_m48"] = "M48 Kukri"
L["eft_weapon_m48_full"] = "Taktyczne kukri M48 United Cutlery"
L["eft_weapon_m48_desc"] = "Taktyczne kukri z serii 48. Posiada ostrze ze stali nierdzewnej i gumowaną rękojeść. Świetny dodatek do arsenału każdego survivalowca! Wyprodukowane przez United Cutlery."

L["eft_weapon_taiga"] = "Tajga-1"
L["eft_weapon_taiga_full"] = "Maczeta surwiwalowa UWSR Tajga-1"
L["eft_weapon_taiga_desc"] = "UWSR „Urządzenie do prowadzenia działań ratowniczych” Tajga-1. Prawdziwie wszechstronne narzędzie do przetrwania w dziczy. Jest to broń i łopata, siekiera i wszystko, co można sobie wyobrazić. Został opracowany i wyprodukowany w ZSRR dla różnych organizacji."

/////// Throwable fragmentation
L["eft_weapon_f1"] = "F-1"
L["eft_weapon_f1_full"] = "Granat ręczny F-1"
L["eft_weapon_f1_desc"] = "Granat ręczny F-1 (indeks GRAU 57-G-721) to przeciwpiechotny granat obronny, przeznaczony do neutralizowania wroga w walce obronnej. Ze względu na znaczny efektywny promień fragmentacji, powinien być wyrzucany tylko zza osłon, takich jak betonowa ściana, APC lub czołg."

L["eft_weapon_m67"] = "M67"
L["eft_weapon_m67_full"] = "Granat ręczny M67"
L["eft_weapon_m67_desc"] = "Odłamkowy granat M67 jest amerykańskim przeciwpiechotnym granatem ręcznym przyjętym do użytku przez armię Stanów Zjednoczonych w 1969 roku."

L["eft_weapon_rgd5"] = "RGD-5"
L["eft_weapon_rgd5_full"] = "Granat ręczny RGD-5"
L["eft_weapon_rgd5_desc"] = "Granat ręczny RGD-5 (indeks GRAU 57-G-717) to przeciwpiechotny ofensywny odłamkowy ręczny granat z opóźnionym zapłonem. Czas opóźnienia zapalnika UZRGM zapewnia opóźnienie od 3,2 do 4,2 sekundy. Ze względu na ofensywny charakter granatu, odłamki są stosunkowo lekkie i mają mniejszy zasięg rażenia."

L["eft_weapon_rgn"] = "RGN"
L["eft_weapon_rgn_full"] = "Granat ręczny RGN"
L["eft_weapon_rgn_desc"] = "RGN (Rucznaja Granata Nastupatielnaja – „ofensywny granat ręczny”) to ofensywny antypiechotny granat odłamkowy wybuchający po uderzeniu."

L["eft_weapon_rgo"] = "RGO"
L["eft_weapon_rgo_full"] = "Granat ręczny RGO"
L["eft_weapon_rgo_desc"] = "RGO (Rucznaja Granata Oboronitielnaja – „obronny granat ręczny”) to obronny antypiechotny granat odłamkowy wybuchający po uderzeniu."

L["eft_weapon_v40"] = "M67"
L["eft_weapon_v40_full"] = "Granat ręczny M67"
L["eft_weapon_v40_desc"] = "Odłamkowy granat M67 jest amerykańskim przeciwpiechotnym granatem ręcznym przyjętym do użytku przez armię Stanów Zjednoczonych w 1969 roku."

L["eft_weapon_vog17"] = "WOG-17"
L["eft_weapon_vog17_full"] = "Improwizowany granat ręczny WOG-17 Chattabka"
L["eft_weapon_vog17_desc"] = "Granat ręczny „Chattabka” oparty na pocisku z granatnika WOG-17. Granat ten to pocisk WOG-17 z usuniętą górną częścią zawierającą detonator i destruktor. Krótki lont został zainstalowany, aby przyspieszyć zapłon. Wyjątkowo śmiercionośny granat."

L["eft_weapon_vog25"] = "WOG-25"
L["eft_weapon_vog25_full"] = "Improwizowany granat ręczny WOG-25 Chattabka"
L["eft_weapon_vog25_desc"] = "Granat ręczny „Chattabka” oparty na pocisku z granatnika WOG-25. Granat ten to pocisk WOG-25 z usuniętą górną częścią zawierającą detonator i destruktor. Krótki lont został zainstalowany, aby przyspieszyć zapłon. Wyjątkowo śmiercionośny granat."

