-- [[ muzzle_ar10 ]]
L["eft_muzzle_ar10_cmmgsv.printname"] = "Hamulec wylotowy do AR-10 CMMG SV Brake 7,62 x 51"
L["eft_muzzle_ar10_cmmgsv.compactname"] = "SV Brake"
L["eft_muzzle_ar10_cmmgsv.description"] = "Hamulec wylotowy SV Brake to skuteczny 1-portowy hamulec wylotowy zaprojektowany szczególne pod kaliber .308 do broni z systemu AR-10. Duży port daje szeroką powierzchnię dla gazów do uderzenia, która sprawia, że lufa się nie podnosi i zostaje na celu. Wyprodukowany przez firmę CMMG."

L["eft_muzzle_ar10_vette.printname"] = "Hamulec wylotowy do AR-10 Nordic Components Corvette 7,62 x 51"
L["eft_muzzle_ar10_vette.compactname"] = "Vette 762"
L["eft_muzzle_ar10_vette.description"] = "Hamulec wylotowy Corvette kalibru 7,62 × 51 mm ogranicza odrzut i skręt lufy poprzez odprowadzanie gazów na górę i do boku. Wyprodukowany przez firmę Nordic Components."

L["eft_muzzle_ar10_wave.printname"] = "Hamulec wylotowy do AR-10 Daniel Defense WAVE 7,62 x 51"
L["eft_muzzle_ar10_wave.compactname"] = "WAVE 762"
L["eft_muzzle_ar10_wave.description"] = "Hamulec wylotowy WAVE zaprojektowany, by bezpiecznie mocować tłumik dźwięku do broni palnej, ale działa też dobrze bez niego. Wyprodukowany ze stali nierdzewnej 17-4 PH stosowanej w przemyśle powietrznym i pod koniec azotowany w kąpieli solnej dla zminimalizowania korozji. Wyprodukowany przez firmę Daniel Defense."

L["eft_muzzle_ar10_warden.printname"] = "Tłumik płomieni do AR-10 SureFire Warden 7,62 x 51"
L["eft_muzzle_ar10_warden.compactname"] = "War 762"
L["eft_muzzle_ar10_warden.description"] = "Tłumik płomieni Warden w obudowie ze stali nierdzewnej, która poprawia ogólny wygląd, jednocześnie chroniąc wewnętrzne części przed uszkodzeniem. Ten tłumik płomieni zapobiega powstawaniu wewnętrznych uszkodzeń przez brud, kurz, gruz itp. Wyprodukowany przez firmę SureFire."

L["eft_muzzle_ar10_zk38.printname"] = "Hamulec wylotowy do AR-10 TAA ZK-38 7,62 x 51"
L["eft_muzzle_ar10_zk38.compactname"] = "ZK-38"
L["eft_muzzle_ar10_zk38.description"] = "ZK-38 to jednokomorowy bardzo skuteczny skośny hamulec wylotowy do broni opartych o platformę AR-10. Przód jest dość spiczasty i służy zarówno jako środek odstraszający w walce w zwarciu oraz jako broń do delikatnych celów. Nie jest zalecany w sytuacjach, w których istnieje obawa wystąpienia dużych wstrząsów. Wyprodukowany przez firmę Tactical Advantage Armory."

L["eft_muzzle_ar10_30cb.printname"] = "Hamulec wylotowy do AR-10 TAA ZK-38 7,62 x 51"
L["eft_muzzle_ar10_30cb.compactname"] = "ZK-38"
L["eft_muzzle_ar10_30cb.description"] = "ZK-38 to jednokomorowy bardzo skuteczny skośny hamulec wylotowy do broni opartych o platformę AR-10. Przód jest dość spiczasty i służy zarówno jako środek odstraszający w walce w zwarciu oraz jako broń do delikatnych celów. Nie jest zalecany w sytuacjach, w których istnieje obawa wystąpienia dużych wstrząsów. Wyprodukowany przez firmę Tactical Advantage Armory."

L["eft_muzzle_ar10_aac51t.printname"] = "Tłumik płomieni do AR-10 AAC Blackout 51T 7,62 x 51"
L["eft_muzzle_ar10_aac51t.compactname"] = "51T 762"
L["eft_muzzle_ar10_aac51t.description"] = "Blackout 51T jest skutecznym tłumikiem płomieni, który służy również jako platforma do dołączania tłumika dźwięku 762-SDN-6. Może być instalowany na opartej na systemie AR-10. Wyprodukowany przez firmę Advanced Armament Corporation (AAC)."

L["eft_muzzle_ar10_lantac.printname"] = "Hamulec wylotowy do AR-10 Lantac Dragon 7,62 x 51"
L["eft_muzzle_ar10_lantac.compactname"] = "DGN762B"
L["eft_muzzle_ar10_lantac.description"] = "Hamulec wylotowy Dragon został opracowany z myślą o instalacji na broni kalibru 7,62 × 51 mm. Zmniejsza odrzut i przeciwdziała podrzutowi lufy. Wyprodukowany przez firmę Lantac."

L["eft_muzzle_ar10_qdc_brake.printname"] = "Hamulec wylotowy do AR-10 KAC QDC Muzzle Brake Kit 7,62 x 51"
L["eft_muzzle_ar10_qdc_brake.compactname"] = "QDC 762"
L["eft_muzzle_ar10_qdc_brake.description"] = "Muzzle Brake Kit jest skutecznym hamulcem wylotowym, który służy również jako platforma do dołączania tłumika dźwięku PRS QDC. Może być instalowany na broni kompatybilnej z AR-10. Wyprodukowany przez firmę Knight’s Armament Company."

L["eft_muzzle_ar10_m11.printname"] = "Hamulec wylotowy do AR-10 Precision Armanent M11 Severe-Duty 7,62 x 51"
L["eft_muzzle_ar10_m11.compactname"] = "M11"
L["eft_muzzle_ar10_m11.description"] = "Hamulec wylotowy M11 zaprojektowany do instalacji na systemach AR-10 i kompatybilnych. Wyprodukowany przez firmę Precision Armament."

L["eft_muzzle_ar10_x3.printname"] = "Hamulec wylotowy do AR-10 2A Armanent X3 7,62 x 51"
L["eft_muzzle_ar10_x3.compactname"] = "X3 762"
L["eft_muzzle_ar10_x3.description"] = "Hamulec wylotowy X3 zaprojektowany do instalacji na systemach AR-10 i kompatybilnych. Wyprodukowany przez firmę 2A Armament."

L["eft_muzzle_ar10_mdr.printname"] = "Tłumik płomieni Desert Tech 7,62 x 51"
L["eft_muzzle_ar10_mdr.compactname"] = "MDR 762"
L["eft_muzzle_ar10_mdr.description"] = "Tłumik płomieni zaprojektowany specjalnie do karabinu automatycznego MDR kalibru 7,62 × 51 mm (.308). Wyprodukowany przez firmę Desert Tech."

L["eft_muzzle_ar10_procomp.printname"] = "Hamulec wylotowy do AR-10 SureFire ProComp 7,62 x 51"
L["eft_muzzle_ar10_procomp.compactname"] = "ProComp 762"
L["eft_muzzle_ar10_procomp.description"] = "Hamulec wylotowy ProComp zaprojektowany do instalacji na broni opartej o platformę AR-10 i kompatybilnych. Hamulec wylotowy znacznie ogranicza zarówno odrzut jak i podrzut lufy, tak, że broń wraca prosto do punktu pierwotnego, by łatwiej utrzymać cel i oddać kolejny strzał. Wyprodukowany przez firmę SureFire."

L["eft_muzzle_ar10_red.printname"] = "Hamulec wylotowy do AR-10 Fortis RED Brake 7,62 x 51"
L["eft_muzzle_ar10_red.compactname"] = "RED 762"
L["eft_muzzle_ar10_red.description"] = "Hamulec wylotowy RED Brake zaprojektowany do instalacji na systemach AR-10 i kompatybilnych. Wyprodukowany przez firmę Fortis."

L["eft_muzzle_ar10_atlas.printname"] = "Hamulec wylotowy do AR-10 Odin Works ATLAS-7 7,62 x 51"
L["eft_muzzle_ar10_atlas.compactname"] = "ATLAS-7"
L["eft_muzzle_ar10_atlas.description"] = "Hamulec wylotowy ATLAS-7 zaprojektowany do instalacji na systemach AR-10 i kompatybilnych. Wyprodukowany przez firmę Odin Works."

L["eft_muzzle_ar10_scarsd.printname"] = "Tłumik płomieni AAC SCAR-SD 51T 7,62 x 51"
L["eft_muzzle_ar10_scarsd.compactname"] = "SCAR-SD 762"
L["eft_muzzle_ar10_scarsd.description"] = "SCAR-SD 51T jest skutecznym tłumikiem płomieni, który służy również jako platforma do dołączania tłumika dźwięku 762-SDN-6. Może być instalowany na opartej na systemie AR-10. Wyprodukowany przez firmę Advanced Armament Corporation (AAC)."

L["eft_muzzle_ar10_shrewed.printname"] = "Hamulec wylotowy do AR-10 Keeno Arms SHREWD 7,62 x 51"
L["eft_muzzle_ar10_shrewed.compactname"] = "SHREWD 762"
L["eft_muzzle_ar10_shrewed.description"] = "Hamulec wylotowy SHREWD zaprojektowany do instalacji na systemach AR-10 i kompatybilnych. Wyprodukowany przez firmę Keeno Arms."

L["eft_silencer_ar10_prsqdc.printname"] = "Tłumik dźwięku KAC PRS/QDC 7,62 x 51"
L["eft_silencer_ar10_prsqdc.compactname"] = "PRS QDC"
L["eft_silencer_ar10_prsqdc.description"] = "Tłumik dźwięku PRS QDC, który może być instalowany na zestawie Flash Suppressor Kit lub Muzzle Brake Kit. Wyprodukowany przez firmę Knight’s Armament Company."

L["eft_muzzle_ar10_bmda3.printname"] = "Bezpośredni adapter gwintu tłumika płomienia Lantac BMD A3"
L["eft_muzzle_ar10_bmda3.compactname"] = "BMD A3"
L["eft_muzzle_ar10_bmda3.description"] = "Adapter A3 do instalacji na tłumiku płomieni BMD (Blast Mitigation Device). Wyprodukowany przez firmę Lantac."

L["eft_muzzle_bmd_bmd762.printname"] = "Tłumik płomieni Lantac BMD Blast Mitigation Device 7,62 x 51"
L["eft_muzzle_bmd_bmd762.compactname"] = "BMD 762"
L["eft_muzzle_bmd_bmd762.description"] = "Tłumik płomieni BMD (Blast Mitigation Device) to unikalna osłona przeciwbłyskowa, która pozwala na normalne funkcjonowanie hamulca wylotowego znajdującego się wewnątrz niego biorąc pod uwagę podrzut lufy. Instalowane na broni kalibru 7,62 × 51 mm. Wyprodukowane przez firmę Lantac."

L["eft_muzzle_deltatek_dtk_ar10.printname"] = "Hamulec wylotowy do AR-10 Delta-Tek DTK-AR10 7,62 x 51"
L["eft_muzzle_deltatek_dtk_ar10.compactname"] = "DTK-AR10"
L["eft_muzzle_deltatek_dtk_ar10.description"] = "Hamulec wylotowy DTK-AR10 zaprojektowany do broni opartej o platformę AR-10 i kompatybilnych. Dzięki specjalnej komorze kompensacyjnej znacznie zmniejsza odrzut i podrzut lufy. Wyprodukowany przez firmę Delta-Tek."

