L["eft_muzzle_psr_protector.printname"] = "Osłona hamulca wylotowego AWC PSR"
L["eft_muzzle_psr_protector.compactname"] = "Osł. PSR"
L["eft_muzzle_psr_protector.description"] = "Osłona gwintu do hamulca wylotowego PSR. Wyprodukowana przez firmę AWC Silencers."

L["eft_muzzle_ar15_onemount.printname"] = "Bezpośredni adapter gwintu Gemtech ONE"
