L["eft_muzzle_ar15_onemount.description"] = "Bezpośredni adapter gwintu do instalacji wielokalibrowego tłumika dźwięku Gemtech ONE bezpośrednio na gwincie lufy."

L["eft_silencer_gemtechone.printname"] = "Tłumik dźwięku Gemech ONE, wielokalibrowy"
L["eft_silencer_gemtechone.compactname"] = "ONE"
L["eft_silencer_gemtechone.description"] = "Tłumik dźwięku ONE zaprojektowany do użycia z nabojami kalibru 7,62 × 51 mm NATO, ale jest też kompatybilny z kilkoma innymi kalibrami. Wymaga montażu z bezpośrednim adapterem gwintu, by zainstalować. Wyprodukowany przez firmę Gemtech."

L["eft_silencer_waveqd.printname"] = "Tłumik dźwięku Daniel Defense WAVE QD"
L["eft_silencer_waveqd.compactname"] = "WAVE QD"
L["eft_silencer_waveqd.description"] = "Tłumik dźwięku WAVE QD (Quick Detach, z systemem szybkiego odłączania), który może być zainstalowany na kompatybilnych hamulcach wylotowych WAVE. Wyprodukowany przez firmę Daniel Defense."

L["eft_silencer_ultra5.printname"] = "Tłumik dźwięku Thunder Beast Arms Ultra 5"
L["eft_silencer_ultra5.compactname"] = "Ultra 5"
L["eft_silencer_ultra5.description"] = "Tłumik dźwięku Ultra 5 zaprojektowany do użytku z amunicją 7,62 × 51 mm NATO, lecz funkcjonuje również jako wielokalibrowy tłumik do różnych broni, zapewnia doskonałe osiągi przy 7,62 mm NATO, .300 AAC, 6,8 mm SPC, 6,5 mm oraz 5,56 mm NATO. Możliwy do zainstalowania tylko z kompatybilnym urządzeniem wylotowym zaprojektowanym przez Thunder Beast Arms."

L["eft_silencer_sdn6.printname"] = "Tłumik dźwięku AAC 762-SDN-6, wielokalibrowy"
L["eft_silencer_sdn6.compactname"] = "SDN-6"
L["eft_silencer_sdn6.description"] = "Tłumik dźwięku 762-SDN-6 zaprojektowany do użytku z amunicją 7,62 × 51 mm NATO, ale funkcjonuje również jako znakomity wielokalibrowy tłumik dźwięku do różnych broni, zapewniając doskonałe osiągi przy 7,62 mm NATO, .300 AAC, 6,8 mm SPC, 6,5 mm oraz 5,56 mm NATO. Może być instalowany na kompatybilnych urządzeniach 51T. Wyprodukowany przez firmę Advanced Armament Corporation (AAC)."

L["eft_muzzle_ar10_keymount.printname"] = "Hamulec wylotowy do AR-10 Dead Air Keymount 7,62 x 51"
L["eft_muzzle_ar10_keymount.compactname"] = "Keymount"
L["eft_muzzle_ar10_keymount.description"] = "Hamulec wylotowy zaprojektowany do instalacji na broni opartej o platformę AR-10 i kompatybilnych. Hamulec wylotowy znacznie ogranicza zarówno odrzut jak i podrzut lufy, tak, że broń wraca prosto do punktu pierwotnego, by łatwiej utrzymać cel i oddać kolejny strzał."

L["eft_muzzle_dthybrid.printname"] = "Bezpośredni adapter gwintu SilencerCo Hybrid 46"
L["eft_muzzle_dthybrid.compactname"] = "DT Hybrid"
L["eft_muzzle_dthybrid.description"] = "Bezpośredni adapter gwintu do instalacji tłumika dźwięku SilencerCo Hybrid 46 bezpośrednio na gwincie lufy."

L["eft_muzzle_pws_cqb.printname"] = "Hamulec wylotowy do AR-15 PWS CQB 5,56 x 45"
L["eft_muzzle_pws_cqb.compactname"] = "PWS CQB"
L["eft_muzzle_pws_cqb.description"] = "Hamulec wylotowy PWS CQB kalibru 5,56 × 45 mm zaprojektowany w szczególności do kompaktowych broni w użyciu w ograniczonej przestrzeni."

L["eft_silencer_dthybrid.printname"] = "Tłumik dźwięku SilencerCo Hybrid 46, wielokalibrowy"
L["eft_silencer_dthybrid.compactname"] = "Hybrid 46"
L["eft_silencer_dthybrid.description"] = "Wielokalibrowy tłumik dźwięku Hybrid 46 zaprojektowany do użytku z pistoletami kalibrów od 9 mm do .45 ACP i karabinków od 5,56 mm do .45. Wymagany odpowiedni adapter do zainstalowania. Wyprodukowany przez firmę SilencerCo."

L["eft_silencer_r43_556.printname"] = "Hamulec wylotowy Rotor 43 5,56 x 45"
L["eft_silencer_r43_556.compactname"] = "R43 5,56"
L["eft_silencer_r43_556.description"] = "Hamulec wylotowy Rotor 43, przeznaczony do instalacji na karabinkach AR lub AK kalibru 5,56 × 45 mm. Mimo że jest to hamulec wylotowy, działa również jako tłumik dźwięku."

L["eft_muzzle_ar10_sig_taperlok.printname"] = "Adapter wylotowy SIG Sauer Taper-LOK 7,62 x 51 / .300 BLK"
L["eft_muzzle_ar10_sig_taperlok.compactname"] = "T-LOK"
L["eft_muzzle_ar10_sig_taperlok.description"] = "Opatentowany system montażowy „Taper-LOK”, pozwala na instalację różnego rodzaju urządzeń wylotowych na broni kalibru 7,62 × 51 mm i .300 Blackout. Wyprodukowany przez firmę SIG Sauer."

L["eft_muzzle_tlok_3prong.printname"] = "Tłumik płomieni SIG Sauer 7,62 x 51, 3-bolcowy"
L["eft_muzzle_tlok_3prong.compactname"] = "3-bo 762"
L["eft_muzzle_tlok_3prong.description"] = "3-bolcowy tłumik płomieni, który może być zainstalowany na opatentowanym systemie montażowym Taper-LOK. Wyprodukowany przez firmę SIG Sauer."

L["eft_muzzle_tlok_mirco.printname"] = "Hamulec wylotowy SIG Sauer Micro Brake 7,62 x 51"
L["eft_muzzle_tlok_mirco.compactname"] = "Micro 762"
L["eft_muzzle_tlok_mirco.description"] = "Tłumik płomieni Micro Brake, który może być zainstalowany na opatentowanym systemie montażowym Taper-LOK. Wyprodukowany przez firmę SIG Sauer."

L["eft_muzzle_tlok_tpb.printname"] = "Hamulec wylotowy SIG Sauer Two Port Brake 7,62 x 51"
L["eft_muzzle_tlok_tpb.compactname"] = "TPB 762"
L["eft_muzzle_tlok_tpb.description"] = "Tłumik płomieni Two Port Brake, który może być zainstalowany na opatentowanym systemie montażowym Taper-LOK. Wyprodukowany przez firmę SIG Sauer."

L["eft_silencer_ar10_tlok_srd.printname"] = "Tłumik dźwięku SIG Sauer SRD762-QD 7,62 x 51"
L["eft_silencer_ar10_tlok_srd.compactname"] = "SRD762-QD"
L["eft_silencer_ar10_tlok_srd.description"] = "Tłumik dźwięku SRD762-QD z tytanu Grade 5, który może być zainstalowany na opatentowanym systemie montażowym Taper-LOK. Wyprodukowany przez firmę SIG Sauer."

L["eft_muzzle_ar10_qdc_sup.printname"] = "Tłumik płomieni do AR-10 KAC QDC Flash Suppressor Kit 7,62 x 51"
L["eft_muzzle_ar10_qdc_sup.compactname"] = "KAC QDC"
L["eft_muzzle_ar10_qdc_sup.description"] = "Flash Suppressor Kit jest skutecznym tłumikiem płomieni, który służy również jako platforma do dołączania tłumika dźwięku PRS QDC. Może być instalowany na broni kompatybilnej z AR-10. Wyprodukowany przez firmę Knight’s Armament Company."

L["eft_silencer_ar10_srdti.printname"] = "Tłumik dźwięku SIG Sauer SRD762Ti 7,62 x 51"
L["eft_silencer_ar10_srdti.compactname"] = "SRD762"
L["eft_silencer_ar10_srdti.description"] = "Tłumik dźwięku SRD762Ti z tytanu Grade 5 zaprojektowany do użytku z lufami kalibrów .308/7,62/300 BLK. Wyprodukowany przez firmę SIG Sauer."

L["eft_muzzle_deltatek_dtk_ar15.printname"] = "Hamulec wylotowy do AR-15 Delta-Tek DTK-M16 5,56 x 45"
L["eft_muzzle_deltatek_dtk_ar15.compactname"] = "DTK-M16"
L["eft_muzzle_deltatek_dtk_ar15.description"] = "Hamulec wylotowy DTK-M16 zaprojektowany do broni opartej o platformę AR-15 i kompatybilnych. Dzięki specjalnej komorze kompensacyjnej znacznie zmniejsza odrzut i podrzut lufy. Wyprodukowany przez firmę Delta-Tek."

L["eft_muzzle_carlson_ar15.printname"] = "Hamulec wylotowy do AR-15 DoubleStar Carlson Tac Comp 5,56 x 45"
L["eft_muzzle_carlson_ar15.compactname"] = "CTC"
L["eft_muzzle_carlson_ar15.description"] = "Hamulec wylotowy Carlson Tac Comp zaprojektowany do broni opartej o platformę AR-15 i kompatybilnych. Zmniejsza odrzut i podrzut lufy. Idealny do broni z krótką lufą. Wyprodukowany przez firmę DoubleStar."

-- [[ optic ]]
L["eft_optic_1p78_eyecap.printname"] = "Muszla oczna do celownika NPZ 1P78"
L["eft_optic_1p78_eyecap.compactname"] = "Muszla 1P78"
L["eft_optic_1p78_eyecap.description"] = "Gumowa muszla oczna do celownika NPZ 1P78."

L["eft_optic_553.printname"] = "Celownik holograficzny EOTech 553"
L["eft_optic_553.compactname"] = "553"
L["eft_optic_553.description"] = "Celownik holograficzny EOTech 553. Dzięki swojej konstrukcji może być użyty na wielu rodzajach broni, nawet z bronią posiadającą stałe przyrządy celownicze. Kompatybilny z noktowizorami dzięki trybowi noktowizyjnemu siatki celowniczej. Używany przez US SOCOM jako podstawowy celownik do działań w ciasnych przestrzeniach."

L["eft_optic_acrop1.printname"] = "Celownik kolimatorowy Aimpoint ACRO P-1"
L["eft_optic_acrop1.compactname"] = "ACRO P-1"
L["eft_optic_acrop1.description"] = "Kompaktowy celownik kolimatorowy ACRO P-1 został zaprojektowany do użycia ze specjalnymi montażami. Lekki, kompaktowy i wytrzymały. Wyprodukowany przez firmę Aimpoint."

L["eft_optic_compm4.printname"] = "Celownik kolimatorowy Aimpoint CompM4"
L["eft_optic_compm4.compactname"] = "CompM4"
L["eft_optic_compm4.description"] = "Seria kolimatorów Aimpoint CompM4 to najtrwalsze celowniki wyprodukowane przez Aimpoint i są standardowym przyrządem celowniczym dla wojsk lądowych wielu krajów NATO. Te celowniki są naprawdę odporne i potrafią działać do 8 lat na pojedynczej baterii AA. Aimpoint CompM4s to najnowsza wersja celownika M68 CCO (Close-Combat Optic) używanego w siłach lądowych USA i kontynuującego współpracę trwającą nieprzerwanie od 1997 roku."

L["eft_optic_deltapoint.printname"] = "Celownik kolimatorowy Leupold DeltaPoint"
L["eft_optic_deltapoint.compactname"] = "DP"
L["eft_optic_deltapoint.description"] = "Kompaktowy celownik kolimatorowy DeltaPoint firmy Leupold praktycznie nie ma ograniczeń w zakresie zastosowań. Początkowo przeznaczony do używania na strzelbach powtarzalnych w zawodach strzeleckich i w trakcie ćwiczeń, zdobył uznanie dzięki wysokiej efektywności z szeroką gamą uzbrojenia. Od strzelb i pistoletów po karabinki oparte o system AR, jako główny lub pomocniczy celownik będzie prezentował doskonałe wyniki."

L["eft_optic_exps3_tan.printname"] = "Celownik holograficzny EOTech EXPS3 (tan)"
L["eft_optic_exps3_tan.compactname"] = "EXPS3"
L["eft_optic_exps3_tan.description"] = "Celownik holograficzny EOTech EXPS3. Dzięki swojej konstrukcji jest kompatybilny z wieloma rodzajami broni, nawet z modelami o stałych przyrządach celowniczych. Kompatybilny z noktowizorami dzięki trybowi noktowizyjnemu siatki celowniczej. Używany przez US SOCOM jako celownik do działań w ciasnych przestrzeniach."

L["eft_optic_ff3.printname"] = "Celownik kolimatorowy Burris FastFire 3"
L["eft_optic_ff3.compactname"] = "FF3"
L["eft_optic_ff3.description"] = "Lekki i wytrzymały, celownik kolimatorowy z serii FastFire oznacza cel jednoznaczną, jasną czerwoną kropką, dając ci przewagę szybkości i celności. Może być użyty jako główny celownik, albo zamontowany na górze istniejącego."

L["eft_optic_h2.printname"] = "Celownik kolimatorowy Aimpoint Micro H-2"
L["eft_optic_h2.compactname"] = "H-2"
L["eft_optic_h2.description"] = "Kompaktowy celownik kolimatorowy Micro H-2 firmy Aimpoint został zaprojektowany do użytku z każdego rodzaju bronią palną, a nawet z łukami. Lekki, kompaktowy i wytrzymały."

L["eft_optic_hhs1.printname"] = "Celownik hybrydowy EOTech HHS-1"
L["eft_optic_hhs1.compactname"] = "HHS-1"
L["eft_optic_hhs1.description"] = "Hybrydowy celownik holograficzny HHS-1 (Holographic Hybrid Sight 1) zawiera celownik holograficzny EXPS3-4 wraz z powiększeniem G33.STS. Wyprodukowany przez firmę EOTech."

L["eft_optic_hhs1_tan.printname"] = "Celownik hybrydowy EOTech HHS-1 (tan)"
L["eft_optic_hhs1_tan.compactname"] = "HHS-1 Tan"
L["eft_optic_hhs1_tan.description"] = "Hybrydowy celownik holograficzny HHS-1 (Holographic Hybrid Sight 1) zawiera celownik holograficzny EXPS3-4 wraz z powiększeniem G33.STS. Wyprodukowany przez firmę EOTech. Wersja tan."

L["eft_optic_hs401g5.printname"] = "Celownik kolimatorowy Holosun HS401G5"
L["eft_optic_hs401g5.compactname"] = "HS401G5"
L["eft_optic_hs401g5.description"] = "Celownik kolimatorowy Holosun HS401G5 z wbudowanym wskaźnikiem laserowym wyprodukowany przez firmę Holosun."

L["eft_optic_kmz_eyecap.printname"] = "Muszla oczna do lunety KMZ 1P59"
L["eft_optic_kmz_eyecap.compactname"] = "Muszla 1P59"
L["eft_optic_kmz_eyecap.description"] = "Gumowa muszla oczna do celownika KMZ 1P59."

L["eft_optic_kobra.printname"] = "Celownik kolimatorowy Axion Kobra EKP-8-18"
L["eft_optic_kobra.compactname"] = "EKP-8-18"
L["eft_optic_kobra.description"] = "Koba to bardzo popularny wśród cywili i agencji ochrony celownik kolimatorowy. Został zaprojektowany dla sił zbrojnych Federacji Rosyjskiej, ale nigdy nie został formalnie przyjęty. Wyprodukowany przez firmę Axion."

L["eft_optic_krechet.printname"] = "Celownik kolimatorowy Valday „Krieczet”"
L["eft_optic_krechet.compactname"] = "Krieczet"
L["eft_optic_krechet.description"] = "Celownik kolimatorowy Krieczet zaprojektowany do precyzyjnego celowania w dzień i w nocy w połączeniu z noktowizorem."

L["eft_optic_mrs.printname"] = "Celownik kolimatorowy Walther MRS"
L["eft_optic_mrs.compactname"] = "MRS"
L["eft_optic_mrs.description"] = "Celownik kolimatorowy MRS (Multi-Reticle Sight) firmy Walther zawiera 2 kropki i 2 siatki celownicze z możliwością dostosowania poziomu jasności. Tani, celny (choć musi być zerowany za każdym razem, gdy zmieniany jest typ siatki), kompaktowy i z wytrzymałą baterią – ale o niskim współczynniku trwałości i niezawodności."

L["eft_optic_nspu_eyecap.printname"] = "Muszla oczna do lunety NSPU-M"
L["eft_optic_nspu_eyecap.compactname"] = "Muszla NSPU-M"
L["eft_optic_nspu_eyecap.description"] = "Gumowa muszla oczna do lunety nocnej NSPU-M."

L["eft_optic_okp7.printname"] = "Celownik kolimatorowy OKP-7"
L["eft_optic_okp7.compactname"] = "OKP-7"
L["eft_optic_okp7.description"] = "Podstawowa wersja celownika OKP umożliwia prowadzenie ognia z niestabilnej pozycji i w nagle zmieniających się warunkach. Zmniejsza zmęczenie strzelca oraz umożliwia korzystanie z celownika mechanicznego. Główną cechą tego celownika jest zwierciadło bez obudowy, które nie blokuje pola widzenia. Szkło chronione jest natomiast przez niewielkiej grubości osłonę, która w czasie strzelania jest niemal niewidoczna."

L["eft_optic_p1x42.printname"] = "Celownik kolimatorowy VOMZ Pilad P1x42 Weaver"
L["eft_optic_p1x42.compactname"] = "P1x42"
L["eft_optic_p1x42.description"] = "Otwarty celownik kolimatorowy zaprojektowany do precyzyjnego celowania bronią łowiecką i sportową do różnorodnych celów, także do tych szybko się poruszających. Wyprodukowany przez firmę VOMZ."

L["eft_optic_pk06.printname"] = "Celownik kolimatorowy BelOMO PK-06"
L["eft_optic_pk06.compactname"] = "PK-06"
L["eft_optic_pk06.description"] = "Współczesny otwarty celownik kolimatorowy z automatyczną regulacją jasności i przełączaniem pomiędzy trzema typami siatki celownika. Wyprodukowany przez firmę Zenit-BelOMO."

L["eft_optic_pk120.printname"] = "Celownik holograficzny Valday PK-120 (1P87)"
L["eft_optic_pk120.compactname"] = "PK-120"
L["eft_optic_pk120.description"] = "Celownik holograficzny PK-120 (indeks GRAU 1P87) jest częścią zestawu DPNK (zestaw widokowo-obserwacyjny dzienno-nocny) eksperymentalnego programu „Ratnik”."

L["eft_optic_pro.printname"] = "Celownik kolimatorowy Aimpoint PRO"
L["eft_optic_pro.compactname"] = "PRO"
L["eft_optic_pro.description"] = "Celownik kolimatorowy PRO (Patrol Rifle Optic) instalowany na montażu QRP2 lub LRP, który montuje się do szyny Picatinny bez żadnych dodatkowych narzędzi. Celownik ma osłony typu flip-up – lity przód i przezroczysty tył – dzięki czemu w sytuacji awaryjnej użytkownik może strzelać z zamkniętymi osłonami soczewek i z otwartym obojgiem oczu. Celownik PRO jest w pełni kompatybilny ze wszystkimi generacjami noktowizorów. Wyprodukowany przez firmę Aimpoint."

L["eft_optic_pso_eyecap.printname"] = "Muszla oczna do lunety PSO"
L["eft_optic_pso_eyecap.compactname"] = "Muszla PSO"
L["eft_optic_pso_eyecap.description"] = "Gumowa muszla oczna do lunet z rodziny PSO."

L["eft_optic_reapir_eyecup.printname"] = "Muszla oczna do celownika Trijicon REAP-IR"
L["eft_optic_reapir_eyecup.compactname"] = "Muszla REAP-IR"
L["eft_optic_reapir_eyecup.description"] = "Gumowa muszla oczna do celownika termicznego Trijicon REAP-IR."

L["eft_optic_rmr.printname"] = "Celownik kolimatorowy Trijicon RMR"
L["eft_optic_rmr.compactname"] = "RMR"
L["eft_optic_rmr.description"] = "RMR (Ruggedized Miniature Reflex) to wytrzymały i lekki kompaktowy celownik kolimatorowy. Może być używany jako główny, albo zapasowy. Wymaga konkretnego montażu do instalacji na szynach Weaver. Wyprodukowany przez firmę Trijcon."

L["eft_optic_romeo4.printname"] = "Celownik kolimatorowy SIG Sauer ROMEO4"
L["eft_optic_romeo4.compactname"] = "ROMEO4"
L["eft_optic_romeo4.description"] = "Celownik kolimatorowy ROMEO4 do szybkiego i celnego namierzania celu. Wyprodukowany przez firmę SIG Sauer."

L["eft_optic_romeo8t.printname"] = "Celownik kolimatorowy SIG Sauer ROMEO8T"
L["eft_optic_romeo8t.compactname"] = "ROMEO8T"
L["eft_optic_romeo8t.description"] = "Celownik kolimatorowy ROMEO8T zaprojektowany do precyzyjnego strzelania współczesnymi sportowymi karabinami i karabinkami oraz strzelbami. Wyprodukowany przez firmę SIG Sauer."

L["eft_optic_srs02.printname"] = "Celownik kolimatorowy Trijicon SRS-02"
L["eft_optic_srs02.compactname"] = "SRS-02"
L["eft_optic_srs02.description"] = "Celownik kolimatorowy SRS (Sealed Reflex Sight) jest kompaktowy i zajmuje mniej miejsca na szynie montażowej, zachowując przy tym trwałość i wydajność. Duża 38-milimetrowa przesłona pozwala na szerokie pole widzenia i szybkie namierzanie celów. Trijicon SRS Sealed Reflex Sight posiada kropkę na 1,75 MOA i jest zasilany przez ogniwo słoneczne i jedną baterię AA. Wyprodukowany przez firmę Trijicon."

L["eft_optic_t1.printname"] = "Celownik kolimatorowy Aimpoint Micro T-1"
L["eft_optic_t1.compactname"] = "T-1"
L["eft_optic_t1.description"] = "Kompaktowy celownik kolimatorowy Micro T-1 firmy Aimpoint został zaprojektowany do użytku z każdego rodzaju bronią palną, a nawet z łukami. Lekki, kompaktowy i wytrzymały."

L["eft_optic_t12w.printname"] = "Termiczny celownik kolimatorowy 30 Hz Torrey Pines Logic T12W"
L["eft_optic_t12w.compactname"] = "T12W"
L["eft_optic_t12w.description"] = "Kompaktowy termiczny celownik kolimatorowy o niskiej częstotliwości. Wyprodukowany przez firmę Torrey Pines Logic."

L["eft_optic_tulpan_eyecap.printname"] = "Muszla oczna do celownika NPZ USP-1"
L["eft_optic_tulpan_eyecap.compactname"] = "Muszla USP-1"
L["eft_optic_tulpan_eyecap.description"] = "Gumowa muszla oczna do celownika NPZ USP-1."

L["eft_optic_uh1.printname"] = "Celownik holograficzny Vortex Razor AMG UH-1"
L["eft_optic_uh1.compactname"] = "UH-1"
L["eft_optic_uh1.description"] = "Celownik holograficzny Vortex Razor AMG UH-1 to nie tylko futurystyczny design, ale według jego twórców również i rewolucja wśród celowników holograficznych i kolimatorowych."

L["eft_optic_utg.printname"] = "Celownik kolimatorowy Leapers UTG"
L["eft_optic_utg.compactname"] = "UTG"
L["eft_optic_utg.description"] = "Celownik kolimatorowy UTG z czerwonym/zielonym kółkiem celowniczym. Ten model to kompaktowy mały kolimator, który montuje się na szynach Weaver/Picatinny."

L["eft_optic_vulcan_eyecup.printname"] = "Muszla oczna do lunety Armasight Vulcan MG"
L["eft_optic_vulcan_eyecup.compactname"] = "Vulcan MG"
L["eft_optic_vulcan_eyecup.description"] = "Gumowa muszla oczna do lunety Vulcan MG. Wyprodukowana przez Armasight."

L["eft_optic_xps3_0.printname"] = "Celownik holograficzny EOTech XPS3-0"
L["eft_optic_xps3_0.compactname"] = "XPS3-0"
L["eft_optic_xps3_0.description"] = "XPS3 jest tak samo skuteczny jak inne celowniki holograficzne, ale jest mniejszy. Dzięki temu uzyskuje się więcej miejsca na dodatkowy osprzęt. Zarówno myśliwi jak i operatorzy sił specjalnych cenią sobie jego niewielki rozmiar oraz wagę, które są znaczące w trakcie długich misji. Model 3-0 oferuje siatkę celowniczą z jedną kropką na 1 MOA."

L["eft_optic_xps3_2.printname"] = "Celownik holograficzny EOTech XPS3-2"
L["eft_optic_xps3_2.compactname"] = "XPS3-2"
L["eft_optic_xps3_2.description"] = "XPS3 jest tak samo skuteczny jak inne celowniki holograficzne, ale jest mniejszy. Dzięki temu uzyskuje się więcej miejsca na dodatkowy osprzęt. Zarówno myśliwi jak i operatorzy sił specjalnych cenią sobie jego niewielki rozmiar oraz wagę, które są znaczące w trakcie długich misji. Model 3-2 oferuje siatkę celowniczą z dwoma kropkami na 1 MOA."

L["eft_aux_ekp_shade.printname"] = "Osłona przeciwsłoneczna celownika Axion Kobra"
L["eft_aux_ekp_shade.compactname"] = "Kobra"
L["eft_aux_ekp_shade.description"] = "Osłona przeciwsłoneczna do celowników kolimatorowych z rodziny Kobra. Ochrania soczewki przed mechanicznymi uszkodzeniami i ogranicza efekt flary. Wyprodukowany przez firmę Axion."

L["eft_optic_targetring.printname"] = "Celownik kolimatorowy VOMZ Pilad TargetRing"
L["eft_optic_targetring.compactname"] = "TargetRing"
L["eft_optic_targetring.description"] = "Przeznaczony do polowań na ptaki lub drobną zwierzynę, do strzelania sportowego do celów stacjonarnych lub szybko się poruszających. Idealny do tego celu, tworzy kontrastowy okrąg na celu, dając strzelcowi możliwość wykonania przedmiaru i nietracenia prędkości. Montowany jest wyłącznie na broni gładkolufowej (strzelby)."

L["eft_optic_elcan_hco.printname"] = "Celownik holograficzny ELCAN Specter HCO"
L["eft_optic_elcan_hco.compactname"] = "HCO"
L["eft_optic_elcan_hco.description"] = "Celownik holograficzny Specter Holographic Combat Optic. Wyprodukowany przez firmę ELCAN."

L["eft_optic_sig_romeo7.printname"] = "Celownik kolimatorowy SIG Sauer ROMEO7 1x30 (prototyp)"
L["eft_optic_sig_romeo7.compactname"] = "ROMEO7"
