L["eft_scope_25mm_pilad.compactname"] = "Pilad 4x32"
L["eft_scope_25mm_pilad.description"] = "Zaprojektowana do strzelania na małe i średnie odległości, ta luneta będzie praktyczna przy strzelaniu do ruchomych obiektów na dystansie do 150 metrów."

L["eft_scope_30mm_march.printname"] = "Luneta March Tactical 3-24x42 FFP 30 mm"
L["eft_scope_30mm_march.compactname"] = "3-24x42 FFP"
L["eft_scope_30mm_march.description"] = "Luneta March Tactical 3-24x42 FFP jest niezawodna i dokładna z amunicją o każdej mocy."

L["eft_scope_30mm_mark4.printname"] = "Luneta Leupold Mark 4 LR 6,5-20x50 30 mm"
L["eft_scope_30mm_mark4.compactname"] = "6,5-20x50"
L["eft_scope_30mm_mark4.description"] = "Precyzyjna, dokładna, trwała i niezawodna – luneta Leupold Mark 4 6,5-20x50. Model ten zapewnia powiększenie 6,5—20× dając wyraźny obraz namierzanego obiektu rzeczywistych rozmiarów."

L["eft_scope_30mm_razor.printname"] = "Luneta Vortex Razor HD Gen.2 1-6x24"
L["eft_scope_30mm_razor.compactname"] = "Razor HD Gen.2"
L["eft_scope_30mm_razor.description"] = "Taktyczna luneta zaprojektowana do szybkiego namierzania celów i maksymalnej precyzji. Wyprodukowana przez firmę Vortex Optics."

L["eft_scope_30mm_sb_pmii1.printname"] = "Luneta Schmidt & Bender PM II 1-8x24"
L["eft_scope_30mm_sb_pmii1.compactname"] = "PM II 1-8x24"
L["eft_scope_30mm_sb_pmii1.description"] = "Luneta Schmidt & Bender PM II 1-8x24 została pierwotnie stworzona dla elitarnych sił wojskowych w celu zapewnienia szybkiego namierzania celów i bardzo wysokiej celności."

L["eft_scope_30mm_tac30.printname"] = "Luneta Burris FullField TAC30 1-4x24 30 mm"
L["eft_scope_30mm_tac30.compactname"] = "TAC30"
L["eft_scope_30mm_tac30.description"] = "TAC30 oferuje bardzo szerokie pole widzenia i brak powiększenia w dolnej skali, co pozwala na łatwe i szybkie rozpoznanie celu. Pełne powiększenie 4× i siatka Ballistic CQ sprawiają, że strzały do około 300 metrów są bardzo praktyczne. Podświetlany punkt centralny i duży, jasny okrąg pozwalają na błyskawiczne namierzanie celów na bliskich dystansach."

L["eft_scope_30mm_vudu.printname"] = "Luneta EOTech Vudu 1-6x24 30 mm"
L["eft_scope_30mm_vudu.compactname"] = "Vudu 1-6x24"
L["eft_scope_30mm_vudu.description"] = "Luneta EOTech Vudu 1-6x24, równie skuteczna na platformach AR i karabinach powtarzalnych. Siatka celownica w stylu EOtech została zaprojektowana do szybkiego namierzania celów przy niskiej mocy powiększenia, jednocześnie zapewniając rozdzielczość i precyzję potrzebną przy celach o większym zasięgu."

L["eft_scope_34mm_atacr.printname"] = "Luneta Nightforce ATACR 7-35x56 34 mm"
L["eft_scope_34mm_atacr.compactname"] = "ATACR 7-35x56"
L["eft_scope_34mm_atacr.description"] = "Wszechstronna luneta ATACR 7-35x56 o obszernym zakresie powiększenia i szerokim polu widzenia w całym zakresie, co ułatwia dostrzeżenie i zaangażowanie celów. Wyprodukowana przez firmę Nightforce."

L["eft_scope_34mm_hsff.printname"] = "Luneta Hensoldt FF 4-16x56 34 mm"
L["eft_scope_34mm_hsff.compactname"] = "FF 4-16"
L["eft_scope_34mm_hsff.description"] = "Celownik optyczny Hensoldt FF 4-16×56 został opracowany w celu zapewnienia maksymalnej wydajności i wysokich zakresów regulacji przy zachowaniu krótkiego układu optycznego. Dzięki zwiększonej przezroczystości w niebieskim zakresie widmowym i regulowanemu światłu siatkowemu jest szczególnie wygodny w warunkach słabego oświetlenia. Wersja FF lunety posiada siatkę umieszczoną w pierwszej płaszczyźnie obrazu, a zatem wzór siatki jest również powiększany, gdy powiększenie jest zmieniane. Umożliwia to określenie odległości za pomocą MIL-DOT bez konieczności używania obliczeń."

L["eft_scope_34mm_sb_pmii3.printname"] = "Luneta Schmidt & Bender PM II 3-12x50 34 mm"
L["eft_scope_34mm_sb_pmii3.compactname"] = "PM II 3-20x50"
L["eft_scope_34mm_sb_pmii3.description"] = "Luneta Schmidt & Bender PM II 3-20x50 została pierwotnie stworzona dla elitarnych sił wojskowych w celu zapewnienia szybkiego namierzania celów i bardzo wysokiej celności."

L["eft_scope_34mm_sb_pmii5.printname"] = "Luneta Schmidt & Bender PM II 5-25x56"
L["eft_scope_34mm_sb_pmii5.compactname"] = "PM II 5-25x56"
L["eft_scope_34mm_sb_pmii5.description"] = "Luneta Schmidt & Bender PM II 5-25x56 została pierwotnie stworzona dla elitarnych sił wojskowych w celu zapewnienia szybkiego namierzania celów i bardzo wysokiej celności."

L["eft_scope_acog_ta01.printname"] = "Luneta Trijicon ACOG TA01NSN 4x32 (czarna)"
L["eft_scope_acog_ta01.compactname"] = "TA01NSN"
L["eft_scope_acog_ta01.description"] = "Zaprojektowany zgodnie z dokładną specyfikacją Dowództwa Operacji Specjalnych USA do użytku przez elitarne siły amerykańskie, Trijicon ACOG TA01NSN jest najpopularniejszą lunetą ACOG firmy Trijicon. Siatka celownicza pozwala na kompensację spadku pocisku do 600 metrów bez konieczności ręcznej regulacji. Można zamontować dodatkowe tylne celowniki zapasowe. Nie wymaga baterii. Wersja czarna."

L["eft_scope_acog_ta01_tan.printname"] = "Luneta Trijicon ACOG TA01NSN 4x32 (tan)"
L["eft_scope_acog_ta01_tan.compactname"] = "TA01NSN"
L["eft_scope_acog_ta01_tan.description"] = "Zaprojektowany zgodnie z dokładną specyfikacją Dowództwa Operacji Specjalnych USA do użytku przez elitarne siły amerykańskie, Trijicon ACOG TA01NSN jest najpopularniejszą lunetą ACOG firmy Trijicon. Siatka celownicza pozwala na kompensację spadku pocisku do 600 metrów bez konieczności ręcznej regulacji. Można zamontować dodatkowe tylne celowniki zapasowe. Nie wymaga baterii. Wersja tan."

L["eft_scope_acog_ta11d.printname"] = "Luneta Trijicon ACOG 3,5x35"
L["eft_scope_acog_ta11d.compactname"] = "TA11D"
L["eft_scope_acog_ta11d.description"] = "Luneta ACOG 3,5x35. Nadal pozostaje ulubiona wśród strzelców wyborowych, stróżów prawa i kochających wolność cywilów z całego świata. Wyprodukowana przez firmę Trijicon."

L["eft_scope_adop4.printname"] = "Luneta NcSTAR ADO P4 Sniper 3-9x42"
L["eft_scope_adop4.compactname"] = "ADO P4"
L["eft_scope_adop4.description"] = "Luneta Advance Dual Optic (ADO) ze zmiennym powiększeniem 3–9× z obiektywem 42 mm. Luneta ADO posiada zitegrowany celownik kolimatorowy typu „red dot” na górze celownika. Wyprodukowany przez firmę NcSTAR."

L["eft_scope_bravo4.printname"] = "Luneta SIG Sauer BRAVO4 4x30"
L["eft_scope_bravo4.compactname"] = "BRAVO4"
L["eft_scope_bravo4.description"] = "Zaprojektowana przez firmę SIG Sauer luneta BRAVO 4x30 posiada wyjątkowo duże pole widzenia, o 43% szersze od konkurencji, oraz dodatkową szynę montażową u góry, która pozwala na montaż dodatkowego, kompaktowego celownika."

L["eft_scope_compactprism.printname"] = "Celownik pryzmatyczny Monstrum Compact 2x32"
L["eft_scope_compactprism.compactname"] = "Compact 2x32"
L["eft_scope_compactprism.description"] = "Kompaktowy celownik pryzmatyczny z 2-krotnym przybliżeniem. Wyprodukowany przez firmę Monstrum Tactical."

L["eft_scope_dovetail_1p78.printname"] = "Luneta NPZ 1P78-1 2,8x"
L["eft_scope_dovetail_1p78.compactname"] = "1P78-1"
L["eft_scope_dovetail_1p78.description"] = "Luneta dzienna z 2,8-krotnym powiększeniem, instalowana na szynie typu jaskółczy ogon. Wyprodukowana przez firmę NPZ."

L["eft_scope_dovetail_ekp802.printname"] = "Celownik kolimatorowy Axion Kobra EKP-8-02 (jaskółczy ogon)"
L["eft_scope_dovetail_ekp802.compactname"] = "EKP-8-02"
L["eft_scope_dovetail_ekp802.description"] = "Koba to bardzo popularny wśród cywili i agencji ochrony celownik kolimatorowy. Został zaprojektowany dla sił zbrojnych Federacji Rosyjskiej, ale nigdy nie został formalnie przyjęty. Tę wersję zaprojektowano do instalacji na szynie typu jaskółczy ogon. Wyprodukowany przez firmę Axion."

L["eft_scope_dovetail_nspum.printname"] = "Luneta noktowizyjna NSPU-M 3,5x na jaskółczy ogon"
L["eft_scope_dovetail_nspum.compactname"] = "NSPU-M"
L["eft_scope_dovetail_nspum.description"] = "NSPU-M (Nocznoj Striełkowyj Priceł Unificyrowannyj Modiernizirowannyj – „luneta noktowizyjna zunifikowana ulepszona”), indeks GRAU 1PN58. Stara radziecka luneta termowizyjna do karabinków z serii AK, zaprojektowana, by pasować na montaż typu jaskółczy ogon."

L["eft_scope_dovetail_okp7.printname"] = "Celownik kolimatorowy OKP-7 (jaskółczy ogon)"
L["eft_scope_dovetail_okp7.compactname"] = "OKP-7 JO"
L["eft_scope_dovetail_okp7.description"] = "Podstawowa wersja celownika OKP umożliwia prowadzenie ognia z niestabilnej pozycji i w nagle zmieniających się warunkach. Zmniejsza zmęczenie strzelca oraz umożliwia korzystanie z celownika mechanicznego. Główną cechą tego celownika jest zwierciadło bez obudowy, które nie blokuje pola widzenia. Szkło chronione jest natomiast przez niewielkiej grubości osłonę, która w czasie strzelania jest niemal niewidoczna. Ten model instaluje się na szynie typu jaskółczy ogon."

L["eft_scope_dovetail_pk1.printname"] = "Celownik kolimatorowy NPZ PK1 „Obzor” na jaskółczy ogon"
L["eft_scope_dovetail_pk1.compactname"] = "„Obzor”"
L["eft_scope_dovetail_pk1.description"] = "Celownik kolimatorowy zaprojektowany dla myśliwych do szybkiego namierzania celu podczas pracy w bardzo niskich temperaturach, montowany na szynach typu jaskółczy ogon. Wyprodukowany przez firmę NPZ."

L["eft_scope_dovetail_pkaa.printname"] = "Celownik kolimatorowy BelOMO PK-AA na jaskółczy ogon"
L["eft_scope_dovetail_pkaa.compactname"] = "PK-AA"
L["eft_scope_dovetail_pkaa.description"] = "Celownik kolimatorowy PK-11 zaprojektowany, by pasować na każdy karabinek z serii AK z szyną typu jaskółczy ogon. Wyprodukowany przez firmę BelOMO."

L["eft_scope_dovetail_pso1.printname"] = "Luneta BelOMO PSO-1 4x24"
L["eft_scope_dovetail_pso1.compactname"] = "PSO-1"
L["eft_scope_dovetail_pso1.description"] = "Wojskowej klasy luneta celownicza PSO-1. Ten celownik optyczny został zaprojektowany do namierzania różnych celów z karabinu wyborowego Dragunowa (SWD). Posiada 4-krotne przybliżenie i sześciostopniowy kąt widzenia. Wyprodukowana przez firmę BelOMO."

L["eft_scope_dovetail_pso1m2.printname"] = "Luneta BelOMO PSO-1M2-1 4x24"
L["eft_scope_dovetail_pso1m2.compactname"] = "PSO 1M 2-1"
L["eft_scope_dovetail_pso1m2.description"] = "Wojskowej klasy luneta celownicza PSO-1M2-1. Ten celownik optyczny został zaprojektowany do precyzyjnego oddawania strzałów z karabinka wyborowego Wintoriez (WSS) oraz karabinu wyborowego Dragunowa (SWD). Posiada 4-krotne przybliżenie i sześciostopniowy kąt widzenia. Wyprodukowana przez firmę BelOMO."

L["eft_scope_dovetail_pso1m2non1.printname"] = "Luneta BelOMO PSO-1M2 4x24"
L["eft_scope_dovetail_pso1m2non1.compactname"] = "PSO-1M2"
L["eft_scope_dovetail_pso1m2non1.description"] = "Wojskowej klasy luneta celownicza PSO-1M2. Ten celownik optyczny został zaprojektowany do precyzyjnego oddawania strzałów z karabinu wyborowego Dragunowa (SWD). Posiada 4-krotne przybliżenie i sześciostopniowy kąt widzenia. Wyprodukowana przez firmę BelOMO."

L["eft_scope_dovetail_tulpan.printname"] = "Celownik NPZ USP-1 „Tulipan” 4x"
L["eft_scope_dovetail_tulpan.compactname"] = "USP-1"
L["eft_scope_dovetail_tulpan.description"] = "Zunifikowany celownik USP-1 „Tulipan” jest zaprojektowany, aby zapewnić precyzyjne prowadzenie ognia z karabinków automatycznych AK-74N, AK-74M, AN-94 oraz ciężkich karabinów maszynowych RPK-74N i PKMN w dzień i w nocy na podświetlone cele. Instalowany na szynie typu jaskółczy ogon."

L["eft_scope_elcan.printname"] = "Luneta ELCAN SpecterDR 1x/4x"
L["eft_scope_elcan.compactname"] = "SpecterDR"
L["eft_scope_elcan.description"] = "Luneta SpecterDR 1×/4× (Dual Role) była przełomem w dziedzinie celowników optycznych, stając się pierwszym celownikiem z poprawnie działającymi dwoma trybami pracy, gdzie zmiana z powiększenia 4-krotnego do 1-krotnego wymaga tylko jednego ruchu. Posiada również zapasowy celownik mechaniczny. Wyprodukowana przez firmę ELCAN."

L["eft_scope_elcan_fde.printname"] = "Luneta ELCAN SpecterDR 1x/4x (FDE)"
L["eft_scope_elcan_fde.compactname"] = "SpecterDR"
L["eft_scope_elcan_fde.description"] = "Luneta SpecterDR 1×/4× (Dual Role) była przełomem w dziedzinie celowników optycznych, stając się pierwszym celownikiem z poprawnie działającymi dwoma trybami pracy, gdzie zmiana z powiększenia 4-krotnego do 1-krotnego wymaga tylko jednego ruchu. Posiada również zapasowy celownik mechaniczny. Wyprodukowana przez firmę ELCAN. Wersja Flat Dark Earth."

L["eft_scope_flir.printname"] = "Luneta termiczna FLIR RS-32 2,25-9x 35 mm 60 Hz"
L["eft_scope_flir.compactname"] = "FLIR RS-32"
L["eft_scope_flir.description"] = "Uniwersalna luneta termowizyjna z wieloma zastosowaniami i zaletami. Może być zainstalowana na broni do wykorzystania w postaci lunety lub na hełmie jako okular przez adapter i jako oddzielne urządzenie obserwacyjne."

L["eft_scope_hamr.printname"] = "Luneta hybrydowa Leupold Mark 4 HAMR 4x24 DeltaPoint"
L["eft_scope_hamr.compactname"] = "HAMR"
L["eft_scope_hamr.description"] = "Luneta hybrydowa produkowana przez zakłady Leupold składa się z celownika teleskopowego Mark 4 HAMR 4x24 mm oraz celownika kolimatorowego DeltaPoint instalowanego na górze. Została opracowana z myślą o precyzyjnym prowadzeniu ognia na średnim dystansie, używając optyki 4×, jednocześnie zachowując skuteczność w walce na bliskim dystansie przy użyciu kompaktowego celownika kolimatorowego."

L["eft_scope_kmz_1p59.printname"] = "Luneta KMZ 1P59 3-10x"
L["eft_scope_kmz_1p59.compactname"] = "1P59"
L["eft_scope_kmz_1p59.description"] = "Celownik 1P59 „Giperon” przeznaczony jest do zwiększenia skuteczności strzelania z karabinu wyborowego SVD w porównaniu ze standardowym celownikiem PSO-1 nawet dwukrotnie (w zależności od odległości i rozmiaru celu). Z powodu większego powiększenia, jest wyposażony w bardziej precyzyjny dalmierz, który jest idealny w operacjach mierzenia odległości od celu, oraz umieszczenie skali do wchodzenia w kąty celowania w polu widzenia celownika."

L["eft_scope_kmz_1p69.printname"] = "Luneta KMZ 1P69 3-10x"
L["eft_scope_kmz_1p69.compactname"] = "1P69"
L["eft_scope_kmz_1p69.description"] = "1P69 „Giperon” jest to nowoczesna wersja celowników „Giperon” i przeznaczony jest do instalacji na powtarzalnym karabinie wyborowym SW-98, zwiększając skuteczność na polu bitwy. Celownik posiada elastyczne opcje powiększania, a także dość dokładny dalmierz, ułatwiając pracę z mierzeniem odległości do celu oraz uwzględnia kąt celowania."

L["eft_scope_pa_prism.printname"] = "Luneta Kiba Arms Short Prism 2,5x"
L["eft_scope_pa_prism.compactname"] = "Prism 2,5×"
L["eft_scope_pa_prism.description"] = "Luneta Short Prism z 2,5-krotnym powiększeniem. Niskie powiększenie i jasna, podświetlana siatka pozwalają na szybkie strzelanie z otwartymi oboma oczami na bliskim dystansie. Wyprodukowana przez firmę Kiba Arms."

