L["eft_rearsight_mbus_fde.description"] = "Le cran de mire escamotable amovible Magpul MBUS Gen2. Produit par Magpul. Modèle Flat Dark Earth."

L["eft_rearsight_mcx.printname"] = "Cran de mire escamotable pour MCX"
L["eft_rearsight_mcx.compactname"] = "MCX RS"
L["eft_rearsight_mcx.description"] = "A detachable flip-up rear sight for MCX assault rifles, originally designed for the AR platform. Manufactured by SIG Sauer."

L["eft_rearsight_mp7.printname"] = "Cran de mire escamotable pour HK MP7"
L["eft_rearsight_mp7.compactname"] = "CDM MP7"
L["eft_rearsight_mp7.description"] = "Un cran de mire escamotable et amovible pour les pistolets-mitraileurs MP7. Produit par Heckler & Koch."

