-- [[ scope ]]
L["eft_scope_25mm_pilad.printname"] = "Lunette VOMZ Pilad 4 x 32 (25,4 mm)"
L["eft_scope_25mm_pilad.compactname"] = "Pilad 4x32"
L["eft_scope_25mm_pilad.description"] = "Conçue pour le tir à petites et moyennes distances. La lunette sera pratique pour tirer sur des objets en mouvement jusqu'à une distance de 150 m."

L["eft_scope_30mm_march.printname"] = "Lunette March Tactical 3-24 x 42 mm FFP (30 mm)"
L["eft_scope_30mm_march.compactname"] = "3-24 x 42 FFP"
L["eft_scope_30mm_march.description"] = "La lunette March Tactical 3-24 x 42 FFP est fiable et précise quelle que soit la munition utilisée."

L["eft_scope_30mm_mark4.printname"] = "Lunette Leupold Mark 4 LR 6,5-20 x 50 mm (30 mm)"
L["eft_scope_30mm_mark4.compactname"] = "6,5-20 x 50"
L["eft_scope_30mm_mark4.description"] = "Precise, accurate, durable and dependable - the Leupold Mark 4 6.5-20x50 rifle scope. The model provides 6.5 - 20x magnification giving you a clear idea of the actual size of the targeted object."

L["eft_scope_30mm_razor.printname"] = "Lunette Vortex Razor HD Gen.2 1-6 x 24 mm (30 mm)"
L["eft_scope_30mm_razor.compactname"] = "Razor HD Gen.2"
L["eft_scope_30mm_razor.description"] = "Une lunette de visée tactique pour une acquisition rapide des cibles et une précision maximum. Produit par Vortex Optics."

L["eft_scope_30mm_sb_pmii1.printname"] = "Lunette Schmidt & Bender PM II 1-8 x 24 mm (30 mm)"
L["eft_scope_30mm_sb_pmii1.compactname"] = "PM II 1-8 x 24"
L["eft_scope_30mm_sb_pmii1.description"] = "The Schmidt & Bender PM II 1-8x24 riflescope was originally created for elite military forces for high-quality target acquisition and pinpoint accuracy. "

L["eft_scope_30mm_tac30.printname"] = "Lunette Burris FullField TAC30 1-4 x 24 mm (30 mm)"
L["eft_scope_30mm_tac30.compactname"] = "TAC30"
L["eft_scope_30mm_tac30.description"] = "The TAC30 offers a very wide field of view, and 1x magnification at the lower end - making for easy, fast target acquisition. The 4x full zoom and Ballistic CQ reticle make shots out to about 300 yards very practical. The illuminated center dot and large, bright circle allow for instant target engagement at close distances."

L["eft_scope_30mm_vudu.printname"] = "Lunette EOTech Vudu 1-6 x 24 mm (30 mm)"
L["eft_scope_30mm_vudu.compactname"] = "Vudu 1-6x24"
L["eft_scope_30mm_vudu.description"] = "The EOTech Vudu 1-6x24 riflescope, equally effective on AR platforms and bolt-action rifles. Its EOTech-style speed ring reticle is designed for fast target engagement at low power, while delivering the resolution and accuracy needed for longer range targets."

L["eft_scope_34mm_atacr.printname"] = "Lunette Nightforce ATACR 7-35 x 56 mm (34 mm)"
L["eft_scope_34mm_atacr.compactname"] = "ATACR 7-35 x 56"
L["eft_scope_34mm_atacr.description"] = "Lunette NightForce ATACR 7-35 x 56 avec une plage de grossissement élevée et un large champ de vision, elle rend facile l'acquisition et l'engagement des cibles."

L["eft_scope_34mm_hsff.printname"] = "Lunette Hensoldt FF 4-16 x 56 mm (34 mm)"
L["eft_scope_34mm_hsff.compactname"] = "FF 4-16"
L["eft_scope_34mm_hsff.description"] = "La lunette de visée Hensoldt FF 4-16 x 56 a été développée pour offrir un maximum de performance et un réglage longue distance, tout en restant compacte. Avec une transparence augmentée sur la plage spectrale bleue et un réglage de l’intensité lumineuse du réticule, elle est particulièrement pratique dans des conditions de faible luminosité. La version FF de la lunette possède un réticule placé au premier plan focal, ainsi le motif du réticule est également agrandi lorsque l'on augmente le grossissement. Ceci permet de déterminer la distance par calcul en utilisant le MIL-DOT quelque soit le grossissement."

L["eft_scope_34mm_sb_pmii3.printname"] = "Lunette Schmidt & Bender PM II 3-12 x 50 mm (34 mm)"
L["eft_scope_34mm_sb_pmii3.compactname"] = "PM II 3-12 x 50"
L["eft_scope_34mm_sb_pmii3.description"] = "The Schmidt & Bender PM II 3-20x50 riflescope was originally created for elite military forces for high-quality target acquisition and pinpoint accuracy."

L["eft_scope_34mm_sb_pmii5.printname"] = "Lunette Schmidt & Bender PM II 5-25 x 56 (34 mm)"
L["eft_scope_34mm_sb_pmii5.compactname"] = "PM II 5-25x56"
L["eft_scope_34mm_sb_pmii5.description"] = "La lunette de visée Schmidt & Bender PM II 5-25x56 a été créée à l'origine pour les forces militaires d'élite pour une acquisition de haute qualité des cibles et une précision parfaite."

L["eft_scope_acog_ta01.printname"] = "Lunette Trijicon ACOG TA01NSN 4 x 32 (noire)"
L["eft_scope_acog_ta01.compactname"] = "TA01NSN"
L["eft_scope_acog_ta01.description"] = "Designed to the exact specifications of the US Special Operations Command for use by elite US forces, the Trijicon ACOG TA01NSN is Trijicon’s most popular ACOG. The ranging reticle allows for bullet drop compensation out to 600 meters without any manual adjustments. Additional backup rear sights can be mounted. No batteries needed. Black version."

L["eft_scope_acog_ta01_tan.printname"] = "Lunette Trijicon ACOG TA01NSN 4 x 32 (Tan)"
L["eft_scope_acog_ta01_tan.compactname"] = "TA01NSN"
L["eft_scope_acog_ta01_tan.description"] = "Designed to the exact specifications of the US Special Operations Command for use by elite US forces, the Trijicon ACOG TA01NSN is Trijicon’s most popular ACOG. The ranging reticle allows for bullet drop compensation out to 600 meters without any manual adjustments. Additional backup rear sights can be mounted. No batteries needed. Tan version."

L["eft_scope_acog_ta11d.printname"] = "Lunette Trijicon ACOG TA11D 3,5 x 35 mm"
L["eft_scope_acog_ta11d.compactname"] = "TA11D"
L["eft_scope_acog_ta11d.description"] = "La lunette ACOG 3,5 x 35 mm produite par Trijicon reste favorite parmi les tireurs de compétition, les forces de l'ordre et les civils défenseurs de la liberté dans le monde entier."

L["eft_scope_adop4.printname"] = "Lunette NcSTAR ADO P4 3-9 x 42"
L["eft_scope_adop4.compactname"] = "ADO P4"
L["eft_scope_adop4.description"] = "The Advance Dual Optic (ADO) 3X-9X variable magnification scope with a 42mm objective lens. The ADO Scope features an integrated Red Dot Reflex Optic on top of the scope body. Manufactured by NcSTAR."

L["eft_scope_bravo4.printname"] = "Lunette SIG Sauer BRAVO4 4 x 30 mm"
L["eft_scope_bravo4.compactname"] = "BRAVO4"
L["eft_scope_bravo4.description"] = "Conçue par SIG Sauer, la lunette de visée BRAVO4 4 x 30 mm offre un large champs de vision, 43% supérieur à ses concurrents les plus proches. Elle possède également un rail sur le dessus permettant l'installation d'un viseur reflex compact de secours."

L["eft_scope_compactprism.printname"] = "Lunette Monstrum Compact prism 2 x 32"
L["eft_scope_compactprism.compactname"] = "Compact 2 x 32"
L["eft_scope_compactprism.description"] = "Lunette compacte avec un grossissement de 2x produite par Monstrum."

L["eft_scope_dovetail_1p78.printname"] = "Lunette NPZ 1P78-1 2,8 x"
L["eft_scope_dovetail_1p78.compactname"] = "1P78-1"
L["eft_scope_dovetail_1p78.description"] = "Une lunette de visée à grossissement 2,8 x, installée sur un montage queue d'aronde. Produit par NPZ."

L["eft_scope_dovetail_ekp802.printname"] = "Viseur reflex Axion Kobra EKP-8-02 (queue d'aronde)"
L["eft_scope_dovetail_ekp802.compactname"] = "EKP-8-02 DT"
L["eft_scope_dovetail_ekp802.description"] = "Le viseur reflex Kobra est très populaire parmi les agences de sécurité et les tireurs civils. Il a été conçu pour les forces armées de la Fédération Russe, mais n'a jamais été officiellement adopté. Cette version est conçue pour s'installer sur un montage queue d'aronde. Conçu par Axion."

L["eft_scope_dovetail_nspum.printname"] = "Lunette de visée nocturne NSPU-M"
L["eft_scope_dovetail_nspum.compactname"] = "NSPU-M"
L["eft_scope_dovetail_nspum.description"] = "NSPU-M (Nochnoy Strelkovyy Pritsel Unifitsirovannyy Modernizirovannyy - \"Lunette nocture modernisée pour armes\"), index GRAU 1PN58. Une vieille lunette de visée nocturne soviétique pour les armes de la série AK, conçue pour se monter sur la queue d'aronde."

L["eft_scope_dovetail_okp7.printname"] = "Viseur reflex OKP-7 (queue d'aronde)"
L["eft_scope_dovetail_okp7.compactname"] = "OKP-7 QA"
L["eft_scope_dovetail_okp7.description"] = "Le design unique des viseurs OKP permet de tirer depuis des positions instables, dans des conditions changeant rapidement, en réduisant la fatigue de l'opérateur et, si nécessaire, permet une utilisation sans entrave des mires métalliques de base. La caractéristique clé de ce viseur est un réflecteur sans bordures qui ne bloque pas le champ de vision du tireur. Au lieu de cela, le réflecteur est protégé par un écran mince et résistant quasiment imperceptible en tirant. Cette variante s'installe sur un montage queue d'aronde."

L["eft_scope_dovetail_pk1.printname"] = "Viseur reflex NPZ PK1 \"Obzor\""
L["eft_scope_dovetail_pk1.compactname"] = "Obzor"
L["eft_scope_dovetail_pk1.description"] = "Un viseur reflex conçu pour les chasseurs pour une acquisition rapide de la cible dans un environnement très froid, installé sur un montage queue d'aronde. Produit par NPZ."

L["eft_scope_dovetail_pkaa.printname"] = "Viseur reflex BelOMO PK-AA"
L["eft_scope_dovetail_pkaa.compactname"] = "PK-AA"
L["eft_scope_dovetail_pkaa.description"] = "Le viseur reflex PK-AA, conçu pour convenir à n'importe quel modèle d'AK doté un montage queue d'aronde. Produit par BelOMO."

L["eft_scope_dovetail_pso1.printname"] = "Lunette BelOMO PSO-1 4 x 24"
L["eft_scope_dovetail_pso1.compactname"] = "PSO-1"
L["eft_scope_dovetail_pso1.description"] = "The PSO-1 military grade optical sniper scope, manufactured by BelOMO. This optical scope is designed for the Dragunov Sniper Rifle (SVD) on the variety of targets with 4x magnification and 6 degree FOV."

L["eft_scope_dovetail_pso1m2.printname"] = "Lunette BelOMO PSO-1M2-1 4 x 24"
L["eft_scope_dovetail_pso1m2.compactname"] = "PSO 1M 2-1"
L["eft_scope_dovetail_pso1m2.description"] = "La lunette militaire PSO-1M2-1, fabriquée par BelOMO. Cette lunette est conçue pour les tirs de précision avec les fusils de précision VSS et Dragunov (SVD) sur diverses cibles. Elle propose un grossissement de 4x et un champ de vision de 6 degrés."

L["eft_scope_dovetail_pso1m2non1.printname"] = "Lunette BelOMO PSO-1M2 4 x 24"
L["eft_scope_dovetail_pso1m2non1.compactname"] = "PSO-1M2"
L["eft_scope_dovetail_pso1m2non1.description"] = "The PSO-1M2 military grade optical sniper scope, manufactured by BelOMO. This optical scope is designed for precision sight fire with the Dragunov Sniper Rifle (SVD) on the variety of targets with 4x magnification and 6 degree FOV."

L["eft_scope_dovetail_tulpan.printname"] = "Lunette NPZ USP-1 4x"
L["eft_scope_dovetail_tulpan.compactname"] = "USP-1"
L["eft_scope_dovetail_tulpan.description"] = "The USP-1 \"Tyulpan\" unified rifle scope is designed to conduct accurate fire from AK-74N, AK-74M, AN-94 assault rifles and RPK-74N and PKMN machine guns in the daytime and at night. Installed on dovetail mount rail."

L["eft_scope_elcan.printname"] = "Lunette ELCAN SpecterDR 1x/4x"
L["eft_scope_elcan.compactname"] = "SpecterDR"
L["eft_scope_elcan.description"] = "Le viseur SpecterDR (Double rôle) 1x/4x de la série Spectre conçue par ELCAN a marqué une percée dans le développement de lunette de visée en devenant la première optique à grossissement variable par commutation, passant d'un grossissement 4x à 1 x d'un seul geste. Propose aussi des mires métalliques secondaires."

L["eft_scope_elcan_fde.printname"] = "Lunette ELCAN SpecterDR 1x/4x (FDE)"
L["eft_scope_elcan_fde.compactname"] = "SpecterDR"
L["eft_scope_elcan_fde.description"] = "La lunette SpecterDR (Dual Role) 1x/4x de la série Specter conçue par ELCAN a marqué une percée dans le développement des lunettes en devenant la première optique à grossissement variable par commutation, passant d'un grossissement 4x à 1x d'un seul geste. Elle propose également une mire métallique de secours. Modèle Flat Dark Earth."

L["eft_scope_flir.printname"] = "Lunette thermique FLIR RS-32 2,25-9 x 35 mm 60Hz"
L["eft_scope_flir.compactname"] = "FLIR RS-32"
L["eft_scope_flir.description"] = "Une lunette de visée thermique polyvalente, avec bien des utilisations et des avantages. Peut être installée sur les armes comme un viseur ou sur un casque via adaptateur en monoculaire, ou encore être utilisé comme simple outil d'observation."

L["eft_scope_hamr.printname"] = "Lunette d’assaut hybride Leupold Mark 4 HAMR 4 x 24 mm DeltaPoint"
L["eft_scope_hamr.compactname"] = "HAMR"
L["eft_scope_hamr.description"] = "Le viseur hybride produit par Leupold combine la lunette Mark 4 HAMR 4 x 24mm avec le viseur reflex DeltaPoint installé sur le dessus. Il a été développé pour du tir de précision à moyenne distance, tout en étant aussi efficace en combat rapproché grâce à l'utilisation d'un viseur reflex compact en cas de nécessité."

L["eft_scope_kmz_1p59.printname"] = "Lunette KMZ 1P59 3-10x"
L["eft_scope_kmz_1p59.compactname"] = "1P59"
L["eft_scope_kmz_1p59.description"] = "The 1P59 \"Giperon\" pancratic sight is designed to increase the effectiveness of firing from SVD in comparison with the standard PSO-1 sight by 1.3-2 times (depending on the range and size of the target) due to a larger zoom, the use of a more accurate range finder, combining target ranging and sighting angle input operations, as well as the sighting angle input scale incorporated within the field of view of the sight."

L["eft_scope_kmz_1p69.printname"] = "Lunette KMZ 1P69 3-10x"
L["eft_scope_kmz_1p69.compactname"] = "1P69"
L["eft_scope_kmz_1p69.description"] = "The 1P69 \"Giperon\" pancratic sight is a further development of the \"Giperon\" sights and is intended for installation on the SV-98 sniper rifle in order to increase the effectiveness on the battlefield. The sight has a flexible adjustment multiples, as well as a fairly accurate range finder, facilitating the operation of combining, measuring the distance to the target and entering the aiming angles."

L["eft_scope_pa_prism.printname"] = "Lunette Kiba Arms Short Prism 2,5x"
L["eft_scope_pa_prism.compactname"] = "Prism 2,5x"
L["eft_scope_pa_prism.description"] = "La lunette Short Prism 2,5x produite par Kiba Arms. Le faible grossissement et le réticule lumineux permettent des tirs rapides à courte distance en gardant les deux yeux ouverts."

L["eft_scope_reapir.printname"] = "Lunette thermique Trijicon REAP-IR"
L["eft_scope_reapir.compactname"] = "REAP-IR"
L["eft_scope_reapir.description"] = "Une lunette thermique polyvalente. Peut être installée sur les armes comme un viseur, sur un casque (via adaptateur) en monoculaire, ou être utilisé comme simple outil d'observation."

