L["eft_hg_ak_6p20sb9p.description"] = "A 6P20 Sb.9 polymer handguard for AK-74 automatic rifles, manufactured by Izhmash. Made out of plum-colored polymer, for which has earned the nickname \"Sliva\" (\"Plum\")."

L["eft_hg_ak_agressor.printname"] = "Garde-main 5.45 Design Aggressor pour AK"
L["eft_hg_ak_agressor.compactname"] = "Aggressor"
L["eft_hg_ak_agressor.description"] = "Aggressor is a railed handguard for AK series rifles manufactured by 5.45 Design. Features 4 side rails for installation of tactical devices and 2 long rails for installation of various optics and foregrips."

L["eft_hg_ak_ak100.printname"] = "Garde-main polymère pour AK de la série 100"
L["eft_hg_ak_ak100.compactname"] = "AK100"
L["eft_hg_ak_ak100.description"] = "A polymer handguard for the 100-series AKs, manufactured by Izhmash. A further modification of the polymer AK-74M handguard. Features a bottom rail for installation of tactical foregrips."

L["eft_hg_ak_akm_l.printname"] = "Garde-main TDI AKM-L pour AK"
L["eft_hg_ak_akm_l.compactname"] = "AKM-L"
L["eft_hg_ak_akm_l.description"] = "Le garde main TDI Arms AKM-L peut être installé sur les systèmes AKM/AK-74 compatibles. Équipé avec des rails M-LOK pour l'installation d'accessoires ou de rails supplémentaires. Modèle anodisé noir."

L["eft_hg_ak_akm_l_ab.printname"] = "Garde-main TDI AKM-L pour AK (anodisé bronze)"
L["eft_hg_ak_akm_l_ab.compactname"] = "AKM-L"
L["eft_hg_ak_akm_l_ab.description"] = "Le garde main TDI Arms AKM-L peut être installé sur les systèmes AKM/AK-74 compatibles. Équipé avec des rails M-LOK pour l'installation d'accessoires ou de rails supplémentaires. Modèle anodisé bronze."

L["eft_hg_ak_akm_l_ar.printname"] = "Garde-main TDI AKM-L pour AK (anodisé rouge)"
L["eft_hg_ak_akm_l_ar.compactname"] = "AKM-L"
L["eft_hg_ak_akm_l_ar.description"] = "Le garde main TDI Arms AKM-L peut être installé sur les systèmes AKM/AK-74 compatibles. Équipé avec des rails M-LOK pour l'installation d'accessoires ou de rails supplémentaires. Modèle anodisé rouge."

L["eft_hg_ak_b10.printname"] = "Garde-main Zenit B-10 pour AK"
L["eft_hg_ak_b10.compactname"] = "B-10"
L["eft_hg_ak_b10.description"] = "The integrally machined B-10 foregrip is manufactured from aluminum alloy D16T with black coating and can be installed instead of the standard-issue foregrip on an AK. It's fitted with Picatinny rail mounts on two sides, allowing for the installation of additional equipment such as tactical foregrips, flashlights, and laser designators. Manufactured by Zenit."

L["eft_hg_ak_b10mb19.printname"] = "Garde-main Zenit B-10M et montage B-19"
L["eft_hg_ak_b10mb19.compactname"] = "B10M+B19"
L["eft_hg_ak_b10mb19.description"] = "Le garde-main B-10M est fraisé dans un alliage d'aluminium D16T, muni d'un revêtement noir et peut être installé à la place du garde-main standard sur les AK séries 103, 104, 105, 74S, 74M, AKM et AKMS. Le montage B-19 est essentiellement un montage de visée surplombant le tube d'emprunt de gaz. Également fraisé dans un alliage d'aluminium D16T et muni d'un revêtement noir, il peut être installé sur les garde-main B-10M ou B-21M. ©Zenit"

L["eft_hg_ak_b11.printname"] = "Garde-main Zenit B-11 pour AKS-74U"
L["eft_hg_ak_b11.compactname"] = "B-11"
L["eft_hg_ak_b11.description"] = "Le garde-main B-11 intégralement usiné est fabriqué à partir d'alliage d'aluminium D16T avec revêtement noir et peut être installé à la place du garde main standard de l'AKS-74U. Le garde-main est équipé avec des rails Picatinny sur trois côtés, il permet l'installation d'équipements supplémentaires tels que des poignées tactiques, lampes-torches et laser. Produit par Zenit."

L["eft_hg_ak_b30b31s.printname"] = "Garde-main Zenit B-30 et rail B-31S pour AK"
L["eft_hg_ak_b30b31s.compactname"] = "B-30+B-31S"
L["eft_hg_ak_b30b31s.description"] = "Intégralement usiné, le garde-main B-30 est fabriqué en alliage d’aluminium D16T avec revêtement noir et peut être installé sur les séries AK 103, 104, 105, 74C, 74M, AKM et AKMS en remplacement du garde-main d'origine. Le rail B-31S se trouvant au dessus de l'emprunt de gaz est entièrement usiné en alliage d'aluminium D16T et possède un revêtement noir également, il s'installe sur les garde-mains B-30. © «Zenit»"

L["eft_hg_ak_cmrd.printname"] = "Garde-main Vltor CMRD KeyMod pour AK"
L["eft_hg_ak_cmrd.compactname"] = "CMRD"
L["eft_hg_ak_cmrd.description"] = "The CMRD lightweight handguard by Vltor, compatible with AK-74 and AKM series automatic rifles. Includes KeyMod slots for rail installation."

L["eft_hg_ak_cncguns.printname"] = "Garde-main CNC Guns OV GP pour AK"
L["eft_hg_ak_cncguns.compactname"] = "OV GP"
L["eft_hg_ak_cncguns.description"] = "Le garde-main OV GP pour systèmes AK, équipé d'une interface KeyMod pour l'installation d'équipements et d'accessoires supplémentaires. Produit par CNC Guns."

L["eft_hg_ak_goliaf.printname"] = "Garde-main Alfa Arms Goliaf pour AKS-74U"
L["eft_hg_ak_goliaf.compactname"] = "Goliaf"
L["eft_hg_ak_goliaf.description"] = "This integrally machined handguard is manufactured from aluminum alloy D16T with coyote brown coating and can be installed instead of the standard-issue foregrip on the AKS-74U. It comes fitted with Picatinny rail mounts on three sides, allowing for the installation of additional equipment such as tactical foregrips, flashlights, and laser designators."

L["eft_hg_ak_hexagon.printname"] = "Garde-main Hexagon pour AK"
L["eft_hg_ak_hexagon.compactname"] = "Hexagon AK"
L["eft_hg_ak_hexagon.description"] = "Un garde-main pour la famille des fusils d'assaut AK, produit par Hexagon."

L["eft_hg_ak_hexagon_r.printname"] = "Garde-main Hexagon pour AK (anodisé rouge)"
L["eft_hg_ak_hexagon_r.compactname"] = "Hexagon AK"
L["eft_hg_ak_hexagon_r.description"] = "Un garde-main pour la famille des fusils d'assaut AK, produit par Hexagon. Version anodisée rouge. LE ROUGE VA PLUS VITE !"

