L["eft_hg_ak_rs47.printname"] = "Garde-main CAA RS47 pour AK"
L["eft_hg_ak_rs47.compactname"] = "CAA RS47"
L["eft_hg_ak_rs47.description"] = "Le garde-main RS47 léger en polymère, s'adapte à la majorité des fusils et carabines construits sur la base des fusils de la famille AK. Équipé de deux rail courts et deux rails longs pour l'installation d'équipements et d'accessoires supplémentaires. Produit par Command Arms."

L["eft_hg_ak_trax1.printname"] = "Garde-main Strike Industries TRAX 1 pour AK"
L["eft_hg_ak_trax1.compactname"] = "TRAX 1"
L["eft_hg_ak_trax1.description"] = "The Strike Industries AK TRAX 1 rail system is a modular drop-in rail that is versatile, light-weight, and durable. It will provide an adaptable platform for the user to mount their accessories. Can be combined with the TRAX 2 rail for maximum amount of modularity."

L["eft_hg_ak_trax2.printname"] = "Extension de garde-main Strike Industries TRAX 2 pour AK"
L["eft_hg_ak_trax2.compactname"] = "TRAX 2"
L["eft_hg_ak_trax2.description"] = "TRAX 2 is a front rail section that covers the gas block. Provides additional places to mount accessories. Requires the TRAX Bridge rail to be mounted with the TRAX 1 handguard. Manufactured by Strike Industries."

L["eft_hg_ak_trax_bridge.printname"] = "Rail Strike Industries TRAX bridge"
L["eft_hg_ak_trax_bridge.compactname"] = "TRAX Bridge"
L["eft_hg_ak_trax_bridge.description"] = "Le Rail Strike Industries TRAX Bridge se monte sur le garde-main TRAX 1 et permet l'installation de l'extension de garde-main TRAX 2."

L["eft_hg_ak_ufm.printname"] = "Garde-main Krebs Custom UFM KeyMod pour AK"
L["eft_hg_ak_ufm.compactname"] = "UFM"
L["eft_hg_ak_ufm.description"] = "The Krebs Custom UFM Keymod System lightweight aluminum handguard for AK family assault rifles with KeyMod slots for rail installation. The Kiba Arms VDM CS gas tube is required for installation."

L["eft_hg_ak_vpo136.printname"] = "Garde-main en bois pour VPO-136 \"Vepr-KM\""
L["eft_hg_ak_vpo136.compactname"] = "VPO136"
L["eft_hg_ak_vpo136.description"] = "A standard wooden handguard for VPO-136 Vepr KM carbines, manufactured by Molot Arms."

L["eft_hg_ak_vpo209.printname"] = "Garde-main en bois pour VPO-209"
L["eft_hg_ak_vpo209.compactname"] = "VPO209"
L["eft_hg_ak_vpo209.description"] = "Un garde-main standard en bois pour la carabine VPO-209 AKM. Produit par Molot Arms."

L["eft_hg_ak_wasr.printname"] = "Garde-main en bois CAF WASR-10/63"
L["eft_hg_ak_wasr.compactname"] = "WASR"
L["eft_hg_ak_wasr.description"] = "Un garde-main roumain pour les fusils d'assaut WASR -10/63, produit par Cugir Arms Factory. Compatible avec les systèmes AK."

L["eft_hg_ak_x47.printname"] = "AK TDI X47 tactical handguard rail system"
L["eft_hg_ak_x47.compactname"] = "X47"
L["eft_hg_ak_x47.description"] = "A tactical aluminum handguard for the AK family assault rifles, designed and manufactured by TDI Arms. Equipped with multiple rail mounts for installation of a wide range of additional equipment."

L["eft_hg_ak_xrsu47.printname"] = "Garde-main tactique CAA XRSU47SU pour AKS-74U"
L["eft_hg_ak_xrsu47.compactname"] = "XRSU47SU"
L["eft_hg_ak_xrsu47.description"] = "Garde-main tactique en aluminium pour AKS-74U, AKS-74UN, AKS-74UB, conçu et produit par SAA. Équipé de multiples rails pour l'installation d'une grande variété d’accessoires."

L["eft_hg_ak_zhu.printname"] = "Garde-main Magpul Zhukov-U pour AK (noir)"
L["eft_hg_ak_zhu.compactname"] = "Zhukov-U"
L["eft_hg_ak_zhu.description"] = "The Magpul Zhukov-U handguard is installed on AKM/AK-74 compatible weapon systems. Equipped with licensed M-LOK mounts for installation of additional devices or rails. Black version."

L["eft_hg_ak_zhu_fde.printname"] = "Garde-main Magpul Zhukov-U pour AK (FDE)"
