L["eft_mount_hexagon_m.description"] = "Le rail de longueur moyenne pour garde-main Hexagon vous permet d'installer des équipements et accessoires supplémentaires."

L["eft_mount_hexagon_m_ar.printname"] = "Rail moyen Hexagon (anodisé rouge)"
