-- [[ ammo_338 ]]
L["eft_ammo_338_ap.printname"] = ".338 Lapua Magnum AP"
L["eft_ammo_338_ap.compactname"] = "AP"
L["eft_ammo_338_ap.description"] = "Une cartouche de .338 Lapua Magnum (8,6 x 70 mm) AP comprenant une balle perforante de 15,4 grammes en carbure de tungstène à sabot dans un étui en laiton. Cette cartouche a été conçue pour améliorer les capacités anti-matérielles des armes en calibre .338 Lapua Magnum, en ayant la capacité de traverser les protections balistiques des véhicules légers ainsi que les protections balistiques personnelles les plus avancées, en plus d'avoir un pouvoir d'arrêt significatif et de provoquer des dégâts considérables dans la cible à l'impact. Elle possède cependant une forte probabilité de ricocher sur diverses surfaces."

L["eft_ammo_338_fmj.printname"] = ".338 Lapua Magnum FMJ"
L["eft_ammo_338_fmj.compactname"] = "FMJ"
L["eft_ammo_338_fmj.description"] = "Une cartouche de .338 Lapua Magnum (8,6 x 70 mm) FMJ comprenant une balle de 16,2 grammes à noyau de plomb dans une chemise bimétallique dans un étui en laiton. Malgré son design rudimentaire, elle est capable de traverser les protections balistiques basiques et intermédiaires, en plus d'avoir un pouvoir d'arrêt significatif et de provoquer des dégâts considérables dans la cible à l'impact. Elle possède cependant une forte probabilité de ricocher sur diverses surfaces."

L["eft_ammo_338_tacx.printname"] = ".338 Lapua Magnum TAC-X"
L["eft_ammo_338_tacx.compactname"] = "TAC-X"
L["eft_ammo_338_tacx.description"] = "Une cartouche de .338 Lapua Magnum (8,6 x 70 mm) TAC-X comprenant une balle de 18,4 grammes monométallique faite entièrement de cuivre dans un étui en laiton. La balle de cette cartouche offre de très bons groupements à longue distance et est conçue pour la chasse au gros gibier, elle possède un pouvoir d'arrêt supérieur et est capable de provoquer des dégâts considérables dans la cible à l'impact. Cependant, malgré son énergie élevée, et de par le design même de la balle TAC-X, elle a des difficultés à traverser les protections balistiques basiques et possède une forte probabilité de ricocher sur diverses surfaces."

L["eft_ammo_338_upz.printname"] = ".338 Lapua Magnum UCW"
L["eft_ammo_338_upz.compactname"] = "UCW"
L["eft_ammo_338_upz.description"] = "Une cartouche de .338 Lapua Magnum (8,6 x 70 mm) UCW comprenant une balle de 16,2 grammes à noyau de plomb dans une chemise en laiton dans un étui en laiton, conçue pour la chasse et le tir sportif, produit par Ulyanovsk Cartridge Works. Malgré son design rudimentaire, elle possède un pouvoir d'arrêt impressionnant et est capable de provoquer des dégâts considérables dans la cible à l'impact, et même de traverser les protections balistiques basiques ainsi que quelques protections intermédiaires. Elle possède cependant une forte probabilité de ricocher sur diverses surfaces."

