L["eft_rearsight_m4carry.description"] = "Una maniglia per il trasporto staccabile con una tacca di mira per fucili AR-15. Dotazione standard per fucili d'assalto M4A1."

L["eft_rearsight_mbus.printname"] = "Tacca posteriore Magpul MBUS 2° Gen ribaltabile"
L["eft_rearsight_mbus.compactname"] = "MBUS RS"
