L["eft_tactical_raptar.printname"] = "Telemetro tattico Wilcox RAPTAR ES"
L["eft_tactical_raptar.compactname"] = "RAPTAR"
L["eft_tactical_raptar.description"] = "La prima generazione del dispositivo tattico chiamato \"RAPTAR\" combinato con un telemetro. Dispone di laser visibili e IR, nonché di un illuminatore a infrarossi."

L["eft_tactical_wf501b.printname"] = "Torcia Ultrafire WF-501B"
L["eft_tactical_wf501b.compactname"] = "WF501B"
L["eft_tactical_wf501b.description"] = "Una semplice ma potente torcia LED con un corpo robusto, molto resistente per una lunga durata."

L["eft_tactical_wmx200.printname"] = "Torcia tattica Insight WMX200"
L["eft_tactical_wmx200.compactname"] = "WMX200"
